#include <stdio.h>
#include <stdarg.h>
#include <stdlib.h>
#include <string.h>
#include <stdbool.h>
#include <limits.h>
#include <sys/types.h>
#include <sys/stat.h>

static void probe_log(const char *fmt, ...)
{
  FILE *f;
  /* try to ensure directory exists; ignore errors */
  (void)mkdir("/tmp/logs", 0755);
  f = fopen("/tmp/logs/58.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fprintf(f, "\n");
  fclose(f);
}

static enum MHD_Result
add_response_header_connection (struct MHD_Response *response,
                                const char *value)
{
  static const char *key = MHD_HTTP_HEADER_CONNECTION;
  /** the length of the "Connection" key */
  static const size_t key_len =
    MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONNECTION);
  size_t value_len; /**< the length of the @a value */
  size_t old_value_len; /**< the length of the existing "Connection" value */
  size_t buf_size;  /**< the size of the buffer */
  size_t norm_len;  /**< the length of the normalised value */
  char *buf;        /**< the temporal buffer */
  struct MHD_HTTP_Res_Header *hdr; /**< existing "Connection" header */
  bool value_has_close; /**< the @a value has "close" token */
  bool already_has_close; /**< existing "Connection" header has "close" token */
  size_t pos = 0;   /**< position of addition in the @a buf */

  /* Log entry and key state */
  probe_log("add_response_header_connection: entry value='%s' response=%p flags_auto=0x%zx key_len=%zu",
            value ? value : "(null)", (void *)response,
            (response ? (size_t)response->flags_auto : 0U), key_len);

  if ( (NULL != strchr (value, '\r')) ||
       (NULL != strchr (value, '\n')) )
  {
    probe_log("add_response_header_connection: rejected value contains CR or LF");
    return MHD_NO;
  }

  if (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                       key, key_len);
    already_has_close =
      (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));

    /* Probe: log hdr and the measured "close" match */
    if (hdr != NULL)
    {
      int memcmp_is_close = (0 == memcmp (hdr->value, "close", 5));
      probe_log("branch: has_conn_hdr=1 response=%p flags_auto=0x%zx hdr=%p hdr->value='%.*s' hdr->value_size=%zu memcmp_is_close=%d already_has_close=%d",
                (void *)response, (size_t)response->flags_auto, (void *)hdr,
                (int)hdr->value_size, hdr->value ? hdr->value : "(null)", hdr->value_size,
                memcmp_is_close, already_has_close);
    }
    else
    {
      probe_log("branch: has_conn_hdr=1 response=%p flags_auto=0x%zx hdr=NULL already_has_close=%d",
                (void *)response, (size_t)response->flags_auto, already_has_close);
    }

    mhd_assert (already_has_close == (0 == memcmp (hdr->value, "close", 5)));
    mhd_assert (NULL != hdr);
  }
  else
  {
    hdr = NULL;
    already_has_close = false;
    mhd_assert (NULL == MHD_get_response_element_n_ (response,
                                                     MHD_HEADER_KIND,
                                                     key, key_len));

    /* This is the assertion the other agent reported as dead code when mhd_assert is ((void)0).
       We add logging to capture the evaluated expression so that the behaviour can be observed
       even when the assertion macro is a no-op. */
    {
      unsigned long flags = (unsigned long) response->flags_auto;
      int expr = (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
      probe_log("branch: has_conn_hdr=0 response=%p flags_auto=0x%lx (flags & HAS_CONNECTION_CLOSE)=0x%lx asserted_expr=(0 == (flags & HAS_CONNECTION_CLOSE)) -> %d",
                (void *)response, flags,
                (unsigned long)(response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE), expr);
    }

    mhd_assert (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
  }
  if (NULL != hdr)
    old_value_len = hdr->value_size + 2; /* additional size for ", " */
  else
    old_value_len = 0;

  value_len = strlen (value);
  probe_log("value_len=%zu old_value_len=%zu", value_len, old_value_len);
  if (value_len >= SSIZE_MAX)
    return MHD_NO;
  /* Additional space for normalisation and zero-termination */
  norm_len = value_len + value_len / 2 + 1;
  if (norm_len >= SSIZE_MAX)
    return MHD_NO;
  buf_size = old_value_len + (size_t) norm_len;

  probe_log("allocating buf_size=%zu norm_len=%zu", buf_size, norm_len);
  buf = malloc (buf_size);
  if (NULL == buf)
  {
    probe_log("malloc failed for size=%zu", buf_size);
    return MHD_NO;
  }
  if (1)
  { /* local scope */
    ssize_t norm_len_s = (ssize_t) norm_len;
    /* Remove "close" token (if any), it will be moved to the front */
    value_has_close = MHD_str_remove_token_caseless_ (value, value_len, "close",
                                                      MHD_STATICSTR_LEN_ ( \
                                                        "close"),
                                                      buf + old_value_len,
                                                      &norm_len_s);
    probe_log("after remove_token value_has_close=%d norm_len_s=%zd", value_has_close, norm_len_s);
    mhd_assert (0 <= norm_len_s);
    if (0 > norm_len_s)
    {
      /* Must never happen with realistic sizes */
      free (buf);
      return MHD_NO;
    }
    else
      norm_len = (size_t) norm_len_s;
  }
#ifdef UPGRADE_SUPPORT
  if ( (NULL != response->upgrade_handler) && value_has_close)
  { /* The "close" token cannot be used with connection "upgrade" */
    probe_log("upgrade conflict: upgrade_handler=%p value_has_close=%d",
              (void *)response->upgrade_handler, value_has_close);
    free (buf);
    return MHD_NO;
  }
#endif /* UPGRADE_SUPPORT */
  if (0 != norm_len)
  {
    probe_log("removing keep-alive tokens from '%s' (norm_len=%zu)", buf + old_value_len, norm_len);
    MHD_str_remove_tokens_caseless_ (buf + old_value_len, &norm_len,
                                     "keep-alive",
                                     MHD_STATICSTR_LEN_ ("keep-alive"));
  }
  if (0 == norm_len)
  { /* New value is empty after normalisation */
    if (! value_has_close)
    { /* The new value had no tokens */
      probe_log("normalized value empty and no close token - nothing to add");
      free (buf);
      return MHD_NO;
    }
    if (already_has_close)
    { /* The "close" token is already present, nothing to modify */
      probe_log("already has close token - nothing to modify");
      free (buf);
      return MHD_YES;
    }
  }
  /* Add "close" token if required */
  if (value_has_close && ! already_has_close)
  {
    /* Need to insert "close" token at the first position */
    mhd_assert (buf_size >= old_value_len + norm_len   \
                + MHD_STATICSTR_LEN_ ("close, ") + 1);
    probe_log("inserting close token: buf_size=%zu old_value_len=%zu norm_len=%zu",
              buf_size, old_value_len, norm_len);
    if (0 != norm_len)
      memmove (buf + MHD_STATICSTR_LEN_ ("close, ") + old_value_len,
               buf + old_value_len, norm_len + 1);
    memcpy (buf, "close", MHD_STATICSTR_LEN_ ("close"));
    pos += MHD_STATICSTR_LEN_ ("close");
  }
  /* Add old value tokens (if any) */
  if (0 != old_value_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    memcpy (buf + pos, hdr->value,
            hdr->value_size);
    pos += hdr->value_size;
    probe_log("appended old value, new pos=%zu", pos);
  }
  /* Add new value token (if any) */
  if (0 != norm_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    /* The new value tokens must be already at the correct position */
    mhd_assert ((value_has_close && ! already_has_close) ? \
                (MHD_STATICSTR_LEN_ ("close, ") + old_value_len == pos) : \
                (old_value_len == pos));
    pos += norm_len;
    probe_log("appended new normalized tokens, norm_len=%zu pos=%zu", norm_len, pos);
  }
  mhd_assert (buf_size > pos);
  buf[pos] = 0; /* Null terminate the result */

  if (NULL == hdr)
  {
    struct MHD_HTTP_Res_Header *new_hdr; /**< new "Connection" header */
    /* Create new response header entry */
    new_hdr = MHD_calloc_ (1, sizeof (struct MHD_HTTP_Res_Header));
    if (NULL != new_hdr)
    {
      new_hdr->header = malloc (key_len + 1);
      if (NULL != new_hdr->header)
      {
        memcpy (new_hdr->header, key, key_len + 1);
        new_hdr->header_size = key_len;
        new_hdr->value = buf;
        new_hdr->value_size = pos;
        new_hdr->kind = MHD_HEADER_KIND;
        if (value_has_close)
          response->flags_auto = (MHD_RAF_HAS_CONNECTION_HDR
                                  | MHD_RAF_HAS_CONNECTION_CLOSE);
        else
          response->flags_auto = MHD_RAF_HAS_CONNECTION_HDR;
        probe_log("inserting new header: response=%p flags_auto=0x%zx new_hdr=%p value='%s' value_size=%zu",
                  (void *)response, (size_t)response->flags_auto, (void *)new_hdr, new_hdr->value ? new_hdr->value : "(null)", new_hdr->value_size);
        _MHD_insert_header_first (response, new_hdr);
        return MHD_YES;
      }
      free (new_hdr);
    }
    probe_log("failed to create new header - cleanup and return MHD_NO");
    free (buf);
    return MHD_NO;
  }

  /* Update existing header entry */
  probe_log("updating existing header hdr=%p old_value_size=%zu new_value_size=%zu value_has_close=%d already_has_close=%d",
            (void *)hdr, hdr->value_size, pos, value_has_close, already_has_close);
  free (hdr->value);
  hdr->value = buf;
  hdr->value_size = pos;
  if (value_has_close && ! already_has_close)
  {
    response->flags_auto |= MHD_RAF_HAS_CONNECTION_CLOSE;
    probe_log("set HAS_CONNECTION_CLOSE flag, response->flags_auto=0x%zx", (size_t)response->flags_auto);
  }
  probe_log("add_response_header_connection: exit MHD_YES response=%p flags_auto=0x%zx",
            (void *)response, (size_t)response->flags_auto);
  return MHD_YES;
}
