#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <stdarg.h>
#include <sys/types.h>

/* Probe logging helper: append human-readable traces to /tmp/logs/58.txt */
static void probe_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/58.txt", "a");
  if (NULL == f)
    return;
  {
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
  }
  fputc('\n', f);
  fclose(f);
}

static enum MHD_Result
add_response_header_connection (struct MHD_Response *response,
                                const char *value)
{
  static const char *key = MHD_HTTP_HEADER_CONNECTION;
  /** the length of the "Connection" key */
  static const size_t key_len =
    MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONNECTION);
  size_t value_len; /**< the length of the @a value */
  size_t old_value_len; /**< the length of the existing "Connection" value */
  size_t buf_size;  /**< the size of the buffer */
  size_t norm_len;  /**< the length of the normalised value */
  char *buf;        /**< the temporal buffer */
  struct MHD_HTTP_Res_Header *hdr; /**< existing "Connection" header */
  bool value_has_close; /**< the @a value has "close" token */
  bool already_has_close; /**< existing "Connection" header has "close" token */
  size_t pos = 0;   /**< position of addition in the @a buf */

  probe_log("enter: add_response_header_connection response=%p value=\"%s\"",
            (void *) response, (value != NULL) ? value : "(null)");

  if ( (NULL != strchr (value, '\r')) ||
       (NULL != strchr (value, '\n')) )
  {
    probe_log("early-return: value contains CR or LF");
    return MHD_NO;
  }

  if (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                       key, key_len);
    already_has_close =
      (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
    /* Log state used by the following assertions */
    if (hdr != NULL)
    {
      int cmp = memcmp (hdr->value, "close", 5);
      probe_log("assert-check: already_has_close=%d memcmp_close_5=%d hdr=%p hdr->value=\"%.*s\" hdr->value_size=%zu",
                (int) already_has_close, cmp, (void *) hdr, (int) (hdr->value_size < 64 ? (int) hdr->value_size : 64), hdr->value ? hdr->value : "(null)", hdr->value_size);
    }
    else
    {
      probe_log("assert-check: already_has_close=%d hdr is NULL",
                (int) already_has_close);
    }
    probe_log("assert-check: hdr pointer=%p (expected non-NULL)", (void *) hdr);
    /* Original assertions retained (may be no-ops depending on mhd_assert). */
    mhd_assert (already_has_close == (0 == memcmp (hdr->value, "close", 5)));
    mhd_assert (NULL != hdr);
  }
  else
  {
    hdr = NULL;
    already_has_close = false;
    /* Log state used by the following assertions */
    probe_log("assert-check: no existing connection header; verifying lookup returns NULL");
    {
      struct MHD_HTTP_Res_Header *tmp = MHD_get_response_element_n_ (response,
                                                                    MHD_HEADER_KIND,
                                                                    key, key_len);
      probe_log("assert-check: lookup returned %p (expected NULL)", (void *) tmp);
    }
    probe_log("assert-check: flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE = %u",
              (unsigned int) (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
    mhd_assert (NULL == MHD_get_response_element_n_ (response,
                                                     MHD_HEADER_KIND,
                                                     key, key_len));
    mhd_assert (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
  }
  if (NULL != hdr)
    old_value_len = hdr->value_size + 2; /* additional size for ", " */
  else
    old_value_len = 0;

  value_len = strlen (value);
  if (value_len >= SSIZE_MAX)
  {
    probe_log("early-return: value_len(%zu) >= SSIZE_MAX", value_len);
    return MHD_NO;
  }
  /* Additional space for normalisation and zero-termination */
  norm_len = value_len + value_len / 2 + 1;
  if (norm_len >= SSIZE_MAX)
  {
    probe_log("early-return: norm_len(%zu) >= SSIZE_MAX", norm_len);
    return MHD_NO;
  }
  buf_size = old_value_len + (size_t) norm_len;

  buf = malloc (buf_size);
  if (NULL == buf)
  {
    probe_log("early-return: malloc failed for buf_size=%zu", buf_size);
    return MHD_NO;
  }
  if (1)
  { /* local scope */
    ssize_t norm_len_s = (ssize_t) norm_len;
    /* Remove "close" token (if any), it will be moved to the front */
    value_has_close = MHD_str_remove_token_caseless_ (value, value_len, "close",
                                                      MHD_STATICSTR_LEN_ ( \
                                                        "close"),
                                                      buf + old_value_len,
                                                      &norm_len_s);
    /* Log the result of token removal and norm_len_s */
    probe_log("token-remove: value_has_close=%d norm_len_s=%zd old_value_len=%zu",
              (int) value_has_close, norm_len_s, old_value_len);

    mhd_assert (0 <= norm_len_s);
    if (0 > norm_len_s)
    {
      /* Must never happen with realistic sizes */
      probe_log("error: negative norm_len_s=%zd -> free buf and return MHD_NO", norm_len_s);
      free (buf);
      return MHD_NO;
    }
    else
      norm_len = (size_t) norm_len_s;
  }
#ifdef UPGRADE_SUPPORT
  if ( (NULL != response->upgrade_handler) && value_has_close)
  { /* The "close" token cannot be used with connection "upgrade" */
    probe_log("early-return: upgrade_handler present and value_has_close=true");
    free (buf);
    return MHD_NO;
  }
#endif /* UPGRADE_SUPPORT */
  if (0 != norm_len)
    MHD_str_remove_tokens_caseless_ (buf + old_value_len, &norm_len,
                                     "keep-alive",
                                     MHD_STATICSTR_LEN_ ("keep-alive"));
  if (0 == norm_len)
  { /* New value is empty after normalisation */
    probe_log("normalisation: norm_len==0 value_has_close=%d already_has_close=%d",
              (int) value_has_close, (int) already_has_close);
    if (! value_has_close)
    { /* The new value had no tokens */
      free (buf);
      probe_log("early-return: new value empty and no close token -> MHD_NO");
      return MHD_NO;
    }
    if (already_has_close)
    { /* The "close" token is already present, nothing to modify */
      free (buf);
      probe_log("early-return: already has close -> MHD_YES");
      return MHD_YES;
    }
  }
  /* Add "close" token if required */
  if (value_has_close && ! already_has_close)
  {
    /* Need to insert "close" token at the first position */
    probe_log("about-to-assert: checking buf_size(%zu) >= old_value_len(%zu) + norm_len(%zu) + len(close, ) + 1",
              buf_size, old_value_len, norm_len);
    mhd_assert (buf_size >= old_value_len + norm_len   \
                + MHD_STATICSTR_LEN_ ("close, ") + 1);
    if (0 != norm_len)
      memmove (buf + MHD_STATICSTR_LEN_ ("close, ") + old_value_len,
               buf + old_value_len, norm_len + 1);
    memcpy (buf, "close", MHD_STATICSTR_LEN_ ("close"));
    pos += MHD_STATICSTR_LEN_ ("close");
    probe_log("insert-close: pos after insert=%zu buf_size=%zu", pos, buf_size);
  }
  /* Add old value tokens (if any) */
  if (0 != old_value_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    memcpy (buf + pos, hdr->value,
            hdr->value_size);
    pos += hdr->value_size;
    probe_log("added-old-value: pos=%zu old_value_len=%zu hdr->value_size=%zu",
              pos, old_value_len, hdr->value_size);
  }
  /* Add new value token (if any) */
  if (0 != norm_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    /* The new value tokens must be already at the correct position */
    probe_log("about-to-assert: checking positional invariant: pos=%zu old_value_len=%zu norm_len=%zu value_has_close=%d already_has_close=%d",
              pos, old_value_len, norm_len, (int) value_has_close, (int) already_has_close);
    mhd_assert ((value_has_close && ! already_has_close) ? \
                (MHD_STATICSTR_LEN_ ("close, ") + old_value_len == pos) : \
                (old_value_len == pos));
    pos += norm_len;
    probe_log("added-new-value: pos now=%zu", pos);
  }
  probe_log("about-to-assert: buf_size(%zu) > pos(%zu)", buf_size, pos);
  mhd_assert (buf_size > pos);
  buf[pos] = 0; /* Null terminate the result */

  if (NULL == hdr)
  {
    struct MHD_HTTP_Res_Header *new_hdr; /**< new "Connection" header */
    /* Create new response header entry */
    new_hdr = MHD_calloc_ (1, sizeof (struct MHD_HTTP_Res_Header));
    if (NULL != new_hdr)
    {
      new_hdr->header = malloc (key_len + 1);
      if (NULL != new_hdr->header)
      {
        memcpy (new_hdr->header, key, key_len + 1);
        new_hdr->header_size = key_len;
        new_hdr->value = buf;
        new_hdr->value_size = pos;
        new_hdr->kind = MHD_HEADER_KIND;
        if (value_has_close)
          response->flags_auto = (MHD_RAF_HAS_CONNECTION_HDR
                                  | MHD_RAF_HAS_CONNECTION_CLOSE);
        else
          response->flags_auto = MHD_RAF_HAS_CONNECTION_HDR;
        _MHD_insert_header_first (response, new_hdr);
        probe_log("return: created new header value=\"%s\" value_size=%zu -> MHD_YES",
                  buf, pos);
        return MHD_YES;
      }
      free (new_hdr);
    }
    free (buf);
    probe_log("return: failed to allocate new_hdr->header -> MHD_NO");
    return MHD_NO;
  }

  /* Update existing header entry */
  free (hdr->value);
  hdr->value = buf;
  hdr->value_size = pos;
  if (value_has_close && ! already_has_close)
    response->flags_auto |= MHD_RAF_HAS_CONNECTION_CLOSE;
  probe_log("return: updated existing header value=\"%s\" value_size=%zu flags_auto=0x%08x -> MHD_YES",
            hdr->value, hdr->value_size, (unsigned int) response->flags_auto);
  return MHD_YES;
}
