#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <stdarg.h>
#include <time.h>

/* Probe logging helper: append formatted messages to /tmp/logs/58.txt. */
static void probe_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/58.txt", "a");
  if (NULL == f)
    return; /* do not alter program logic on logging failure */
  va_list ap;
  va_start(ap, fmt);
  /* timestamp */
  time_t t = time(NULL);
  struct tm tm;
  if (NULL != localtime_r(&t, &tm))
  {
    char ts[64];
    if (0 < strftime(ts, sizeof(ts), "%Y-%m-%d %H:%M:%S", &tm))
      fprintf(f, "%s - ", ts);
  }
  vfprintf(f, fmt, ap);
  fprintf(f, "\n");
  va_end(ap);
  fclose(f);
}

static enum MHD_Result
add_response_header_connection (struct MHD_Response *response,
                                const char *value)
{
  static const char *key = MHD_HTTP_HEADER_CONNECTION;
  /** the length of the "Connection" key */
  static const size_t key_len =
    MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONNECTION);
  size_t value_len; /**< the length of the @a value */
  size_t old_value_len; /**< the length of the existing "Connection" value */
  size_t buf_size;  /**< the size of the buffer */
  size_t norm_len;  /**< the length of the normalised value */
  char *buf;        /**< the temporal buffer */
  struct MHD_HTTP_Res_Header *hdr; /**< existing "Connection" header */
  bool value_has_close; /**< the @a value has "close" token */
  bool already_has_close; /**< existing "Connection" header has "close" token */
  size_t pos = 0;   /**< position of addition in the @a buf */

  probe_log("ENTER add_response_header_connection: response=%p, value=%p ('%s')",
            (void *)response, (const void *)value, value ? value : "(null)");

  if ( (NULL != strchr (value, '\r')) ||
       (NULL != strchr (value, '\n')) )
  {
    probe_log("Rejecting value because it contains CR or LF");
    return MHD_NO;
  }

  if (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                       key, key_len);
    already_has_close =
      (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));

    /* Log the conditions that the mhd_assert would check. */
    if (hdr != NULL)
    {
      int cmp_is_close = (0 == memcmp (hdr->value, "close", 5));
      probe_log("BRANCH has_connection_hdr: hdr=%p, hdr->value='%s', hdr->value_size=%zu, already_has_close=%d, memcmp_close=%d",
                (void *)hdr, hdr->value ? hdr->value : "(null)", hdr->value_size,
                (int)already_has_close, cmp_is_close);
    }
    else
    {
      /* Important: avoid dereferring hdr if it is NULL (the original mhd_assert
       * invoked memcmp on hdr->value before checking hdr != NULL, but if
       * mhd_assert is a no-op this side-effect doesn't happen. We log safely.) */
      probe_log("BRANCH has_connection_hdr: hdr=NULL, already_has_close=%d",
                (int)already_has_close);
    }

    /* Original code: mhd_assert (already_has_close == (0 == memcmp (hdr->value, "close", 5))); */
    probe_log("(assert) already_has_close == (hdr->value == 'close') would be evaluated here (but mhd_assert may be a no-op)");
    /* Original code: mhd_assert (NULL != hdr); */
    probe_log("(assert) hdr != NULL would be tested here");
  }
  else
  {
    hdr = NULL;
    already_has_close = false;
    /* The original code asserts that there is no existing header entry. We can call
     * the getter to observe the current state safely for logging. */
    struct MHD_HTTP_Res_Header *tmp_hdr = MHD_get_response_element_n_ (response,
                                                                     MHD_HEADER_KIND,
                                                                     key, key_len);
    probe_log("BRANCH no_connection_hdr: tmp_hdr=%p, flags_auto=0x%zx",
              (void *)tmp_hdr, (size_t)response->flags_auto);
    probe_log("(assert) 0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE) : %d",
              (int)(0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE)));
  }
  if (NULL != hdr)
    old_value_len = hdr->value_size + 2; /* additional size for ", " */
  else
    old_value_len = 0;

  probe_log("Computed old_value_len=%zu", old_value_len);

  value_len = strlen (value);
  probe_log("value_len=%zu", value_len);
  if (value_len >= SSIZE_MAX)
  {
    probe_log("value_len >= SSIZE_MAX, returning MHD_NO");
    return MHD_NO;
  }
  /* Additional space for normalisation and zero-termination */
  norm_len = value_len + value_len / 2 + 1;
  probe_log("Initial norm_len=%zu", norm_len);
  if (norm_len >= SSIZE_MAX)
  {
    probe_log("norm_len >= SSIZE_MAX, returning MHD_NO");
    return MHD_NO;
  }
  buf_size = old_value_len + (size_t) norm_len;
  probe_log("buf_size=%zu", buf_size);

  buf = malloc (buf_size);
  probe_log("malloc buf=%p (requested %zu)", (void *)buf, buf_size);
  if (NULL == buf)
  {
    probe_log("malloc failed, returning MHD_NO");
    return MHD_NO;
  }
  if (1)
  { /* local scope */
    ssize_t norm_len_s = (ssize_t) norm_len;
    /* Remove "close" token (if any), it will be moved to the front */
    value_has_close = MHD_str_remove_token_caseless_ (value, value_len, "close",
                                                      MHD_STATICSTR_LEN_ ( \
                                                        "close"),
                                                      buf + old_value_len,
                                                      &norm_len_s);
    probe_log("After MHD_str_remove_token_caseless_: value_has_close=%d, norm_len_s=%zd",
              (int)value_has_close, norm_len_s);
    mhd_assert (0 <= norm_len_s);
    if (0 > norm_len_s)
    {
      /* Must never happen with realistic sizes */
      probe_log("norm_len_s < 0, freeing buf and returning MHD_NO");
      free (buf);
      return MHD_NO;
    }
    else
      norm_len = (size_t) norm_len_s;
  }
#ifdef UPGRADE_SUPPORT
  if ( (NULL != response->upgrade_handler) && value_has_close)
  { /* The "close" token cannot be used with connection "upgrade" */
    probe_log("UPGRADE_SUPPORT: upgrade_handler present and value_has_close, freeing buf and returning MHD_NO");
    free (buf);
    return MHD_NO;
  }
#endif /* UPGRADE_SUPPORT */
  if (0 != norm_len)
    MHD_str_remove_tokens_caseless_ (buf + old_value_len, &norm_len,
                                     "keep-alive",
                                     MHD_STATICSTR_LEN_ ("keep-alive"));
  probe_log("After removing keep-alive tokens: norm_len=%zu", norm_len);
  if (0 == norm_len)
  { /* New value is empty after normalisation */
    if (! value_has_close)
    { /* The new value had no tokens */
      probe_log("norm_len==0 and no value_has_close, freeing buf and returning MHD_NO");
      free (buf);
      return MHD_NO;
    }
    if (already_has_close)
    { /* The "close" token is already present, nothing to modify */
      probe_log("norm_len==0 and already_has_close, freeing buf and returning MHD_YES");
      free (buf);
      return MHD_YES;
    }
  }
  /* Add "close" token if required */
  if (value_has_close && ! already_has_close)
  {
    /* Need to insert "close" token at the first position */
    size_t close_comma_len = strlen("close, ");
    probe_log("Preparing to insert 'close' token: value_has_close=%d, already_has_close=%d, buf_size=%zu, old_value_len=%zu, norm_len=%zu, required_min=%zu",
              (int)value_has_close, (int)already_has_close, buf_size, old_value_len, norm_len,
              old_value_len + norm_len + close_comma_len + 1);
    mhd_assert (buf_size >= old_value_len + norm_len   \
                + MHD_STATICSTR_LEN_ ("close, ") + 1);
    if (0 != norm_len)
      memmove (buf + MHD_STATICSTR_LEN_ ("close, ") + old_value_len,
               buf + old_value_len, norm_len + 1);
    memcpy (buf, "close", MHD_STATICSTR_LEN_ ("close"));
    pos += MHD_STATICSTR_LEN_ ("close");
    probe_log("Inserted 'close' token: pos=%zu", pos);
  }
  /* Add old value tokens (if any) */
  if (0 != old_value_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    memcpy (buf + pos, hdr->value,
            hdr->value_size);
    pos += hdr->value_size;
    probe_log("Added old header value: new pos=%zu", pos);
  }
  /* Add new value token (if any) */
  if (0 != norm_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    /* The new value tokens must be already at the correct position */
    probe_log("Before mhd_assert of position: value_has_close=%d, already_has_close=%d, expected_pos_when_close=%zu, expected_pos_no_close=%zu, actual_pos=%zu",
              (int)value_has_close, (int)already_has_close,
              strlen("close, ") + old_value_len, old_value_len, pos);
    mhd_assert ((value_has_close && ! already_has_close) ? \
                (MHD_STATICSTR_LEN_ ("close, ") + old_value_len == pos) : \
                (old_value_len == pos));
    pos += norm_len;
    probe_log("After adding new value token: pos=%zu", pos);
  }
  mhd_assert (buf_size > pos);
  probe_log("Final checks: buf_size=%zu, pos=%zu", buf_size, pos);
  buf[pos] = 0; /* Null terminate the result */

  if (NULL == hdr)
  {
    struct MHD_HTTP_Res_Header *new_hdr; /**< new "Connection" header */
    /* Create new response header entry */
    new_hdr = MHD_calloc_ (1, sizeof (struct MHD_HTTP_Res_Header));
    if (NULL != new_hdr)
    {
      new_hdr->header = malloc (key_len + 1);
      if (NULL != new_hdr->header)
      {
        memcpy (new_hdr->header, key, key_len + 1);
        new_hdr->header_size = key_len;
        new_hdr->value = buf;
        new_hdr->value_size = pos;
        new_hdr->kind = MHD_HEADER_KIND;
        if (value_has_close)
          response->flags_auto = (MHD_RAF_HAS_CONNECTION_HDR
                                  | MHD_RAF_HAS_CONNECTION_CLOSE);
        else
          response->flags_auto = MHD_RAF_HAS_CONNECTION_HDR;
        probe_log("Inserting new header: new_hdr=%p, header='%s', value='%s', value_size=%zu, flags_auto=0x%zx",
                  (void *)new_hdr, new_hdr->header, new_hdr->value, new_hdr->value_size,
                  (size_t)response->flags_auto);
        _MHD_insert_header_first (response, new_hdr);
        probe_log("Returning MHD_YES after inserting new header");
        return MHD_YES;
      }
      free (new_hdr);
    }
    probe_log("Failed to create new header, freeing buf and returning MHD_NO");
    free (buf);
    return MHD_NO;
  }

  /* Update existing header entry */
  probe_log("Updating existing header hdr=%p: old value at %p (size %zu), new buf=%p (pos=%zu)",
            (void *)hdr, (void *)hdr->value, hdr->value_size, (void *)buf, pos);
  free (hdr->value);
  hdr->value = buf;
  hdr->value_size = pos;
  if (value_has_close && ! already_has_close)
    response->flags_auto |= MHD_RAF_HAS_CONNECTION_CLOSE;
  probe_log("Updated header complete, returning MHD_YES; flags_auto=0x%zx", (size_t)response->flags_auto);
  return MHD_YES;
}
