#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <inttypes.h>

/* Probe logging helper. Appends a single-line record to /tmp/logs/63.txt */
static void
mhd_probe_log (const char *stage, const char *value, struct MHD_Response *response,
               const char *extra)
{
  FILE *f = fopen("/tmp/logs/63.txt", "a");
  if (NULL == f)
    return;
  /* Print stage, pointer values and response flags. Do NOT dereference 'value'. */
  fprintf (f, "stage=%s value_ptr=%p response_ptr=%p flags_auto=0x%zx",
           stage, (void *) value, (void *) response,
           (size_t) (response ? response->flags_auto : 0));
  if (extra)
    fprintf (f, " extra=%s", extra);
  fprintf (f, "\n");
  fclose (f);
}

static enum MHD_Result
add_response_header_connection (struct MHD_Response *response,
                                const char *value)
{
  static const char *key = MHD_HTTP_HEADER_CONNECTION;
  /** the length of the "Connection" key */
  static const size_t key_len =
    MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONNECTION);
  size_t value_len; /**< the length of the @a value */
  size_t old_value_len; /**< the length of the existing "Connection" value */
  size_t buf_size;  /**< the size of the buffer */
  size_t norm_len;  /**< the length of the normalised value */
  char *buf;        /**< the temporal buffer */
  struct MHD_HTTP_Res_Header *hdr; /**< existing "Connection" header */
  bool value_has_close; /**< the @a value has "close" token */
  bool already_has_close; /**< existing "Connection" header has "close" token */
  size_t pos = 0;   /**< position of addition in the @a buf */

  /* Log entry and initial state. Do not dereference 'value' here. */
  mhd_probe_log ("entry", value, response, NULL);

  /* Original code checked for CR/LF in the value. Preserve semantics but capture probes. */
  mhd_probe_log ("before_strchr", value, response, NULL);
  /* Note: calling strchr when value is NULL matches original behavior (may crash). */
  const char *pcr = strchr (value, '\r');
  const char *plf = strchr (value, '\n');
  if (pcr || plf)
  {
    /* Log detected CR/LF and return reason */
    mhd_probe_log ("rejected_crlf", value, response,
                   pcr ? "found_cr" : "found_lf");
    return MHD_NO;
  }

  if (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                       key, key_len);
    already_has_close =
      (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
    mhd_assert (already_has_close == (0 == memcmp (hdr->value, "close", 5)));
    mhd_assert (NULL != hdr);
  }
  else
  {
    hdr = NULL;
    already_has_close = false;
    mhd_assert (NULL == MHD_get_response_element_n_ (response,
                                                     MHD_HEADER_KIND,
                                                     key, key_len));
    mhd_assert (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
  }
  if (NULL != hdr)
    old_value_len = hdr->value_size + 2; /* additional size for ", " */
  else
    old_value_len = 0;

  /* Log right before calling strlen (which may read the buffer) */
  mhd_probe_log ("before_strlen", value, response, NULL);
  value_len = strlen (value);
  /* Log result of strlen to capture value length in successful runs */
  {
    char extra[64];
    snprintf (extra, sizeof (extra), "value_len=%zu", value_len);
    mhd_probe_log ("after_strlen", value, response, extra);
  }

  if (value_len >= SSIZE_MAX)
  {
    mhd_probe_log ("reject_len_too_large", value, response, NULL);
    return MHD_NO;
  }
  /* Additional space for normalisation and zero-termination */
  norm_len = value_len + value_len / 2 + 1;
  if (norm_len >= SSIZE_MAX)
  {
    mhd_probe_log ("reject_norm_too_large", value, response, NULL);
    return MHD_NO;
  }
  buf_size = old_value_len + (size_t) norm_len;

  buf = malloc (buf_size);
  if (NULL == buf)
  {
    mhd_probe_log ("malloc_failed", value, response, NULL);
    return MHD_NO;
  }
  mhd_probe_log ("malloc_succeeded", value, response, NULL);

  if (1)
  { /* local scope */
    ssize_t norm_len_s = (ssize_t) norm_len;
    /* Remove "close" token (if any), it will be moved to the front */
    value_has_close = MHD_str_remove_token_caseless_ (value, value_len, "close",
                                                      MHD_STATICSTR_LEN_ ( \
                                                        "close"),
                                                      buf + old_value_len,
                                                      &norm_len_s);
    mhd_assert (0 <= norm_len_s);
    if (0 > norm_len_s)
    {
      /* Must never happen with realistic sizes */
      mhd_probe_log ("norm_len_negative", value, response, NULL);
      free (buf);
      return MHD_NO;
    }
    else
      norm_len = (size_t) norm_len_s;
  }
  {
    char extra[128];
    snprintf (extra, sizeof (extra), "norm_len=%zu value_has_close=%d", norm_len, value_has_close);
    mhd_probe_log ("after_norm_remove", value, response, extra);
  }
#ifdef UPGRADE_SUPPORT
  if ( (NULL != response->upgrade_handler) && value_has_close)
  { /* The "close" token cannot be used with connection "upgrade" */
    mhd_probe_log ("reject_upgrade_and_close", value, response, NULL);
    free (buf);
    return MHD_NO;
  }
#endif /* UPGRADE_SUPPORT */
  if (0 != norm_len)
    MHD_str_remove_tokens_caseless_ (buf + old_value_len, &norm_len,
                                     "keep-alive",
                                     MHD_STATICSTR_LEN_ ("keep-alive"));
  if (0 == norm_len)
  { /* New value is empty after normalisation */
    if (! value_has_close)
    { /* The new value had no tokens */
      mhd_probe_log ("reject_empty_no_close", value, response, NULL);
      free (buf);
      return MHD_NO;
    }
    if (already_has_close)
    { /* The "close" token is already present, nothing to modify */
      mhd_probe_log ("already_has_close_no_change", value, response, NULL);
      free (buf);
      return MHD_YES;
    }
  }
  /* Add "close" token if required */
  if (value_has_close && ! already_has_close)
  {
    /* Need to insert "close" token at the first position */
    mhd_assert (buf_size >= old_value_len + norm_len   \
                + MHD_STATICSTR_LEN_ ("close, ") + 1);
    if (0 != norm_len)
      memmove (buf + MHD_STATICSTR_LEN_ ("close, ") + old_value_len,
               buf + old_value_len, norm_len + 1);
    memcpy (buf, "close", MHD_STATICSTR_LEN_ ("close"));
    pos += MHD_STATICSTR_LEN_ ("close");
    mhd_probe_log ("inserted_close", value, response, NULL);
  }
  /* Add old value tokens (if any) */
  if (0 != old_value_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    memcpy (buf + pos, hdr->value,
            hdr->value_size);
    pos += hdr->value_size;
    mhd_probe_log ("added_old_value", value, response, NULL);
  }
  /* Add new value token (if any) */
  if (0 != norm_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    /* The new value tokens must be already at the correct position */
    mhd_assert ((value_has_close && ! already_has_close) ? \
                (MHD_STATICSTR_LEN_ ("close, ") + old_value_len == pos) : \
                (old_value_len == pos));
    pos += norm_len;
    mhd_probe_log ("added_new_value", value, response, NULL);
  }
  mhd_assert (buf_size > pos);
  buf[pos] = 0; /* Null terminate the result */
  {
    char extra[64];
    snprintf (extra, sizeof (extra), "final_pos=%zu buf_size=%zu", pos, buf_size);
    mhd_probe_log ("before_commit", value, response, extra);
  }

  if (NULL == hdr)
  {
    struct MHD_HTTP_Res_Header *new_hdr; /**< new "Connection" header */
    /* Create new response header entry */
    new_hdr = MHD_calloc_ (1, sizeof (struct MHD_HTTP_Res_Header));
    if (NULL != new_hdr)
    {
      new_hdr->header = malloc (key_len + 1);
      if (NULL != new_hdr->header)
      {
        memcpy (new_hdr->header, key, key_len + 1);
        new_hdr->header_size = key_len;
        new_hdr->value = buf;
        new_hdr->value_size = pos;
        new_hdr->kind = MHD_HEADER_KIND;
        if (value_has_close)
          response->flags_auto = (MHD_RAF_HAS_CONNECTION_HDR
                                  | MHD_RAF_HAS_CONNECTION_CLOSE);
        else
          response->flags_auto = MHD_RAF_HAS_CONNECTION_HDR;
        _MHD_insert_header_first (response, new_hdr);
        mhd_probe_log ("inserted_new_header", value, response, NULL);
        return MHD_YES;
      }
      free (new_hdr);
    }
    free (buf);
    mhd_probe_log ("failed_insert_new_header", value, response, NULL);
    return MHD_NO;
  }

  /* Update existing header entry */
  free (hdr->value);
  hdr->value = buf;
  hdr->value_size = pos;
  if (value_has_close && ! already_has_close)
    response->flags_auto |= MHD_RAF_HAS_CONNECTION_CLOSE;
  mhd_probe_log ("updated_existing_header", value, response, NULL);
  return MHD_YES;
}
