#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <sys/stat.h>
#include <errno.h>
#include <time.h>

/* Probe logging helper. This function is used to record control flow
   and state information to /tmp/logs/490.txt so other agents can
   reproduce evidence for the reported violation scenario. */
static void
ahc_probe_log (const char *event, const void *url_ptr, const char *method,
               const char *version, const char *upg_header,
               const char *con_header, const char *ws_version_header,
               const char *ws_key_header, size_t key_size, int ret_val)
{
  FILE *f;
  /* Ensure directory exists; ignore errors if it already exists. */
  (void) mkdir("/tmp/logs", 0777);
  f = fopen("/tmp/logs/490.txt", "a");
  if (NULL == f)
  {
    return;
  }
  time_t t = time(NULL);
  fprintf (f, "[%ld] EVENT: %s\n", (long) t, event);
  fprintf (f, "  url_ptr=%p\n", url_ptr);
  fprintf (f, "  method=%s\n", (method != NULL) ? method : "(null)");
  fprintf (f, "  version=%s\n", (version != NULL) ? version : "(null)");
  fprintf (f, "  upg_header=%s\n", (upg_header != NULL) ? upg_header : "(null)");
  fprintf (f, "  con_header=%s\n", (con_header != NULL) ? con_header : "(null)");
  fprintf (f, "  ws_version_header=%s\n", (ws_version_header != NULL) ? ws_version_header : "(null)");
  fprintf (f, "  ws_key_header=%s\n", (ws_key_header != NULL) ? ws_key_header : "(null)");
  fprintf (f, "  key_size=%zu\n", key_size);
  fprintf (f, "  ret_val=%d\n", ret_val);
  fprintf (f, "\n");
  fclose (f);
}

static enum MHD_Result
ahc_cb (void *cls, struct MHD_Connection *con, const char *url,
        const char *method, const char *version, const char *upload_data,
        size_t *upload_data_size, void **req_cls)
{
  struct MHD_Response *res;
  const char *upg_header;
  const char *con_header;
  const char *ws_version_header;
  const char *ws_key_header;
  char *ws_ac_value;
  enum MHD_Result ret;
  size_t key_size;

  (void) cls;               /* Unused. Silent compiler warning. */
  (void) url;               /* Unused. Silent compiler warning. */
  (void) upload_data;       /* Unused. Silent compiler warning. */
  (void) upload_data_size;  /* Unused. Silent compiler warning. */

  /* Log that the explicit no-op cast for 'url' was executed. This
     documents that the '(void) url;' statement runs but has no
     observable effect (the reported dead code). */
  ahc_probe_log ("noop_url_executed", (const void *) url, method, version,
                 NULL, NULL, NULL, NULL, 0, -1);

  if (NULL == *req_cls)
  {
    /* Record entering the initial request-class branch. */
    ahc_probe_log ("initial_req_cls_set", (const void *) url, method, version,
                   NULL, NULL, NULL, NULL, 0, (int) MHD_YES);

    *req_cls = (void *) 1;
    return MHD_YES;
  }
  *req_cls = NULL;

  upg_header = MHD_lookup_connection_value (con, MHD_HEADER_KIND,
                                            MHD_HTTP_HEADER_UPGRADE);
  con_header = MHD_lookup_connection_value (con, MHD_HEADER_KIND,
                                            MHD_HTTP_HEADER_CONNECTION);
  if (MHD_NO == is_websocket_request (con, upg_header, con_header))
  {
    /* Capture state just before sending the chat page response. */
    ret = send_chat_page (con);
    ahc_probe_log ("send_chat_page", (const void *) url, method, version,
                   upg_header, con_header, NULL, NULL, 0, (int) ret);
    return ret;
  }
  if ((0 != strcmp (method, MHD_HTTP_METHOD_GET))
      || (0 != strcmp (version, MHD_HTTP_VERSION_1_1)))
  {
    ret = send_bad_request (con);
    ahc_probe_log ("send_bad_request_method_version_mismatch", (const void *) url,
                   method, version, upg_header, con_header, NULL, NULL, 0, (int) ret);
    return ret;
  }
  ws_version_header =
    MHD_lookup_connection_value (con, MHD_HEADER_KIND,
                                 MHD_HTTP_HEADER_SEC_WEBSOCKET_VERSION);
  if ((NULL == ws_version_header)
      || (0 != strcmp (ws_version_header, WS_SEC_WEBSOCKET_VERSION)))
  {
    ret = send_upgrade_required (con);
    ahc_probe_log ("send_upgrade_required", (const void *) url, method, version,
                   upg_header, con_header, ws_version_header, NULL, 0, (int) ret);
    return ret;
  }
  ret = MHD_lookup_connection_value_n (con, MHD_HEADER_KIND,
                                       MHD_HTTP_HEADER_SEC_WEBSOCKET_KEY,
                                       strlen (
                                         MHD_HTTP_HEADER_SEC_WEBSOCKET_KEY),
                                       &ws_key_header, &key_size);
  if ((MHD_NO == ret) || (key_size != WS_KEY_LEN))
  {
    ret = send_bad_request (con);
    ahc_probe_log ("send_bad_request_key_missing_or_invalid", (const void *) url,
                   method, version, upg_header, con_header, ws_version_header,
                   ws_key_header, key_size, (int) ret);
    return ret;
  }
  ret = ws_get_accept_value (ws_key_header, &ws_ac_value);
  if (MHD_NO == ret)
  {
    ahc_probe_log ("ws_get_accept_value_failed", (const void *) url, method, version,
                   upg_header, con_header, ws_version_header, ws_key_header,
                   key_size, (int) ret);
    return ret;
  }
  res = MHD_create_response_for_upgrade (&uh_cb, NULL);
  if (MHD_YES !=
      MHD_add_response_header (res, MHD_HTTP_HEADER_SEC_WEBSOCKET_ACCEPT,
                               ws_ac_value))
  {
    free (ws_ac_value);
    MHD_destroy_response (res);
    ahc_probe_log ("add_response_header_accept_failed", (const void *) url, method, version,
                   upg_header, con_header, ws_version_header, ws_key_header,
                   key_size, (int) MHD_NO);
    return MHD_NO;
  }
  free (ws_ac_value);
  if (MHD_YES !=
      MHD_add_response_header (res, MHD_HTTP_HEADER_UPGRADE, WS_UPGRADE_VALUE))
  {
    MHD_destroy_response (res);
    ahc_probe_log ("add_response_header_upgrade_failed", (const void *) url, method, version,
                   upg_header, con_header, ws_version_header, ws_key_header,
                   key_size, (int) MHD_NO);
    return MHD_NO;
  }
  ret = MHD_queue_response (con, MHD_HTTP_SWITCHING_PROTOCOLS, res);
  MHD_destroy_response (res);

  /* Final return: log the result of queuing the response. */
  ahc_probe_log ("final_return", (const void *) url, method, version,
                 upg_header, con_header, ws_version_header, ws_key_header,
                 key_size, (int) ret);
  return ret;
}
