#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <time.h>
#include <stdint.h>
#include <unistd.h>
#include <sys/types.h>
#include <sys/stat.h>
#include <fcntl.h>
#include <errno.h>

/* Probe helpers: append human-readable traces to /tmp/logs/402.txt. */
static void probe_log_raw(const char *msg)
{
  FILE *f = fopen("/tmp/logs/402.txt", "a");
  if (NULL == f)
    return;
  time_t t = time(NULL);
  fprintf(f, "[%ld] %s\n", (long)t, msg);
  fclose(f);
}

static void probe_log_param_ptr(const char *label, const void *ptr)
{
  FILE *f = fopen("/tmp/logs/402.txt", "a");
  if (NULL == f)
    return;
  time_t t = time(NULL);
  fprintf(f, "[%ld] %s: ptr=%p\n", (long)t, label, ptr);
  fclose(f);
}

static void probe_log_param_str(const char *label, const char *s)
{
  FILE *f = fopen("/tmp/logs/402.txt", "a");
  if (NULL == f)
    return;
  time_t t = time(NULL);
  fprintf(f, "[%ld] %s: %s\n", (long)t, label, (s != NULL) ? s : "(null)");
  fclose(f);
}

/* This probe records entry to ahc_echo and the pointers/strings of interest. */
static void probe_ahc_echo_entry(void *cls,
                                 struct MHD_Connection *connection,
                                 const char *url,
                                 const char *method,
                                 const char *version,
                                 const char *upload_data,
                                 size_t *upload_data_size,
                                 void **req_cls)
{
  probe_log_raw("ENTER ahc_echo");
  probe_log_param_ptr("cls", cls);
  probe_log_param_ptr("connection", (const void *) connection);
  probe_log_param_str("method", method);
  probe_log_param_str("url", url);
  probe_log_param_ptr("version (ptr)", version);
  probe_log_param_ptr("upload_data (ptr)", upload_data);
  probe_log_param_ptr("upload_data_size (ptr)", upload_data_size);
  probe_log_param_ptr("req_cls (ptr)", req_cls);
}

/* This records that the explicit (void) cast no-op was executed. */
static void probe_ahc_echo_noop_executed(const char *param_name, const void *ptr)
{
  FILE *f = fopen("/tmp/logs/402.txt", "a");
  if (NULL == f)
    return;
  time_t t = time(NULL);
  fprintf(f, "[%ld] executed no-op (void) for parameter '%s' (ptr=%p)\n",
          (long)t, param_name, ptr);
  fclose(f);
}

/* The original ahc_echo function with minimal probes inserted. */
static enum MHD_Result
ahc_echo (void *cls,
          struct MHD_Connection *connection,
          const char *url,
          const char *method,
          const char *version,
          const char *upload_data,
          size_t *upload_data_size, void **req_cls)
{
  static int aptr;
  struct MHD_Response *response;
  enum MHD_Result ret;
  int fd;
  struct stat buf;

  /* Log entry and parameter state before performing the explicit no-op casts. */
  probe_ahc_echo_entry(cls, connection, url, method, version,
                       upload_data, upload_data_size, req_cls);

  (void) cls;               /* Unused. Silent compiler warning. */
  /* Probe immediately after the no-op to demonstrate it executed. */
  probe_ahc_echo_noop_executed("cls", cls);

  (void) version;           /* Unused. Silent compiler warning. */
  probe_ahc_echo_noop_executed("version", (const void *) version);

  (void) upload_data;       /* Unused. Silent compiler warning. */
  probe_ahc_echo_noop_executed("upload_data", (const void *) upload_data);

  (void) upload_data_size;  /* Unused. Silent compiler warning. */
  probe_ahc_echo_noop_executed("upload_data_size", (const void *) upload_data_size);

  if ( (0 != strcmp (method, MHD_HTTP_METHOD_GET)) &&
       (0 != strcmp (method, MHD_HTTP_METHOD_HEAD)) )
    return MHD_NO;              /* unexpected method */
  if (&aptr != *req_cls)
  {
    /* do never respond on first call */
    *req_cls = &aptr;
    probe_log_raw("first call: set *req_cls = &aptr and returning MHD_YES");
    return MHD_YES;
  }
  *req_cls = NULL;                 /* reset when done */
  probe_log_raw("serving request (not first call)");
  /* WARNING: direct usage of url as filename is for example only!
   * NEVER pass received data directly as parameter to file manipulation
   * functions. Always check validity of data before using.
   */
  if (NULL != strstr (url, "../")) /* Very simplified check! */
    fd = -1;                       /* Do not allow usage of parent directories. */
  else
    fd = open (url + 1, O_RDONLY);
  if (-1 != fd)
  {
    if ( (0 != fstat (fd, &buf)) ||
         (! S_ISREG (buf.st_mode)) )
    {
      /* not a regular file, refuse to serve */
      if (0 != close (fd))
        abort ();
      fd = -1;
    }
  }
  if (-1 == fd)
  {
    response = MHD_create_response_from_buffer_static (strlen (PAGE),
                                                       PAGE);
    ret = MHD_queue_response (connection, MHD_HTTP_NOT_FOUND, response);
    MHD_destroy_response (response);
  }
  else
  {
    response = MHD_create_response_from_fd64 ((uint64_t) buf.st_size, fd);
    if (NULL == response)
    {
      if (0 != close (fd))
        abort ();
      return MHD_NO;
    }
    ret = MHD_queue_response (connection, MHD_HTTP_OK, response);
    MHD_destroy_response (response);
  }

  probe_log_raw("EXIT ahc_echo");
  return ret;
}
