static void ahc_echo_probe(const char *tag, const void *ptr)
{
  FILE *log = fopen("/tmp/logs/442.txt", "a");
  if (NULL == log)
    return;
  /* Print a minimal, safe representation of the probe information. */
  fprintf(log, "ahc_echo: probe %s: ptr=%p\n", tag, ptr);
  fclose(log);
}

static enum MHD_Result
ahc_echo (void *cls,
          struct MHD_Connection *connection,
          const char *url,
          const char *method,
          const char *version,
          const char *upload_data,
          size_t *upload_data_size, void **req_cls)
{
  static int aptr;
  struct MHD_Response *response;
  enum MHD_Result ret;
  FILE *file;
  int fd;
  DIR *dir;
  struct stat buf;
  char emsg[1024];

  /* Log entry and the execution of the originally-unused variable casts.
     These probe calls give evidence that the following cast-to-void
     expression-statements are actually executed at runtime. */
  ahc_echo_probe("enter", (const void *)method);
  ahc_echo_probe("cls_before_void_cast", cls);
  (void) cls;               /* Unused. Silent compiler warning. */
  ahc_echo_probe("version_before_void_cast", version);
  (void) version;           /* Unused. Silent compiler warning. */
  ahc_echo_probe("upload_data_before_void_cast", upload_data);
  (void) upload_data;       /* Unused. Silent compiler warning. */
  ahc_echo_probe("upload_data_size_before_void_cast", (const void *)upload_data_size);
  (void) upload_data_size;  /* Unused. Silent compiler warning. */

  if (0 != strcmp (method, MHD_HTTP_METHOD_GET))
  {
    ahc_echo_probe("unexpected_method", method);
    return MHD_NO;              /* unexpected method */
  }
  if (&aptr != *req_cls)
  {
    /* do never respond on first call */
    *req_cls = &aptr;
    ahc_echo_probe("first_call_set_req_cls", *req_cls);
    return MHD_YES;
  }
  *req_cls = NULL;                  /* reset when done */
  ahc_echo_probe("proceeding_after_first_call", *req_cls);

  file = fopen (&url[1], "rb");
  if (NULL != file)
  {
    fd = fileno (file);
    if (-1 == fd)
    {
      (void) fclose (file);
      ahc_echo_probe("fileno_failed", file);
      return MHD_NO;     /* internal error */
    }
    if ( (0 != fstat (fd, &buf)) ||
         (! S_ISREG (buf.st_mode)) )
    {
      /* not a regular file, refuse to serve */
      fclose (file);
      file = NULL;
      ahc_echo_probe("not_regular_file_or_fstat_failed", (const void *)&buf);
    }
  }

  if (NULL == file)
  {
    dir = opendir (".");
    if (NULL == dir)
    {
      /* most likely cause: more concurrent requests than
         available file descriptors / 2 */
      snprintf (emsg,
                sizeof (emsg),
                "Failed to open directory `.': %s\n",
                strerror (errno));
      response = MHD_create_response_from_buffer (strlen (emsg),
                                                  emsg,
                                                  MHD_RESPMEM_MUST_COPY);
      if (NULL == response)
        return MHD_NO;
      ret = MHD_queue_response (connection,
                                MHD_HTTP_SERVICE_UNAVAILABLE,
                                response);
      MHD_destroy_response (response);
      ahc_echo_probe("queued_service_unavailable", response);
    }
    else
    {
      response = MHD_create_response_from_callback (MHD_SIZE_UNKNOWN,
                                                    32 * 1024,
                                                    &dir_reader,
                                                    dir,
                                                    &dir_free_callback);
      if (NULL == response)
      {
        closedir (dir);
        ahc_echo_probe("dir_response_creation_failed", dir);
        return MHD_NO;
      }
      ret = MHD_queue_response (connection, MHD_HTTP_OK, response);
      MHD_destroy_response (response);
      ahc_echo_probe("queued_dir_response", response);
    }
  }
  else
  {
    response = MHD_create_response_from_callback ((size_t) buf.st_size,
                                                  32 * 1024, /* 32k page size */
                                                  &file_reader,
                                                  file,
                                                  &file_free_callback);
    if (NULL == response)
    {
      fclose (file);
      ahc_echo_probe("file_response_creation_failed", file);
      return MHD_NO;
    }
    ret = MHD_queue_response (connection, MHD_HTTP_OK, response);
    MHD_destroy_response (response);
    ahc_echo_probe("queued_file_response", response);
  }
  ahc_echo_probe("returning", (const void *)(intptr_t)ret);
  return ret;
}
