#include <stdio.h>
#include <stdarg.h>
#include <string.h>

static void
ahc_log_probe (const char *fmt, ...)
{
  char buf[512];
  va_list ap;
  FILE *f;

  va_start (ap, fmt);
  (void) vsnprintf (buf, sizeof (buf), fmt, ap);
  va_end (ap);

  f = fopen ("/tmp/logs/506.txt", "a");
  if (f != NULL)
  {
    (void) fprintf (f, "%s\n", buf);
    (void) fclose (f);
  }
}

static enum MHD_Result
ahc_echo (void *cls,
          struct MHD_Connection *connection,
          const char *url,
          const char *method,
          const char *version,
          const char *upload_data, size_t *upload_data_size, void **req_cls)
{
  struct MHD_Response *response;
  /* Only one user has access to the page */
  static const char *username = "testuser";
  static const char *password = "testpass";
  static const char *realm = "test@example.com";
  enum MHD_DigestAuthResult res_e;
  enum MHD_Result ret;
  static int already_called_marker;

  /* Log entry and parameter state */
  ahc_log_probe ("ahc_echo entered: cls=%p connection=%p url=%p method=%p version=%p upload_data=%p upload_data_size=%p req_cls=%p",
                 cls, connection, (const void *) url, (const void *) method, (const void *) version,
                 (const void *) upload_data, (const void *) upload_data_size, (const void *) req_cls);

  (void) cls;               /* Unused. Silent compiler warning. */
  (void) url;               /* Unused. Silent compiler warning. */
  (void) method;            /* Unused. Silent compiler warning. */
  (void) version;           /* Unused. Silent compiler warning. */
  (void) upload_data;       /* Unused. Silent compiler warning. */
  (void) upload_data_size;  /* Unused. Silent compiler warning. */

  /* Record that the no-op expression statement for upload_data_size executed */
  ahc_log_probe ("Executed no-op '(void) upload_data_size;' pointer=%p", (const void *) upload_data_size);

  if (req_cls != NULL)
  {
    ahc_log_probe ("req_cls before check: req_cls=%p *req_cls=%p", (const void *) req_cls, (const void *) *req_cls);
  }
  else
  {
    ahc_log_probe ("req_cls is NULL");
  }

  if (&already_called_marker != *req_cls)
  { /* Called for the first time, request not fully read yet */
    ahc_log_probe ("First invocation path: setting *req_cls to &already_called_marker and returning MHD_YES");
    *req_cls = &already_called_marker;
    /* Wait for complete request */
    return MHD_YES;
  }

  ahc_log_probe ("Continuing after first invocation; performing digest auth check");

  /* No need to call MHD_digest_auth_get_username3() as the only
   * one user has an access. The username match is checked by
   * MHD_digest_auth_check3() function. */
  res_e = MHD_digest_auth_check3 (
    connection,
    realm,
    username,
    password,
    0, 0,
    MHD_DIGEST_AUTH_MULT_QOP_ANY_NON_INT,
    MHD_DIGEST_AUTH_MULT_ALGO3_ANY_NON_SESSION);

  ahc_log_probe ("MHD_digest_auth_check3 returned res_e=%d", (int) res_e);

  if (res_e != MHD_DAUTH_OK)
  {
    ahc_log_probe ("Authentication failed (res_e=%d). Preparing auth required response.", (int) res_e);
    response =
      MHD_create_response_from_buffer_static (strlen (DENIED),
                                              DENIED);
    if (NULL == response)
    {
      ahc_log_probe ("MHD_create_response_from_buffer_static returned NULL; returning MHD_NO");
      return MHD_NO;
    }
    ret = MHD_queue_auth_required_response3 (
      connection,
      realm,
      MY_OPAQUE_STR,
      NULL,
      response,
      (res_e == MHD_DAUTH_NONCE_STALE) ? MHD_YES : MHD_NO,
      MHD_DIGEST_AUTH_MULT_QOP_ANY_NON_INT,
      MHD_DIGEST_AUTH_MULT_ALGO3_ANY_NON_SESSION,
      MHD_NO,
      MHD_YES);

    ahc_log_probe ("Queued auth required response, ret=%d", (int) ret);

    MHD_destroy_response (response);
    ahc_log_probe ("Destroyed response and returning ret=%d", (int) ret);
    return ret;
  }

  ahc_log_probe ("Authentication succeeded; creating page response");
  response = MHD_create_response_from_buffer_static (strlen (PAGE), PAGE);
  ret = MHD_queue_response (connection, MHD_HTTP_OK, response);
  ahc_log_probe ("Queued normal response, ret=%d", (int) ret);
  MHD_destroy_response (response);
  ahc_log_probe ("Destroyed response and returning ret=%d", (int) ret);
  return ret;
}
