#include <stdio.h>
#include <stddef.h>
#include <string.h>

/* Probe function: append execution traces and state to /tmp/logs/530.txt */
static void ahc_echo_probe(const char *event,
                           const char *method,
                           const char *url,
                           const char *version,
                           const char *upload_data,
                           size_t *upload_data_size,
                           void **req_cls)
{
  FILE *f = fopen("/tmp/logs/530.txt", "a");
  if (NULL == f)
    return;

  fprintf(f, "[ahc_echo] event=%s\n", event);
  if (method)
    fprintf(f, "  method='%s'\n", method);
  if (url)
    fprintf(f, "  url='%s'\n", url);
  if (version)
    fprintf(f, "  version='%s'\n", version);
  fprintf(f, "  upload_data=%p\n", (void *) upload_data);
  fprintf(f, "  upload_data_size_ptr=%p\n", (void *) upload_data_size);
  if (upload_data_size)
    fprintf(f, "  upload_data_size_value=%zu\n", *upload_data_size);
  fprintf(f, "  req_cls_ptr=%p\n", (void *) req_cls);
  /* Do NOT dereference *req_cls here: it may be uninitialised on first call. */
  fprintf(f, "---\n");
  fclose(f);
}

static enum MHD_Result
ahc_echo (void *cls,
          struct MHD_Connection *connection,
          const char *url,
          const char *method,
          const char *version,
          const char *upload_data, size_t *upload_data_size, void **req_cls)
{
  static int aptr;
  struct MHD_Response *response;
  enum MHD_Result ret;
  struct MHD_BasicAuthInfo *auth_info;
  int fail;
  (void) cls;               /* Unused. Silent compiler warning. */
  (void) url;               /* Unused. Silent compiler warning. */
  (void) version;           /* Unused. Silent compiler warning. */
  (void) upload_data;       /* Unused. Silent compiler warning. */
  (void) upload_data_size;  /* Unused. Silent compiler warning. */

  /* Log entry and the fact that the (void) cast lines executed (no-op). */
  ahc_echo_probe("entry_after_voids",
                 method, url, version, upload_data, upload_data_size, req_cls);

  if (0 != strcmp (method, "GET"))
  {
    ahc_echo_probe("unexpected_method", method, url, version, upload_data, upload_data_size, req_cls);
    return MHD_NO;              /* unexpected method */
  }
  if (&aptr != *req_cls)
  {
    /* do never respond on first call */
    ahc_echo_probe("first_call_set_req_cls", method, url, version, upload_data, upload_data_size, req_cls);
    *req_cls = &aptr;
    return MHD_YES;
  }
  ahc_echo_probe("second_call_clear_req_cls_before_reset", method, url, version, upload_data, upload_data_size, req_cls);
  *req_cls = NULL;                  /* reset when done */

  /* require: "Aladdin" with password "open sesame" */
  auth_info = MHD_basic_auth_get_username_password3 (connection);
  fail = ( (NULL == auth_info) ||
           (strlen ("Aladdin") != auth_info->username_len) ||
           (0 != memcmp (auth_info->username, "Aladdin",
                         auth_info->username_len)) ||
           /* The next check against NULL is optional,
            * if 'password' is NULL then 'password_len' is always zero. */
           (NULL == auth_info->password) ||
           (strlen ("open sesame") != auth_info->password_len) ||
           (0 != memcmp (auth_info->password, "open sesame",
                         auth_info->password_len)) );
  if (fail)
  {
    ahc_echo_probe("auth_failed", method, url, version, upload_data, upload_data_size, req_cls);
    response =
      MHD_create_response_from_buffer_static (strlen (DENIED),
                                              (const void *) DENIED);
    ret = MHD_queue_basic_auth_required_response3 (connection,
                                                   "TestRealm",
                                                   MHD_NO,
                                                   response);
  }
  else
  {
    ahc_echo_probe("auth_succeeded", method, url, version, upload_data, upload_data_size, req_cls);
    response =
      MHD_create_response_from_buffer_static (strlen (PAGE),
                                              (const void *) PAGE);
    ret = MHD_queue_response (connection, MHD_HTTP_OK, response);
  }
  if (NULL != auth_info)
    MHD_free (auth_info);
  MHD_destroy_response (response);

  ahc_echo_probe("exit", method, url, version, upload_data, upload_data_size, req_cls);
  return ret;
}
