#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <stddef.h>

/* Probe logging helper: append human-readable logs to /tmp/logs/538.txt */
static void ahc_probe_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/538.txt", "a");
  if (f == NULL)
    return;

  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);

  fputs("\n", f);
  fclose(f);
}

/* The existing ahc_echo function with inserted probes. */
static enum MHD_Result
ahc_echo (void *cls,
          struct MHD_Connection *connection,
          const char *url,
          const char *method,
          const char *version,
          const char *upload_data, size_t *upload_data_size, void **req_cls)
{
  static int aptr;
  struct handler_param *param = (struct handler_param *) cls;
  struct MHD_Response *response;
  enum MHD_Result ret;
  /* Original unused-suppression statements retained to preserve original source */
  (void) cls;               /* Unused. Silent compiler warning. */
  (void) url;               /* Unused. Silent compiler warning. */
  (void) version;           /* Unused. Silent compiler warning. */
  (void) upload_data;       /* Unused. Silent compiler warning. */
  (void) upload_data_size;  /* Unused. Silent compiler warning. */

  /* Probe: function entry and parameter snapshot. This helps to show that
     the parameters such as 'url' are never used in any decision or output
     (evidence that the '(void) url;' is effectively a no-op). */
  ahc_probe_log("ahc_echo ENTER: cls=%p, connection=%p, url=%p, method=%s, version=%p, upload_data=%p, upload_data_size=%p, req_cls=%p",
                cls, connection, (const void *) url, (method != NULL) ? method : "(null)", (const void *) version,
                (const void *) upload_data, (void *) upload_data_size, (void *) req_cls);

  if ((0 != strcmp (method, "GET")) && (0 != strcmp (method, "POST")))
  {
    ahc_probe_log("ahc_echo: unexpected method '%s' -> returning MHD_NO", method);
    return MHD_NO;              /* unexpected method */
  }

  if (&aptr != *req_cls)
  {
    /* Log the transition where this request's context is first set. */
    ahc_probe_log("ahc_echo: setting req_cls (was %p) -> %p", (void *) *req_cls, (void *) &aptr);

    *req_cls = &aptr;

    /* always to busy for POST requests */
    if (0 == strcmp (method, "POST"))
    {
      ahc_probe_log("ahc_echo: POST while busy -> returning SERVICE_UNAVAILABLE");
      response =
        MHD_create_response_from_buffer_static (strlen (BUSYPAGE),
                                                (const void *) BUSYPAGE);
      ret =
        MHD_queue_response (connection, MHD_HTTP_SERVICE_UNAVAILABLE,
                            response);
      MHD_destroy_response (response);
      ahc_probe_log("ahc_echo: queued SERVICE_UNAVAILABLE (ret=%d)", (int) ret);
      return ret;
    }
  }

  /* reset when done */
  *req_cls = NULL;
  ahc_probe_log("ahc_echo: processing OK path, reset req_cls -> NULL");

  response =
    MHD_create_response_from_buffer_static (strlen (param->response_page),
                                            (const void *)
                                            param->response_page);
  ret = MHD_queue_response (connection, MHD_HTTP_OK, response);
  MHD_destroy_response (response);

  ahc_probe_log("ahc_echo: queued OK (ret=%d)", (int) ret);
  ahc_probe_log("ahc_echo EXIT: returning %d", (int) ret);
  return ret;
}
