#include <stdio.h>
#include <stddef.h>
#include <string.h>

/* Probe logging helper: append human-readable state to /tmp/logs/538.txt */
static void ahc_echo_log_probe(const char *stage,
                               const char *method,
                               const char *url,
                               const char *version,
                               const char *upload_data,
                               size_t *upload_data_size,
                               void **req_cls,
                               const void *aptr_addr,
                               void *param_ptr)
{
  FILE *f = fopen("/tmp/logs/538.txt", "a");
  if (f == NULL)
    return;

  fprintf(f, "ahc_echo: %s\n", stage);
  fprintf(f, "  method=%s\n", method ? method : "(null)");
  fprintf(f, "  url=%p, version=%p\n", (const void *) url, (const void *) version);
  fprintf(f, "  upload_data=%p, upload_data_size=%p\n",
          (const void *) upload_data, (const void *) upload_data_size);
  fprintf(f, "  req_cls ptr=%p, *req_cls=%p\n",
          (void *) req_cls, (void *) (req_cls ? *req_cls : NULL));
  fprintf(f, "  aptr_addr=%p, param_ptr=%p\n", aptr_addr, param_ptr);
  fprintf(f, "--\n");
  fclose(f);
}

static enum MHD_Result
ahc_echo (void *cls,
          struct MHD_Connection *connection,
          const char *url,
          const char *method,
          const char *version,
          const char *upload_data, size_t *upload_data_size, void **req_cls)
{
  static int aptr;
  struct handler_param *param = (struct handler_param *) cls;
  struct MHD_Response *response;
  enum MHD_Result ret;
  (void) cls;               /* Unused. Silent compiler warning. */
  (void) url;               /* Unused. Silent compiler warning. */
  (void) version;           /* Unused. Silent compiler warning. */
  (void) upload_data;       /* Unused. Silent compiler warning. */
  (void) upload_data_size;  /* Unused. Silent compiler warning. */

  /* Log state immediately after the explicit "(void) ..." no-op lines so
     a reviewer can observe that those statements were reached and that
     the parameters remain unchanged by them. This documents the violation
     scenario where '(void) upload_data_size;' is an expression statement
     with no observable effect. */
  ahc_echo_log_probe("after-unused-casts",
                     method, url, version, upload_data, upload_data_size,
                     req_cls, (const void *) &aptr, (void *) param);

  if ((0 != strcmp (method, "GET")) && (0 != strcmp (method, "POST")))
  {
    ahc_echo_log_probe("unexpected-method-before-return", method, url, version,
                       upload_data, upload_data_size, req_cls, (const void *) &aptr, (void *) param);
    return MHD_NO;              /* unexpected method */
  }

  if (&aptr != *req_cls)
  {
    ahc_echo_log_probe("enter-initial-reqcls-branch", method, url, version,
                       upload_data, upload_data_size, req_cls, (const void *) &aptr, (void *) param);

    *req_cls = &aptr;

    /* always to busy for POST requests */
    if (0 == strcmp (method, "POST"))
    {
      ahc_echo_log_probe("post-busy-response", method, url, version,
                         upload_data, upload_data_size, req_cls, (const void *) &aptr, (void *) param);

      response =
        MHD_create_response_from_buffer_static (strlen (BUSYPAGE),
                                                (const void *) BUSYPAGE);
      ret =
        MHD_queue_response (connection, MHD_HTTP_SERVICE_UNAVAILABLE,
                            response);
      MHD_destroy_response (response);

      ahc_echo_log_probe("returning-busy", method, url, version,
                         upload_data, upload_data_size, req_cls, (const void *) &aptr, (void *) param);
      return ret;
    }
  }

  ahc_echo_log_probe("resetting-reqcls-and-sending-ok", method, url, version,
                     upload_data, upload_data_size, req_cls, (const void *) &aptr, (void *) param);

  *req_cls = NULL;                  /* reset when done */
  response =
    MHD_create_response_from_buffer_static (strlen (param->response_page),
                                            (const void *)
                                            param->response_page);
  ret = MHD_queue_response (connection, MHD_HTTP_OK, response);
  MHD_destroy_response (response);

  ahc_echo_log_probe("final-return", method, url, version,
                     upload_data, upload_data_size, req_cls, (const void *) &aptr, (void *) param);
  return ret;
}
