static void probe_log(const char *stage, const char *method, const char *url, struct Request *req)
{
  FILE *f = fopen("/tmp/logs/602.txt", "a");
  if (NULL == f)
    return;
  fprintf(f, "%s: method=%p", stage, (void *) method);
  if (method)
    fprintf(f, " \"%s\"", method);
  fprintf(f, ", url=%p", (void *) url);
  if (url)
    fprintf(f, " \"%s\"", url);
  fprintf(f, ", req=%p", (void *) req);
  if (req)
    fprintf(f, ", req->timerfd=%d", req->timerfd);
  fprintf(f, "\n");
  fclose(f);
}

static enum MHD_Result
ahc_echo (void *cls,
          struct MHD_Connection *connection,
          const char *url,
          const char *method,
          const char *version,
          const char *upload_data, size_t *upload_data_size, void **req_cls)
{
  struct MHD_Response *response;
  enum MHD_Result ret;
  struct Request *req;
  struct itimerspec ts;

  /* Log entry state before the no-op casts so we can show they have no effect */
  probe_log("entry_before_noop", method, url, NULL);

  (void) cls;
  (void) method;
  (void) version;           /* Unused. Silence compiler warning. */
  (void) upload_data;       /* Unused. Silence compiler warning. */
  (void) upload_data_size;  /* Unused. Silence compiler warning. */

  /* Log entry state after the no-op casts to demonstrate they do not alter state */
  probe_log("entry_after_noop", method, url, NULL);

  req = *req_cls;
  probe_log("after_req_deref", method, url, req);
  if (NULL == req)
  {

    probe_log("alloc_request_enter", method, url, NULL);
    req = malloc (sizeof(struct Request));
    if (NULL == req)
    {
      probe_log("alloc_request_failed", method, url, NULL);
      return MHD_NO;
    }
    probe_log("alloc_request_success", method, url, req);
    req->connection = connection;
    req->timerfd = -1;
    *req_cls = req;
    probe_log("alloc_request_returning_yes", method, url, req);
    return MHD_YES;
  }

  probe_log("req_present_check", method, url, req);
  if (-1 != req->timerfd)
  {
    /* send response (echo request url) */
    probe_log("sending_echo_response", method, url, req);
    response = MHD_create_response_from_buffer_copy (strlen (url),
                                                     (const void *) url);
    if (NULL == response)
    {
      probe_log("create_response_failed", method, url, req);
      return MHD_NO;
    }
    ret = MHD_queue_response (connection,
                              MHD_HTTP_OK,
                              response);
    probe_log("response_queued", method, url, req);
    MHD_destroy_response (response);
    probe_log("response_destroyed_returning_ret", method, url, req);
    return ret;
  }
  /* create timer and suspend connection */
  probe_log("create_timer_enter", method, url, req);
  req->timerfd = timerfd_create (CLOCK_MONOTONIC, TFD_NONBLOCK);
  if (-1 == req->timerfd)
  {
    printf ("timerfd_create: %s", strerror (errno));
    probe_log("timerfd_create_failed", method, url, req);
    return MHD_NO;
  }
  probe_log("timerfd_created", method, url, req);
  evt.events = EPOLLIN;
  evt.data.ptr = req;
  if (-1 == epoll_ctl (epfd, EPOLL_CTL_ADD, req->timerfd, &evt))
  {
    printf ("epoll_ctl: %s", strerror (errno));
    probe_log("epoll_ctl_failed", method, url, req);
    return MHD_NO;
  }
  probe_log("epoll_ctl_added", method, url, req);
  ts.it_value.tv_sec = 1;
  ts.it_value.tv_nsec = 0;
  ts.it_interval.tv_sec = 0;
  ts.it_interval.tv_nsec = 0;
  if (-1 == timerfd_settime (req->timerfd, 0, &ts, NULL))
  {
    printf ("timerfd_settime: %s", strerror (errno));
    probe_log("timerfd_settime_failed", method, url, req);
    return MHD_NO;
  }
  probe_log("timerfd_settime_ok", method, url, req);
  MHD_suspend_connection (connection);
  probe_log("suspended_returning_yes", method, url, req);
  return MHD_YES;
}
