static void ahc_log_probe(const char *event,
                              const char *url,
                              const char *version,
                              struct Request *req,
                              struct MHD_Connection *connection)
{
  FILE *f = fopen("/tmp/logs/602.txt", "a");
  if (NULL == f)
    return;
  fprintf(f, "EVENT: %s\n", event);
  fprintf(f, "  url: %s\n", (url != NULL) ? url : "(null)");
  fprintf(f, "  version: %s\n", (version != NULL) ? version : "(null)");
  fprintf(f, "  req: %p\n", (void *) req);
  fprintf(f, "  connection: %p\n", (void *) connection);
  fprintf(f, "----------------------------------------\n");
  fclose(f);
}

static enum MHD_Result
ahc_echo (void *cls,
          struct MHD_Connection *connection,
          const char *url,
          const char *method,
          const char *version,
          const char *upload_data, size_t *upload_data_size, void **req_cls)
{
  struct MHD_Response *response;
  enum MHD_Result ret;
  struct Request *req;
  struct itimerspec ts;

  /* Original no-op casts preserved to keep behavior identical */
  (void) cls;
  (void) method;
  (void) version;           /* Unused. Silence compiler warning. */
  (void) upload_data;       /* Unused. Silence compiler warning. */
  (void) upload_data_size;  /* Unused. Silence compiler warning. */

  /* Probe: record that we executed the no-op casts (the statement under scrutiny) */
  ahc_log_probe("after_void_casts", url, version, NULL, connection);

  req = *req_cls;
  if (NULL == req)
  {
    ahc_log_probe("req_is_null_enter", url, version, NULL, connection);

    req = malloc (sizeof(struct Request));
    if (NULL == req)
      return MHD_NO;

    req->connection = connection;
    req->timerfd = -1;
    *req_cls = req;

    ahc_log_probe("allocated_req_and_returning", url, version, req, connection);
    return MHD_YES;
  }

  ahc_log_probe("req_exists_check", url, version, req, connection);

  if (-1 != req->timerfd)
  {
    /* send response (echo request url) */
    ahc_log_probe("sending_response", url, version, req, connection);

    response = MHD_create_response_from_buffer_copy (strlen (url),
                                                     (const void *) url);
    if (NULL == response)
      return MHD_NO;
    ret = MHD_queue_response (connection,
                              MHD_HTTP_OK,
                              response);
    MHD_destroy_response (response);

    ahc_log_probe("response_queued", url, version, req, connection);
    return ret;
  }

  /* create timer and suspend connection */
  ahc_log_probe("creating_timerfd", url, version, req, connection);

  req->timerfd = timerfd_create (CLOCK_MONOTONIC, TFD_NONBLOCK);
  if (-1 == req->timerfd)
  {
    printf ("timerfd_create: %s", strerror (errno));
    ahc_log_probe("timerfd_create_failed", url, version, req, connection);
    return MHD_NO;
  }
  evt.events = EPOLLIN;
  evt.data.ptr = req;
  if (-1 == epoll_ctl (epfd, EPOLL_CTL_ADD, req->timerfd, &evt))
  {
    printf ("epoll_ctl: %s", strerror (errno));
    ahc_log_probe("epoll_ctl_failed", url, version, req, connection);
    return MHD_NO;
  }
  ts.it_value.tv_sec = 1;
  ts.it_value.tv_nsec = 0;
  ts.it_interval.tv_sec = 0;
  ts.it_interval.tv_nsec = 0;
  if (-1 == timerfd_settime (req->timerfd, 0, &ts, NULL))
  {
    printf ("timerfd_settime: %s", strerror (errno));
    ahc_log_probe("timerfd_settime_failed", url, version, req, connection);
    return MHD_NO;
  }
  ahc_log_probe("suspending_connection", url, version, req, connection);
  MHD_suspend_connection (connection);
  ahc_log_probe("suspended_and_returning", url, version, req, connection);
  return MHD_YES;
}
