#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <stdarg.h>
#include <time.h>
#include <stddef.h>

/* Probe logging helper: append a timestamped message with state info to the log file. */
static void probe_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/434.txt", "a");
  if (NULL == f)
    return;

  time_t t = time(NULL);
  (void) fprintf(f, "[%ld] ", (long) t);

  va_list ap;
  va_start(ap, fmt);
  (void) vfprintf(f, fmt, ap);
  va_end(ap);

  (void) fprintf(f, "\n");
  (void) fclose(f);
}

static enum MHD_Result
answer_to_connection (void *cls, struct MHD_Connection *connection,
                      const char *url, const char *method,
                      const char *version, const char *upload_data,
                      size_t *upload_data_size, void **req_cls)
{
  /* Original unused-parameter casts are preserved. */
  (void) cls;               /* Unused. Silent compiler warning. */
  (void) url;               /* Unused. Silent compiler warning. */
  (void) version;           /* Unused. Silent compiler warning. */

  /* Log that the entry and the explicit void casts were executed and capture key state. */
  probe_log("enter: answer_to_connection called; method=%s, version=%s, upload_data_size_ptr=%p, req_cls_ptr=%p",
            method ? method : "(null)", version ? version : "(null)", (void *) upload_data_size, (void *) req_cls);

  if (NULL == *req_cls)
  {
    struct connection_info_struct *con_info;

    probe_log("branch: initializing connection_info_struct (req_cls was NULL)");

    con_info = malloc (sizeof (struct connection_info_struct));
    if (NULL == con_info)
    {
      probe_log("alloc failure: malloc returned NULL");
      return MHD_NO;
    }
    con_info->answerstring = NULL;

    if (0 == strcmp (method, "POST"))
    {
      probe_log("branch: method == POST; creating post processor");

      con_info->postprocessor =
        MHD_create_post_processor (connection, POSTBUFFERSIZE,
                                   iterate_post, (void *) con_info);

      if (NULL == con_info->postprocessor)
      {
        probe_log("postprocessor creation failed; freeing con_info and returning MHD_NO");
        free (con_info);
        return MHD_NO;
      }

      con_info->connectiontype = POST;
      probe_log("postprocessor created: con_info=%p, postprocessor=%p, connectiontype=POST",
                (void *) con_info, (void *) con_info->postprocessor);
    }
    else
    {
      con_info->connectiontype = GET;
      probe_log("connectiontype set to GET: con_info=%p", (void *) con_info);
    }

    *req_cls = (void *) con_info;

    probe_log("initialization complete: *req_cls=%p; returning MHD_YES", *req_cls);
    return MHD_YES;
  }

  /* Subsequent requests */
  if (0 == strcmp (method, "GET"))
  {
    probe_log("handling GET: will return askpage via send_page");
    return send_page (connection, askpage);
  }

  if (0 == strcmp (method, "POST"))
  {
    struct connection_info_struct *con_info = *req_cls;

    probe_log("handling POST: con_info=%p, upload_data_size_ptr=%p, upload_data_size=%zu",
              (void *) con_info, (void *) upload_data_size,
              (upload_data_size != NULL) ? *upload_data_size : (size_t)0);

    if (upload_data_size != NULL && *upload_data_size != 0)
    {
      probe_log("POST: upload_data_size != 0; calling MHD_post_process with size=%zu",
                *upload_data_size);

      if (MHD_YES !=
          MHD_post_process (con_info->postprocessor,
                            upload_data,
                            *upload_data_size))
      {
        probe_log("MHD_post_process failed: returning MHD_NO");
        return MHD_NO;
      }
      *upload_data_size = 0;

      probe_log("MHD_post_process accepted data; set *upload_data_size=0; returning MHD_YES");
      return MHD_YES;
    }
    else if (NULL != con_info->answerstring)
    {
      probe_log("POST: no more upload data and answerstring present; sending answerstring");
      return send_page (connection, con_info->answerstring);
    }
    else
    {
      probe_log("POST: no upload data and no answerstring; falling through to errorpage");
    }
  }

  probe_log("fallback: sending errorpage");
  return send_page (connection, errorpage);
}
