#include <stdio.h>
#include <stdarg.h>
#include <time.h>
#include <inttypes.h>
#include <unistd.h>

static void
mhd_log_probe (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/594.txt", "a");
  if (!f)
    return;
  time_t t = time (NULL);
  fprintf (f, "[pid %ld][%ld] ", (long) getpid (), (long) t);
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fprintf (f, "\n");
  fclose (f);
}

static bool
calculate_add_nonce_with_retry (struct MHD_Connection *const connection,
                                const char *realm,
                                struct DigestAlgorithm *da,
                                char *nonce)
{
  const uint64_t timestamp1 = MHD_monotonic_msec_counter ();
  const size_t realm_len = strlen (realm);
  mhd_assert (! da->hashing);

  mhd_log_probe ("Entered calculate_add_nonce_with_retry: connection=%p, daemon=%p, timestamp1=%" PRIu64 ", realm_len=%zu, da=%p, hashing=%d",
                 (void *) connection,
                 (void *) connection->daemon,
                 timestamp1,
                 realm_len,
                 (void *) da,
                 (int) da->hashing);

#ifdef HAVE_MESSAGES
  if (0 == MHD_get_master (connection->daemon)->digest_auth_rand_size)
    MHD_DLOG (connection->daemon,
              _ ("Random value was not initialised by " \
                 "MHD_OPTION_DIGEST_AUTH_RANDOM or " \
                 "MHD_OPTION_DIGEST_AUTH_RANDOM_COPY, generated nonces " \
                 "are predictable.\n"));
#endif

  mhd_log_probe ("Calling calculate_add_nonce (first attempt) with timestamp1=%" PRIu64, timestamp1);
  if (! calculate_add_nonce (connection, timestamp1, realm, realm_len, da,
                             nonce))
  {
    mhd_log_probe ("Initial calculate_add_nonce failed (no free slot or collision)");
    /* Either:
     * 1. The same nonce was already generated. If it will be used then one
     * of the clients will fail (as no initial 'nc' value could be given to
     * the client, the second client which will use 'nc=00000001' will fail).
     * 2. Another nonce uses the same slot, and this nonce never has been
     * used by the client and this nonce is still fresh enough.
     */
    const size_t digest_size = digest_get_size (da);
    mhd_log_probe ("digest_size=%zu", digest_size);
    char nonce2[NONCE_STD_LEN (MAX_DIGEST) + 1];
    uint64_t timestamp2;
#ifdef MHD_DIGEST_HAS_EXT_ERROR
    {
      bool ext_err = digest_ext_error (da);
      mhd_log_probe ("digest_ext_error -> %d", (int) ext_err);
      if (ext_err)
      {
        mhd_log_probe ("Returning false due to digest_ext_error -> no retry needed");
        return false; /* No need to re-try */
      }
    }
#endif /* MHD_DIGEST_HAS_EXT_ERROR */
    {
      size_t nc_size = MHD_get_master (connection->daemon)->nonce_nc_size;
      mhd_log_probe ("nonce_nc_size=%zu", nc_size);
      if (0 == nc_size)
      {
        mhd_log_probe ("Returning false because nonce_nc_size == 0 -> no retry needed");
        return false; /* No need to re-try */
      }
    }

    timestamp2 = MHD_monotonic_msec_counter ();
    mhd_log_probe ("timestamp2 initial=%" PRIu64, timestamp2);
    if (timestamp1 == timestamp2)
    {
      mhd_log_probe ("timestamp1 == timestamp2 -> entering jitter generation branch");
      /* The timestamps are equal, need to generate some arbitrary
       * difference for nonce. */
      /* As the number is needed only to differentiate clients, weak
       * pseudo-random generators could be used. Seeding is not needed. */
      uint64_t base1;
      uint32_t base2;
      uint16_t base3;
      uint8_t base4;
#ifdef HAVE_RANDOM
      base1 = ((uint64_t) random ()) ^ UINT64_C (0x54a5acff5be47e63);
      base4 = 0xb8;
      mhd_log_probe ("HAVE_RANDOM branch used: base1 after random set to 0x%" PRIx64 ", base4=0x%02x", base1, base4);
#elif defined(HAVE_RAND)
      base1 = ((uint64_t) rand ()) ^ UINT64_C (0xc4bcf553b12f3965);
      base4 = 0x92;
      mhd_log_probe ("HAVE_RAND branch used: base1 after rand set to 0x%" PRIx64 ", base4=0x%02x", base1, base4);
#else
      /* Monotonic msec counter alone does not really help here as it is already
         known that this value is not unique. */
      base1 = ((uint64_t) (uintptr_t) nonce2) ^ UINT64_C (0xf2e1b21bc6c92655);
      base2 = ((uint32_t) (base1 >> 32)) ^ ((uint32_t) base1);
      base2 = _MHD_ROTR32 (base2, 4);
      base3 = ((uint16_t) (base2 >> 16)) ^ ((uint16_t) base2);
      base4 = ((uint8_t) (base3 >> 8)) ^ ((uint8_t) base3);
      base1 = ((uint64_t) MHD_monotonic_msec_counter ())
              ^ UINT64_C (0xccab93f72cf5b15);
      mhd_log_probe ("no-rand branch used: base1=0x%" PRIx64 ", base2=0x%08x, base3=0x%04x, base4=0x%02x",
                     base1, base2, base3, base4);
#endif
      base2 = ((uint32_t) (base1 >> 32)) ^ ((uint32_t) base1);
      base2 = _MHD_ROTL32 (base2, (((base4 >> 4) ^ base4) % 32));
      base3 = ((uint16_t) (base2 >> 16)) ^ ((uint16_t) base2);
      base4 = ((uint8_t) (base3 >> 8)) ^ ((uint8_t) base3);
      /* Use up to 127 ms difference */
      timestamp2 -= (base4 & DAUTH_JUMPBACK_MAX);
      mhd_log_probe ("After jitter adjustments: base1=0x%" PRIx64 ", base2=0x%08x, base3=0x%04x, base4=0x%02x, timestamp2=%" PRIu64,
                     base1, base2, base3, base4, timestamp2);
      if (timestamp1 == timestamp2)
      {
        timestamp2 -= 2; /* Fallback value */
        mhd_log_probe ("timestamp still equal after jitter -> fallback timestamp2=%" PRIu64, timestamp2);
      }
    }
    digest_reset (da);
    mhd_log_probe ("Calling calculate_add_nonce (second attempt) with timestamp2=%" PRIu64, timestamp2);
    if (! calculate_add_nonce (connection, timestamp2, realm, realm_len, da,
                               nonce2))
    {
      /* No free slot has been found. Re-tries are expensive, just use
       * the generated nonce. As it is not stored in nonce-nc map array,
       * the next request of the client will be recognized as valid, but 'stale'
       * so client should re-try automatically. */
      mhd_log_probe ("Second calculate_add_nonce failed -> no free slot found, returning false (use generated nonce)");
      return false;
    }
    mhd_log_probe ("Second calculate_add_nonce succeeded -> copying nonce2 into nonce and returning true");
    memcpy (nonce, nonce2, NONCE_STD_LEN (digest_size));
  }
  mhd_log_probe ("Exiting calculate_add_nonce_with_retry -> returning true");
  return true;
}
