#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <string.h>
#include <stddef.h>

/* Probe logging helper: append human-readable logs to /tmp/logs/595.txt */
static void
mhd_probe_log (const char *fmt, ...)
{
  FILE *f = fopen ("/tmp/logs/595.txt", "a");
  if (f == NULL)
    return; /* Best-effort logging, don't change program behavior if logging fails */

  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fputc ('\n', f);
  fclose (f);
}

static bool
calculate_add_nonce_with_retry (struct MHD_Connection *const connection,
                                const char *realm,
                                struct DigestAlgorithm *da,
                                char *nonce)
{
  const uint64_t timestamp1 = MHD_monotonic_msec_counter ();
  const size_t realm_len = strlen (realm);
  mhd_assert (! da->hashing);

  /* Log entry state */
  mhd_probe_log ("ENTER calculate_add_nonce_with_retry: connection=%p, daemon=%p, realm='%s', realm_len=%zu, timestamp1=%llu",
                 (void *) connection,
                 (void *) connection->daemon,
                 realm ? realm : "(null)",
                 realm_len,
                 (unsigned long long) timestamp1);

#ifdef HAVE_MESSAGES
  if (0 == MHD_get_master (connection->daemon)->digest_auth_rand_size)
  {
    /* Log the condition where digest_auth_rand_size is zero */
    mhd_probe_log ("HAVE_MESSAGES: master()->digest_auth_rand_size == 0 (master=%p)",
                   (void *) MHD_get_master (connection->daemon));

    MHD_DLOG (connection->daemon,
              _ ("Random value was not initialised by " \
                 "MHD_OPTION_DIGEST_AUTH_RANDOM or " \
                 "MHD_OPTION_DIGEST_AUTH_RANDOM_COPY, generated nonces " \
                 "are predictable.\n"));
  }
#else
  /* Still log presence/absence of master for tracing the violation scenario */
  mhd_probe_log ("NO_MESSAGES build: master pointer=%p", (void *) MHD_get_master (connection->daemon));
#endif

  if (! calculate_add_nonce (connection, timestamp1, realm, realm_len, da,
                             nonce))
  {
    /* Log failure of first attempt and info useful to reproduce scenario */
    const size_t digest_size = digest_get_size (da);
    mhd_probe_log ("First calculate_add_nonce() failed: digest_size=%zu, hashing=%d",
                   digest_size, (int) da->hashing);

    char nonce2[NONCE_STD_LEN (MAX_DIGEST) + 1];
    uint64_t timestamp2;
#ifdef MHD_DIGEST_HAS_EXT_ERROR
    if (digest_ext_error (da))
    {
      mhd_probe_log ("digest_ext_error() reported an extended error -> returning false");
      return false; /* No need to re-try */
    }
#endif /* MHD_DIGEST_HAS_EXT_ERROR */

    /* Log master->nonce_nc_size to evidence the read-only nature of the access
       that triggers the MISRA Rule 8.13 concern. */
    {
      struct MHD_Daemon *master_for_log = MHD_get_master (connection->daemon);
      /* Record the pointer and the field we read */
      mhd_probe_log ("master pointer=%p, master->nonce_nc_size=%zu",
                     (void *) master_for_log,
                     (size_t) master_for_log->nonce_nc_size);
      if (0 == master_for_log->nonce_nc_size)
      {
        mhd_probe_log ("master->nonce_nc_size == 0 -> returning false (no need to re-try)");
        return false; /* No need to re-try */
      }
    }

    timestamp2 = MHD_monotonic_msec_counter ();
    mhd_probe_log ("timestamp1=%llu, timestamp2(initial)=%llu",
                   (unsigned long long) timestamp1,
                   (unsigned long long) timestamp2);

    if (timestamp1 == timestamp2)
    {
      mhd_probe_log ("Timestamps equal; entering adjustment branch to derive timestamp2");

      uint64_t base1;
      uint32_t base2;
      uint16_t base3;
      uint8_t base4;
#ifdef HAVE_RANDOM
      base1 = ((uint64_t) random ()) ^ UINT64_C (0x54a5acff5be47e63);
      base4 = 0xb8;
#elif defined(HAVE_RAND)
      base1 = ((uint64_t) rand ()) ^ UINT64_C (0xc4bcf553b12f3965);
      base4 = 0x92;
#else
      /* Monotonic msec counter alone does not really help here as it is already
         known that this value is not unique. */
      base1 = ((uint64_t) (uintptr_t) nonce2) ^ UINT64_C (0xf2e1b21bc6c92655);
      base2 = ((uint32_t) (base1 >> 32)) ^ ((uint32_t) base1);
      base2 = _MHD_ROTR32 (base2, 4);
      base3 = ((uint16_t) (base2 >> 16)) ^ ((uint16_t) base2);
      base4 = ((uint8_t) (base3 >> 8)) ^ ((uint8_t) base3);
      base1 = ((uint64_t) MHD_monotonic_msec_counter ())
              ^ UINT64_C (0xccab93f72cf5b15);
#endif
      base2 = ((uint32_t) (base1 >> 32)) ^ ((uint32_t) base1);
      base2 = _MHD_ROTL32 (base2, (((base4 >> 4) ^ base4) % 32));
      base3 = ((uint16_t) (base2 >> 16)) ^ ((uint16_t) base2);
      base4 = ((uint8_t) (base3 >> 8)) ^ ((uint8_t) base3);
      /* Use up to 127 ms difference */
      timestamp2 -= (base4 & DAUTH_JUMPBACK_MAX);
      if (timestamp1 == timestamp2)
      {
        timestamp2 -= 2; /* Fallback value */
        mhd_probe_log ("timestamp adjustment fallback applied; new timestamp2=%llu",
                       (unsigned long long) timestamp2);
      }
      else
      {
        mhd_probe_log ("timestamp adjusted to timestamp2=%llu (after base calculations)",
                       (unsigned long long) timestamp2);
      }
    }

    digest_reset (da);
    mhd_probe_log ("Calling calculate_add_nonce() second time with timestamp2=%llu",
                   (unsigned long long) timestamp2);

    if (! calculate_add_nonce (connection, timestamp2, realm, realm_len, da,
                               nonce2))
    {
      /* No free slot has been found. Re-tries are expensive, just use
       * the generated nonce. As it is not stored in nonce-nc map array,
       * the next request of the client will be recognized as valid, but 'stale'
       * so client should re-try automatically. */
      mhd_probe_log ("Second calculate_add_nonce() also failed -> returning false (use generated nonce)");
      return false;
    }

    mhd_probe_log ("Second calculate_add_nonce() succeeded; copying nonce2 to nonce (digest_size=%zu)",
                   digest_size);
    memcpy (nonce, nonce2, NONCE_STD_LEN (digest_size));
  }

  mhd_probe_log ("Exiting calculate_add_nonce_with_retry with result=true");
  return true;
}
