static void
probe_log(const char *stage,
          const char *method,
          const char *url,
          const char *version,
          const char *upload_data,
          size_t *upload_data_size,
          void *request)
{
  FILE *f = fopen("/tmp/logs/551.txt", "a");
  if (NULL == f)
    return;
  /* Log timestamp and key pointer/state information without dereferencing
     potentially invalid pointers (to avoid introducing new out-of-bounds
     accesses). */
  time_t t = time(NULL);
  fprintf(f, "[%ld] stage=%s method_ptr=%p method_is_null=%d url_ptr=%p upload_data_ptr=%p upload_data_size=%s request_ptr=%p\n",
          (long)t,
          stage,
          (const void *) method,
          (method == NULL),
          (const void *) url,
          (const void *) upload_data,
          (upload_data_size != NULL) ? ({ static char buf[32]; snprintf(buf, sizeof buf, "%zu", *upload_data_size); buf; }) : "(null)",
          request);
  fflush(f);
  fclose(f);
}

static enum MHD_Result
create_response (void *cls,
                 struct MHD_Connection *connection,
                 const char *url,
                 const char *method,
                 const char *version,
                 const char *upload_data,
                 size_t *upload_data_size,
                 void **req_cls)
{
  struct MHD_Response *response;
  struct Request *request;
  struct Session *session;
  enum MHD_Result ret;
  unsigned int i;
  (void) cls;               /* Unused. Silent compiler warning. */
  (void) version;           /* Unused. Silent compiler warning. */

  request = *req_cls;
  if (NULL == request)
  {
    request = calloc (1, sizeof (struct Request));
    if (NULL == request)
    {
      fprintf (stderr, "calloc error: %s\n", strerror (errno));
      return MHD_NO;
    }
    *req_cls = request;

    /* Probe before calling strcmp to capture the pointer value and state.
       Do not dereference 'method' here (only log pointer and nullness) so we
       don't introduce additional out-of-bounds accesses. */
    probe_log("initial_post_check", method, url, version, upload_data, upload_data_size, (void *) request);

    if (0 == strcmp (method, MHD_HTTP_METHOD_POST))
    {
      request->pp = MHD_create_post_processor (connection, 1024,
                                               &post_iterator, request);
      if (NULL == request->pp)
      {
        fprintf (stderr, "Failed to setup post processor for `%s'\n",
                 url);
        return MHD_NO; /* internal error */
      }
    }
    return MHD_YES;
  }
  if (NULL == request->session)
  {
    request->session = get_session (connection);
    if (NULL == request->session)
    {
      fprintf (stderr, "Failed to setup session for `%s'\n",
               url);
      return MHD_NO; /* internal error */
    }
  }
  session = request->session;
  session->start = time (NULL);

  /* Probe before POST processing strcmp */
  probe_log("post_processing_check", method, url, version, upload_data, upload_data_size, (void *) request);

  if (0 == strcmp (method, MHD_HTTP_METHOD_POST))
  {
    /* evaluate POST data */
    if (MHD_YES !=
        MHD_post_process (request->pp,
                          upload_data,
                          *upload_data_size))
      return MHD_NO;
    if (0 != *upload_data_size)
    {
      *upload_data_size = 0;
      return MHD_YES;
    }
    /* done with POST data, serve response */
    MHD_destroy_post_processor (request->pp);
    request->pp = NULL;
    method = MHD_HTTP_METHOD_GET;   /* fake 'GET' */
    if (NULL != request->post_url)
      url = request->post_url;
  }

  /* Probe before GET/HEAD checks to capture current method/url pointers. */
  probe_log("get_head_check", method, url, version, upload_data, upload_data_size, (void *) request);

  if ( (0 == strcmp (method, MHD_HTTP_METHOD_GET)) ||
       (0 == strcmp (method, MHD_HTTP_METHOD_HEAD)) )
  {
    /* find out which page to serve */
    i = 0;
    /* Log page/url pointers before entering the loop to help reproduce any
       issues related to url pointer validity used in strcmp below. */
    probe_log("page_search_start", method, url, version, upload_data, upload_data_size, (void *) request);

    while ( (pages[i].url != NULL) &&
            (0 != strcmp (pages[i].url, url)) )
      i++;
    ret = pages[i].handler (pages[i].handler_cls,
                            pages[i].mime,
                            session, connection);
    if (ret != MHD_YES)
      fprintf (stderr, "Failed to create page for `%s'\n",
               url);
    return ret;
  }
  /* unsupported HTTP method */
  response =
    MHD_create_response_from_buffer_static (strlen (METHOD_ERROR),
                                            (const void *) METHOD_ERROR);
  ret = MHD_queue_response (connection,
                            MHD_HTTP_NOT_ACCEPTABLE,
                            response);
  MHD_destroy_response (response);
  return ret;
}
