static void
create_response_log(const char *stage,
                    const char *url,
                    const char *method,
                    const char *version,
                    const char *upload_data,
                    size_t upload_data_size,
                    void **req_cls,
                    struct Request *request,
                    struct Session *session)
{
  FILE *fp = fopen("/tmp/logs/551.txt", "a");
  if (NULL == fp)
    return;

  fprintf(fp, "--- create_response: %s ---\n", stage);
  fprintf(fp, "url_ptr=%p, method_ptr=%p, version_ptr=%p, upload_data_ptr=%p\n",
          (const void *) url,
          (const void *) method,
          (const void *) version,
          (const void *) upload_data);
  if (req_cls != NULL)
    fprintf(fp, "req_cls_ptr=%p, *req_cls=%p\n", (void *) req_cls, (void *) *req_cls);
  else
    fprintf(fp, "req_cls_ptr=NULL\n");

  fprintf(fp, "upload_data_size=%zu\n", upload_data_size);
  fprintf(fp, "request_ptr=%p, session_ptr=%p\n",
          (void *) request, (void *) session);
  fprintf(fp, "(Note: string pointer values printed, contents NOT dereferenced here)\n");
  fprintf(fp, "-----------------------------\n");
  fclose(fp);
}

static enum MHD_Result
create_response (void *cls,
                 struct MHD_Connection *connection,
                 const char *url,
                 const char *method,
                 const char *version,
                 const char *upload_data,
                 size_t *upload_data_size,
                 void **req_cls)
{
  struct MHD_Response *response;
  struct Request *request;
  struct Session *session;
  enum MHD_Result ret;
  unsigned int i;
  (void) cls;               /* Unused. Silent compiler warning. */
  (void) version;           /* Unused. Silent compiler warning. */

  /* Log entry state */
  create_response_log("entry", url, method, version, upload_data,
                      (upload_data_size != NULL) ? *upload_data_size : 0,
                      req_cls, NULL, NULL);

  request = *req_cls;
  if (NULL == request)
  {
    create_response_log("request_null_before_alloc", url, method, version,
                        upload_data,
                        (upload_data_size != NULL) ? *upload_data_size : 0,
                        req_cls, request, NULL);

    request = calloc (1, sizeof (struct Request));
    if (NULL == request)
    {
      fprintf (stderr, "calloc error: %s\n", strerror (errno));
      create_response_log("calloc_failed", url, method, version, upload_data,
                          (upload_data_size != NULL) ? *upload_data_size : 0,
                          req_cls, request, NULL);
      return MHD_NO;
    }
    *req_cls = request;

    /* Log after allocation */
    create_response_log("request_allocated", url, method, version, upload_data,
                        (upload_data_size != NULL) ? *upload_data_size : 0,
                        req_cls, request, NULL);

    /* Log just before strcmp for POST */
    create_response_log("before_strcmp_post_initial", url, method, version,
                        upload_data,
                        (upload_data_size != NULL) ? *upload_data_size : 0,
                        req_cls, request, NULL);

    if (0 == strcmp (method, MHD_HTTP_METHOD_POST))
    {
      request->pp = MHD_create_post_processor (connection, 1024,
                                               &post_iterator, request);
      if (NULL == request->pp)
      {
        fprintf (stderr, "Failed to setup post processor for `%s'\n",
                 url);
        create_response_log("post_processor_failed", url, method, version,
                            upload_data,
                            (upload_data_size != NULL) ? *upload_data_size : 0,
                            req_cls, request, NULL);
        return MHD_NO; /* internal error */
      }
      create_response_log("post_processor_created", url, method, version,
                          upload_data,
                          (upload_data_size != NULL) ? *upload_data_size : 0,
                          req_cls, request, NULL);
    }
    return MHD_YES;
  }

  create_response_log("have_request", url, method, version, upload_data,
                      (upload_data_size != NULL) ? *upload_data_size : 0,
                      req_cls, request, NULL);

  if (NULL == request->session)
  {
    request->session = get_session (connection);
    if (NULL == request->session)
    {
      fprintf (stderr, "Failed to setup session for `%s'\n",
               url);
      create_response_log("get_session_failed", url, method, version,
                          upload_data,
                          (upload_data_size != NULL) ? *upload_data_size : 0,
                          req_cls, request, NULL);
      return MHD_NO; /* internal error */
    }
  }
  session = request->session;
  session->start = time (NULL);

  /* Log just before potential POST processing on second pass */
  create_response_log("before_post_processing", url, method, version,
                      upload_data,
                      (upload_data_size != NULL) ? *upload_data_size : 0,
                      req_cls, request, session);

  if (0 == strcmp (method, MHD_HTTP_METHOD_POST))
  {
    /* evaluate POST data */
    if (MHD_YES !=
        MHD_post_process (request->pp,
                          upload_data,
                          *upload_data_size))
      return MHD_NO;
    if (0 != *upload_data_size)
    {
      *upload_data_size = 0;
      create_response_log("post_more_data_expected", url, method, version,
                          upload_data,
                          (upload_data_size != NULL) ? *upload_data_size : 0,
                          req_cls, request, session);
      return MHD_YES;
    }
    /* done with POST data, serve response */
    MHD_destroy_post_processor (request->pp);
    request->pp = NULL;
    method = MHD_HTTP_METHOD_GET;   /* fake 'GET' */
    if (NULL != request->post_url)
      url = request->post_url;

    create_response_log("post_handled_method_set_get", url, method, version,
                        upload_data,
                        (upload_data_size != NULL) ? *upload_data_size : 0,
                        req_cls, request, session);
  }

  /* Log just before GET/HEAD handling */
  create_response_log("before_get_head_check", url, method, version,
                      upload_data,
                      (upload_data_size != NULL) ? *upload_data_size : 0,
                      req_cls, request, session);

  if ( (0 == strcmp (method, MHD_HTTP_METHOD_GET)) ||
       (0 == strcmp (method, MHD_HTTP_METHOD_HEAD)) )
  {
    /* find out which page to serve */
    i = 0;
    create_response_log("page_lookup_start", url, method, version,
                        upload_data,
                        (upload_data_size != NULL) ? *upload_data_size : 0,
                        req_cls, request, session);

    while ( (pages[i].url != NULL) &&
            (0 != strcmp (pages[i].url, url)) )
      i++;
    ret = pages[i].handler (pages[i].handler_cls,
                            pages[i].mime,
                            session, connection);
    if (ret != MHD_YES)
      fprintf (stderr, "Failed to create page for `%s'\n",
               url);

    create_response_log("page_served_return", url, method, version,
                        upload_data,
                        (upload_data_size != NULL) ? *upload_data_size : 0,
                        req_cls, request, session);

    return ret;
  }
  /* unsupported HTTP method */
  create_response_log("unsupported_method", url, method, version,
                      upload_data,
                      (upload_data_size != NULL) ? *upload_data_size : 0,
                      req_cls, request, session);

  response =
    MHD_create_response_from_buffer_static (strlen (METHOD_ERROR),
                                            (const void *) METHOD_ERROR);
  ret = MHD_queue_response (connection,
                            MHD_HTTP_NOT_ACCEPTABLE,
                            response);
  MHD_destroy_response (response);
  return ret;
}
