/* Lightweight logging helpers to collect control-flow/state information
   into /tmp/logs/466.txt. These are static so they do not change linkage
   or the external interface. */
static void log_write(const char *msg)
{
  FILE *f = fopen("/tmp/logs/466.txt", "a");
  if (NULL != f)
  {
    fprintf(f, "%s\n", msg);
    fclose(f);
  }
}

static void log_printf(const char *fmt, ...)
{
  va_list ap;
  char buf[512];

  va_start(ap, fmt);
  vsnprintf(buf, sizeof(buf), fmt, ap);
  va_end(ap);

  log_write(buf);
}

static enum MHD_Result
generate_page (void *cls,
               struct MHD_Connection *connection,
               const char *url,
               const char *method,
               const char *version,
               const char *upload_data,
               size_t *upload_data_size, void **req_cls)
{
  struct MHD_Response *response;
  enum MHD_Result ret;
  int fd;
  struct stat buf;

  /* Log entry and parameter state to help reproduce the reported
     dead-operation scenario for the (void) <param>; statements. */
  log_printf("generate_page: ENTER; cls=%p connection=%p url=%s method=%s version=%s upload_data=%p upload_data_size=%p req_cls=%p",
             cls, (void *) connection, (NULL != url) ? url : "(null)",
             (NULL != method) ? method : "(null)",
             (NULL != version) ? version : "(null)",
             (void *) upload_data, (void *) upload_data_size, (void *) req_cls);

  (void) cls;               /* Unused. Silent compiler warning. */
  log_printf("generate_page: executed (void) cls; (address=%p)", cls);
  (void) version;           /* Unused. Silent compiler warning. */
  log_printf("generate_page: executed (void) version; (value=%s)",
             (NULL != version) ? version : "(null)");

  if (0 != strcmp (url, "/"))
  {
    /* should be file download */
#ifdef MHD_HAVE_LIBMAGIC
    char file_data[MAGIC_HEADER_SIZE];
    ssize_t got;
#endif /* MHD_HAVE_LIBMAGIC */
    const char *mime;

    log_printf("generate_page: file-download branch; url=%s", url);

    if ( (0 != strcmp (method, MHD_HTTP_METHOD_GET)) &&
         (0 != strcmp (method, MHD_HTTP_METHOD_HEAD)) )
    {
      log_printf("generate_page: unexpected method for file download: %s", method);
      return MHD_NO;    /* unexpected method (we're not polite...) */
    }

    fd = -1;
    if ( (NULL == strstr (&url[1], "..")) &&
         ('/' != url[1]) )
    {
      fd = open (&url[1], O_RDONLY);
      log_printf("generate_page: attempted open('%s') -> fd=%d", &url[1], fd);
      if ( (-1 != fd) &&
           ( (0 != fstat (fd, &buf)) ||
             (! S_ISREG (buf.st_mode)) ) )
      {
        (void) close (fd);
        log_printf("generate_page: file not regular or fstat failed; closed fd=%d", fd);
        fd = -1;
      }
    }
    if (-1 == fd)
    {
      log_printf("generate_page: file not found; returning 404 for url=%s", url);
      return MHD_queue_response (connection,
                                 MHD_HTTP_NOT_FOUND,
                                 file_not_found_response);
    }
#ifdef MHD_HAVE_LIBMAGIC
    /* read beginning of the file to determine mime type  */
    got = read (fd, file_data, sizeof (file_data));
    (void) lseek (fd, 0, SEEK_SET);
    if (0 < got)
      mime = magic_buffer (magic, file_data, (size_t) got);
    else
#endif /* MHD_HAVE_LIBMAGIC */
    mime = NULL;
    {
      /* Set mime-type by file-extension in some cases */
      const char *ldot = strrchr (&url[1], '.');

      if (NULL != ldot)
      {
        if (0 == strcasecmp (ldot,
                             ".html"))
          mime = "text/html";
        if (0 == strcasecmp (ldot,
                             ".css"))
          mime = "text/css";
        if (0 == strcasecmp (ldot,
                             ".css3"))
          mime = "text/css";
        if (0 == strcasecmp (ldot,
                             ".js"))
          mime = "application/javascript";
        log_printf("generate_page: mime determined by extension %s -> %s", ldot, (mime != NULL) ? mime : "(null)");
      }

    }

    if (NULL == (response = MHD_create_response_from_fd ((size_t) buf.st_size,
                                                         fd)))
    {
      /* internal error (i.e. out of memory) */
      (void) close (fd);
      log_printf("generate_page: MHD_create_response_from_fd failed; closed fd=%d", fd);
      return MHD_NO;
    }

    /* add mime type if we had one */
    if (NULL != mime)
      (void) MHD_add_response_header (response,
                                      MHD_HTTP_HEADER_CONTENT_TYPE,
                                      mime);
    ret = MHD_queue_response (connection,
                              MHD_HTTP_OK,
                              response);
    log_printf("generate_page: queued file response (fd=%d) -> ret=%d", fd, (int) ret);
    MHD_destroy_response (response);
    return ret;
  }

  if (0 == strcmp (method, MHD_HTTP_METHOD_POST))
  {
    /* upload! */
    struct UploadContext *uc = *req_cls;

    log_printf("generate_page: POST (upload) branch; req_cls=%p uc=%p", (void *) req_cls, (void *) uc);

    if (NULL == uc)
    {
      if (NULL == (uc = malloc (sizeof (struct UploadContext))))
        return MHD_NO; /* out of memory, close connection */
      memset (uc, 0, sizeof (struct UploadContext));
      uc->fd = -1;
      uc->connection = connection;
      uc->pp = MHD_create_post_processor (connection,
                                          64 * 1024 /* buffer size */,
                                          &process_upload_data, uc);
      if (NULL == uc->pp)
      {
        /* out of memory, close connection */
        free (uc);
        log_printf("generate_page: failed to create post processor; out of memory");
        return MHD_NO;
      }
      *req_cls = uc;
      log_printf("generate_page: created UploadContext %p and post-processor %p", (void *) uc, (void *) uc->pp);
      return MHD_YES;
    }
    if (0 != *upload_data_size)
    {
      if (NULL == uc->response)
        (void) MHD_post_process (uc->pp,
                                 upload_data,
                                 *upload_data_size);
      *upload_data_size = 0;
      log_printf("generate_page: processed upload chunk; reset upload_data_size to 0");
      return MHD_YES;
    }
    /* end of upload, finish it! */
    MHD_destroy_post_processor (uc->pp);
    uc->pp = NULL;
    if (-1 != uc->fd)
    {
      close (uc->fd);
      uc->fd = -1;
      log_printf("generate_page: closed upload fd and reset to -1");
    }
    if (NULL != uc->response)
    {
      log_printf("generate_page: upload produced response; queuing FORBIDDEN");
      return MHD_queue_response (connection,
                                 MHD_HTTP_FORBIDDEN,
                                 uc->response);
    }
    else
    {
      log_printf("generate_page: upload finished successfully; updating directory");
      update_directory ();
      return return_directory_response (connection);
    }
  }
  if ( (0 == strcmp (method, MHD_HTTP_METHOD_GET)) ||
       (0 == strcmp (method, MHD_HTTP_METHOD_HEAD)) )
  {
    log_printf("generate_page: directory listing branch for method=%s", method);
    return return_directory_response (connection);
  }

  /* unexpected request, refuse */
  log_printf("generate_page: unexpected request; refusing (method=%s)", method);
  return MHD_queue_response (connection,
                             MHD_HTTP_FORBIDDEN,
                             request_refused_response);
}
