static void probe_log_entry(const char *stage,
                                const char *url,
                                const char *method,
                                const char *version)
{
  FILE *f = fopen("/tmp/logs/466.txt", "a");
  if (NULL == f)
    return;
  /* Log basic control-flow/states useful to reproduce the reported scenario. */
  fprintf(f, "probe: %s | url=%s | method=%s | version_ptr=%p",
          stage,
          (url != NULL) ? url : "(null)",
          (method != NULL) ? method : "(null)",
          (void *) version);
  if (version != NULL)
    fprintf(f, " | version_str=\"%s\"", version);
  fprintf(f, "\n");
  fclose(f);
}

static enum MHD_Result
generate_page (void *cls,
               struct MHD_Connection *connection,
               const char *url,
               const char *method,
               const char *version,
               const char *upload_data,
               size_t *upload_data_size, void **req_cls)
{
  struct MHD_Response *response;
  enum MHD_Result ret;
  int fd;
  struct stat buf;

  /* Log entry to function with initial parameters */
  probe_log_entry("entry", url, method, version);

  (void) cls;               /* Unused. Silent compiler warning. */

  /*
   * The cast-to-void for 'version' is the reported violation: it is an
   * executed no-op that evaluates the non-volatile parameter and has no
   * observable effect. We keep the original statement (to preserve logic
   * and warnings suppression) but add a logging probe right after it so
   * the execution of that statement and the state of 'version' can be
   * observed in the produced log.
   */
  (void) version;           /* Unused. Silent compiler warning. */

  /* Record that the void-cast was executed and capture 'version' state */
  probe_log_entry("after_void_cast", url, method, version);

  if (0 != strcmp (url, "/"))
  {
    /* should be file download */
#ifdef MHD_HAVE_LIBMAGIC
    char file_data[MAGIC_HEADER_SIZE];
    ssize_t got;
#endif /* MHD_HAVE_LIBMAGIC */
    const char *mime;

    if ( (0 != strcmp (method, MHD_HTTP_METHOD_GET)) &&
         (0 != strcmp (method, MHD_HTTP_METHOD_HEAD)) )
    {
      probe_log_entry("unexpected_method_file_download", url, method, version);
      return MHD_NO;    /* unexpected method (we're not polite...) */
    }
    fd = -1;
    if ( (NULL == strstr (&url[1], "..")) &&
         ('/' != url[1]) )
    {
      fd = open (&url[1], O_RDONLY);
      if ( (-1 != fd) &&
           ( (0 != fstat (fd, &buf)) ||
             (! S_ISREG (buf.st_mode)) ) )
      {
        (void) close (fd);
        fd = -1;
      }
    }
    if (-1 == fd)
    {
      probe_log_entry("file_not_found", url, method, version);
      return MHD_queue_response (connection,
                                 MHD_HTTP_NOT_FOUND,
                                 file_not_found_response);
    }
#ifdef MHD_HAVE_LIBMAGIC
    /* read beginning of the file to determine mime type  */
    got = read (fd, file_data, sizeof (file_data));
    (void) lseek (fd, 0, SEEK_SET);
    if (0 < got)
      mime = magic_buffer (magic, file_data, (size_t) got);
    else
#endif /* MHD_HAVE_LIBMAGIC */
    mime = NULL;
    {
      /* Set mime-type by file-extension in some cases */
      const char *ldot = strrchr (&url[1], '.');

      if (NULL != ldot)
      {
        if (0 == strcasecmp (ldot,
                             ".html"))
          mime = "text/html";
        if (0 == strcasecmp (ldot,
                             ".css"))
          mime = "text/css";
        if (0 == strcasecmp (ldot,
                             ".css3"))
          mime = "text/css";
        if (0 == strcasecmp (ldot,
                             ".js"))
          mime = "application/javascript";
      }

    }

    if (NULL == (response = MHD_create_response_from_fd ((size_t) buf.st_size,
                                                         fd)))
    {
      /* internal error (i.e. out of memory) */
      (void) close (fd);
      probe_log_entry("out_of_memory_create_response", url, method, version);
      return MHD_NO;
    }

    /* add mime type if we had one */
    if (NULL != mime)
      (void) MHD_add_response_header (response,
                                      MHD_HTTP_HEADER_CONTENT_TYPE,
                                      mime);
    ret = MHD_queue_response (connection,
                              MHD_HTTP_OK,
                              response);
    MHD_destroy_response (response);
    probe_log_entry("file_served", url, method, version);
    return ret;
  }

  if (0 == strcmp (method, MHD_HTTP_METHOD_POST))
  {
    /* upload! */
    struct UploadContext *uc = *req_cls;

    if (NULL == uc)
    {
      if (NULL == (uc = malloc (sizeof (struct UploadContext))))
      {
        probe_log_entry("out_of_memory_upload_alloc", url, method, version);
        return MHD_NO; /* out of memory, close connection */
      }
      memset (uc, 0, sizeof (struct UploadContext));
      uc->fd = -1;
      uc->connection = connection;
      uc->pp = MHD_create_post_processor (connection,
                                          64 * 1024 /* buffer size */,
                                          &process_upload_data, uc);
      if (NULL == uc->pp)
      {
        /* out of memory, close connection */
        free (uc);
        probe_log_entry("out_of_memory_create_post_processor", url, method, version);
        return MHD_NO;
      }
      *req_cls = uc;
      probe_log_entry("upload_context_created", url, method, version);
      return MHD_YES;
    }
    if (0 != *upload_data_size)
    {
      if (NULL == uc->response)
        (void) MHD_post_process (uc->pp,
                                 upload_data,
                                 *upload_data_size);
      *upload_data_size = 0;
      probe_log_entry("upload_in_progress", url, method, version);
      return MHD_YES;
    }
    /* end of upload, finish it! */
    MHD_destroy_post_processor (uc->pp);
    uc->pp = NULL;
    if (-1 != uc->fd)
    {
      close (uc->fd);
      uc->fd = -1;
    }
    if (NULL != uc->response)
    {
      probe_log_entry("upload_forbidden_response", url, method, version);
      return MHD_queue_response (connection,
                                 MHD_HTTP_FORBIDDEN,
                                 uc->response);
    }
    else
    {
      update_directory ();
      probe_log_entry("upload_success_directory_updated", url, method, version);
      return return_directory_response (connection);
    }
  }
  if ( (0 == strcmp (method, MHD_HTTP_METHOD_GET)) ||
       (0 == strcmp (method, MHD_HTTP_METHOD_HEAD)) )
  {
    probe_log_entry("list_directory", url, method, version);
    return return_directory_response (connection);
  }

  /* unexpected request, refuse */
  probe_log_entry("unexpected_request_refuse", url, method, version);
  return MHD_queue_response (connection,
                             MHD_HTTP_FORBIDDEN,
                             request_refused_response);
}
