#include <stdio.h>
#include <stddef.h>

static void
probe_log (const char *event,
           const char *url,
           const char *method,
           const char *version,
           const char *upload_data,
           size_t upload_data_size,
           void *req_cls)
{
  FILE *f = fopen("/tmp/logs/471.txt", "a");
  if (NULL == f)
    return; /* best effort logging, do not affect program flow */

  /* Log event and pointer-level state only. Do not dereference the
     pointers (except for NULL checks) to avoid introducing additional
     undefined behaviour beyond what the original code may already do. */
  (void) fprintf (f, "EVENT=%s\n", event);
  (void) fprintf (f, "  url=%p (%s)\n", (const void *) url,
                  (url == NULL) ? "NULL" : "NON-NULL");
  (void) fprintf (f, "  method=%p (%s)\n", (const void *) method,
                  (method == NULL) ? "NULL" : "NON-NULL");
  (void) fprintf (f, "  version=%p (%s)\n", (const void *) version,
                  (version == NULL) ? "NULL" : "NON-NULL");
  (void) fprintf (f, "  upload_data=%p (%s)\n", (const void *) upload_data,
                  (upload_data == NULL) ? "NULL" : "NON-NULL");
  (void) fprintf (f, "  upload_data_size=%zu\n", upload_data_size);
  (void) fprintf (f, "  req_cls=%p\n", req_cls);
  (void) fprintf (f, "----\n");
  (void) fclose (f);
}

static enum MHD_Result
generate_page (void *cls,
               struct MHD_Connection *connection,
               const char *url,
               const char *method,
               const char *version,
               const char *upload_data,
               size_t *upload_data_size, void **req_cls)
{
  struct MHD_Response *response;
  enum MHD_Result ret;
  int fd;
  struct stat buf;
  (void) cls;               /* Unused. Silent compiler warning. */
  (void) version;           /* Unused. Silent compiler warning. */

  /* Probe: entry */
  probe_log ("enter", url, method, version, upload_data,
             (upload_data_size == NULL) ? 0U : *upload_data_size,
             *req_cls);

  /* Before comparing url to "/" */
  probe_log ("before strcmp(url, \"/\")", url, method, version,
             upload_data, (upload_data_size == NULL) ? 0U : *upload_data_size,
             *req_cls);

  if (0 != strcmp (url, "/"))
  {
    /* should be file download */
#ifdef MHD_HAVE_LIBMAGIC
    char file_data[MAGIC_HEADER_SIZE];
    ssize_t got;
#endif /* MHD_HAVE_LIBMAGIC */
    const char *mime;

    /* Before comparing method to GET/HEAD for file download */
    probe_log ("before strcmp(method, MHD_HTTP_METHOD_GET/HEAD)",
               url, method, version, upload_data,
               (upload_data_size == NULL) ? 0U : *upload_data_size,
               *req_cls);

    if ( (0 != strcmp (method, MHD_HTTP_METHOD_GET)) &&
         (0 != strcmp (method, MHD_HTTP_METHOD_HEAD)) )
      return MHD_NO;    /* unexpected method (we're not polite...) */
    fd = -1;
    if ( (NULL == strstr (&url[1], "..")) &&
         ('/' != url[1]) )
    {
      fd = open (&url[1], O_RDONLY);
      if ( (-1 != fd) &&
           ( (0 != fstat (fd, &buf)) ||
             (! S_ISREG (buf.st_mode)) ) )
      {
        (void) close (fd);
        fd = -1;
      }
    }
    if (-1 == fd)
      return MHD_queue_response (connection,
                                 MHD_HTTP_NOT_FOUND,
                                 file_not_found_response);
#ifdef MHD_HAVE_LIBMAGIC
    /* read beginning of the file to determine mime type  */
    got = read (fd, file_data, sizeof (file_data));
    (void) lseek (fd, 0, SEEK_SET);
    if (0 < got)
      mime = magic_buffer (magic, file_data, (size_t) got);
    else
#endif /* MHD_HAVE_LIBMAGIC */
    mime = NULL;
    {
      /* Set mime-type by file-extension in some cases */
      const char *ldot = strrchr (&url[1], '.');

      if (NULL != ldot)
      {
        if (0 == strcasecmp (ldot,
                             ".html"))
          mime = "text/html";
        if (0 == strcasecmp (ldot,
                             ".css"))
          mime = "text/css";
        if (0 == strcasecmp (ldot,
                             ".css3"))
          mime = "text/css";
        if (0 == strcasecmp (ldot,
                             ".js"))
          mime = "application/javascript";
      }

    }

    if (NULL == (response = MHD_create_response_from_fd ((size_t) buf.st_size,
                                                         fd)))
    {
      /* internal error (i.e. out of memory) */
      (void) close (fd);
      return MHD_NO;
    }

    /* add mime type if we had one */
    if (NULL != mime)
      (void) MHD_add_response_header (response,
                                      MHD_HTTP_HEADER_CONTENT_TYPE,
                                      mime);
    ret = MHD_queue_response (connection,
                              MHD_HTTP_OK,
                              response);
    MHD_destroy_response (response);
    return ret;
  }

  /* Before checking for POST */
  probe_log ("before strcmp(method, MHD_HTTP_METHOD_POST)",
             url, method, version, upload_data,
             (upload_data_size == NULL) ? 0U : *upload_data_size,
             *req_cls);

  if (0 == strcmp (method, MHD_HTTP_METHOD_POST))
  {
    /* upload! */
    struct UploadContext *uc = *req_cls;

    if (NULL == uc)
    {
      if (NULL == (uc = malloc (sizeof (struct UploadContext))))
        return MHD_NO; /* out of memory, close connection */
      memset (uc, 0, sizeof (struct UploadContext));
      uc->fd = -1;
      uc->connection = connection;
      uc->pp = MHD_create_post_processor (connection,
                                          64 * 1024 /* buffer size */,
                                          &process_upload_data, uc);
      if (NULL == uc->pp)
      {
        /* out of memory, close connection */
        free (uc);
        return MHD_NO;
      }
      *req_cls = uc;
      return MHD_YES;
    }
    if (0 != *upload_data_size)
    {
      if (NULL == uc->response)
        (void) MHD_post_process (uc->pp,
                                 upload_data,
                                 *upload_data_size);
      *upload_data_size = 0;
      return MHD_YES;
    }
    /* end of upload, finish it! */
    MHD_destroy_post_processor (uc->pp);
    uc->pp = NULL;
    if (-1 != uc->fd)
    {
      close (uc->fd);
      uc->fd = -1;
    }
    if (NULL != uc->response)
    {
      return MHD_queue_response (connection,
                                 MHD_HTTP_FORBIDDEN,
                                 uc->response);
    }
    else
    {
      update_directory ();
      return return_directory_response (connection);
    }
  }

  /* Before final GET/HEAD check */
  probe_log ("before final GET/HEAD check", url, method, version,
             upload_data, (upload_data_size == NULL) ? 0U : *upload_data_size,
             *req_cls);

  if ( (0 == strcmp (method, MHD_HTTP_METHOD_GET)) ||
       (0 == strcmp (method, MHD_HTTP_METHOD_HEAD)) )
  {
    return return_directory_response (connection);
  }

  /* unexpected request, refuse */
  return MHD_queue_response (connection,
                             MHD_HTTP_FORBIDDEN,
                             request_refused_response);
}
