static void probe_log(const char *stage,
                          const char *url,
                          const char *method,
                          const char *version,
                          size_t *upload_data_size,
                          void *req_cls,
                          int extra_int)
{
  FILE *f = fopen("/tmp/logs/471.txt", "a");
  if (NULL == f)
    return;
  /* Log stage, pointer values and a few safe fields. Do not dereference url. */
  fprintf(f, "[generate_page] %s:\n", stage);
  fprintf(f, "  url ptr=%p\n", (void *) url);
  if (NULL != method)
    fprintf(f, "  method ptr=%p method=\"%s\"\n", (void *) method, method);
  else
    fprintf(f, "  method ptr=NULL\n");
  if (NULL != version)
    fprintf(f, "  version ptr=%p version=\"%s\"\n", (void *) version, version);
  else
    fprintf(f, "  version ptr=NULL\n");
  fprintf(f, "  upload_data_size ptr=%p\n", (void *) upload_data_size);
  fprintf(f, "  req_cls ptr=%p\n", req_cls);
  fprintf(f, "  extra_int=%d\n", extra_int);
  fprintf(f, "----\n");
  fflush(f);
  fclose(f);
}

static enum MHD_Result
generate_page (void *cls,
               struct MHD_Connection *connection,
               const char *url,
               const char *method,
               const char *version,
               const char *upload_data,
               size_t *upload_data_size, void **req_cls)
{
  struct MHD_Response *response;
  enum MHD_Result ret;
  int fd;
  struct stat buf;
  (void) cls;               /* Unused. Silent compiler warning. */
  (void) version;           /* Unused. Silent compiler warning. */

  /* ENTRY log: capture pointer state before any potentially unsafe calls */
  probe_log("ENTRY", url, method, version, upload_data_size, *req_cls, 0);

  /* Preserve original behavior but capture the strcmp result and log it. */
  int cmp_root = strcmp (url, "/");
  probe_log("AFTER strcmp(url, \"/\")", url, method, version, upload_data_size, *req_cls, cmp_root);

  if (0 != cmp_root)
  {
    /* should be file download */
#ifdef MHD_HAVE_LIBMAGIC
    char file_data[MAGIC_HEADER_SIZE];
    ssize_t got;
#endif /* MHD_HAVE_LIBMAGIC */
    const char *mime;

    /* Log before method checks in file-download branch */
    {
      int cmp_method_get = strcmp (method, MHD_HTTP_METHOD_GET);
      int cmp_method_head = strcmp (method, MHD_HTTP_METHOD_HEAD);
      probe_log("FILE-DOWNLOAD method checks", url, method, version, upload_data_size, *req_cls, (cmp_method_get != 0) << 1 | (cmp_method_head != 0));

      if ( (0 != cmp_method_get) &&
           (0 != cmp_method_head) )
        return MHD_NO;    /* unexpected method (we're not polite...) */
    }

    fd = -1;
    if ( (NULL == strstr (&url[1], "..")) &&
         ('/' != url[1]) )
    {
      fd = open (&url[1], O_RDONLY);
      if ( (-1 != fd) &&
           ( (0 != fstat (fd, &buf)) ||
             (! S_ISREG (buf.st_mode)) ) )
      {
        (void) close (fd);
        fd = -1;
      }
    }
    if (-1 == fd)
      return MHD_queue_response (connection,
                                 MHD_HTTP_NOT_FOUND,
                                 file_not_found_response);
#ifdef MHD_HAVE_LIBMAGIC
    /* read beginning of the file to determine mime type  */
    got = read (fd, file_data, sizeof (file_data));
    (void) lseek (fd, 0, SEEK_SET);
    if (0 < got)
      mime = magic_buffer (magic, file_data, (size_t) got);
    else
#endif /* MHD_HAVE_LIBMAGIC */
    mime = NULL;
    {
      /* Set mime-type by file-extension in some cases */
      const char *ldot = strrchr (&url[1], '.');

      if (NULL != ldot)
      {
        if (0 == strcasecmp (ldot,
                             ".html"))
          mime = "text/html";
        if (0 == strcasecmp (ldot,
                             ".css"))
          mime = "text/css";
        if (0 == strcasecmp (ldot,
                             ".css3"))
          mime = "text/css";
        if (0 == strcasecmp (ldot,
                             ".js"))
          mime = "application/javascript";
      }

    }

    if (NULL == (response = MHD_create_response_from_fd ((size_t) buf.st_size,
                                                         fd)))
    {
      /* internal error (i.e. out of memory) */
      (void) close (fd);
      return MHD_NO;
    }

    /* add mime type if we had one */
    if (NULL != mime)
      (void) MHD_add_response_header (response,
                                      MHD_HTTP_HEADER_CONTENT_TYPE,
                                      mime);
    ret = MHD_queue_response (connection,
                              MHD_HTTP_OK,
                              response);
    MHD_destroy_response (response);
    return ret;
  }

  /* POST upload branch: log method comparison and state */
  {
    int cmp_post = strcmp (method, MHD_HTTP_METHOD_POST);
    probe_log("CHECK POST", url, method, version, upload_data_size, *req_cls, cmp_post);
  }

  if (0 == strcmp (method, MHD_HTTP_METHOD_POST))
  {
    /* upload! */
    struct UploadContext *uc = *req_cls;

    if (NULL == uc)
    {
      if (NULL == (uc = malloc (sizeof (struct UploadContext))))
        return MHD_NO; /* out of memory, close connection */
      memset (uc, 0, sizeof (struct UploadContext));
      uc->fd = -1;
      uc->connection = connection;
      uc->pp = MHD_create_post_processor (connection,
                                          64 * 1024 /* buffer size */,
                                          &process_upload_data, uc);
      if (NULL == uc->pp)
      {
        /* out of memory, close connection */
        free (uc);
        return MHD_NO;
      }
      *req_cls = uc;
      return MHD_YES;
    }
    if (0 != *upload_data_size)
    {
      if (NULL == uc->response)
        (void) MHD_post_process (uc->pp,
                                 upload_data,
                                 *upload_data_size);
      *upload_data_size = 0;
      return MHD_YES;
    }
    /* end of upload, finish it! */
    MHD_destroy_post_processor (uc->pp);
    uc->pp = NULL;
    if (-1 != uc->fd)
    {
      close (uc->fd);
      uc->fd = -1;
    }
    if (NULL != uc->response)
    {
      return MHD_queue_response (connection,
                                 MHD_HTTP_FORBIDDEN,
                                 uc->response);
    }
    else
    {
      update_directory ();
      return return_directory_response (connection);
    }
  }
  {
    int cmp_get = strcmp (method, MHD_HTTP_METHOD_GET);
    int cmp_head = strcmp (method, MHD_HTTP_METHOD_HEAD);
    probe_log("CHECK GET/HEAD", url, method, version, upload_data_size, *req_cls, (cmp_get != 0) << 1 | (cmp_head != 0));
  }
  if ( (0 == strcmp (method, MHD_HTTP_METHOD_GET)) ||
       (0 == strcmp (method, MHD_HTTP_METHOD_HEAD)) )
  {
    return return_directory_response (connection);
  }

  /* unexpected request, refuse */
  return MHD_queue_response (connection,
                             MHD_HTTP_FORBIDDEN,
                             request_refused_response);
}
