/* Probe helper: append formatted messages to /tmp/logs/74.txt. */
static void
mhd_log_probe (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/74.txt", "a");
  if (!f)
    {
      /* Try to create directory then open again. If still failing, give up silently. */
      (void) mkdir("/tmp/logs", 0700);
      f = fopen("/tmp/logs/74.txt", "a");
      if (!f)
        return;
    }
  va_list ap;
  va_start (ap, fmt);
  (void) vfprintf (f, fmt, ap);
  va_end (ap);
  (void) fflush (f);
  (void) fclose (f);
}

static ssize_t
get_rq_extended_uname_copy_z (const char *uname_ext, size_t uname_ext_len,
                              char *buf, size_t buf_size)
{
  size_t r;
  size_t w;

  mhd_log_probe ("enter get_rq_extended_uname_copy_z: uname_ext_len=%zu, buf_size=%zu\n",
                 uname_ext_len, buf_size);

  if ((size_t) SSIZE_MAX < uname_ext_len)
    {
      mhd_log_probe ("return -1: too long input string: uname_ext_len=%zu SSIZE_MAX=%zu\n",
                     uname_ext_len, (size_t) SSIZE_MAX);
      return -1; /* Too long input string */
    }

  if (MHD_DAUTH_EXT_PARAM_MIN_LEN > uname_ext_len)
    {
      mhd_log_probe ("return -1: required prefix missing: uname_ext_len=%zu MHD_DAUTH_EXT_PARAM_MIN_LEN=%zu\n",
                     uname_ext_len, (size_t) MHD_DAUTH_EXT_PARAM_MIN_LEN);
      return -1; /* Required prefix is missing */
    }

  if (! MHD_str_equal_caseless_bin_n_ (uname_ext, MHD_DAUTH_EXT_PARAM_PREFIX,
                                       MHD_STATICSTR_LEN_ ( \
                                         MHD_DAUTH_EXT_PARAM_PREFIX)))
    {
      mhd_log_probe ("return -1: prefix mismatch (UTF-8 requirement)\n");
      return -1; /* Only UTF-8 is supported, as it is implied by RFC 7616 */
    }

  r = MHD_STATICSTR_LEN_ (MHD_DAUTH_EXT_PARAM_PREFIX);
  mhd_log_probe ("after prefix, start scanning language tag at r=%zu\n", r);

  /* Skip language tag */
  while (r < uname_ext_len && '\'' != uname_ext[r])
    {
      const char chr = uname_ext[r];
      if ((' ' == chr) || ('\t' == chr) || ('\"' == chr) || (',' == chr) ||
          (';' == chr) )
        {
          mhd_log_probe ("return -1: wrong char in language tag at r=%zu char=0x%02x ('%c')\n",
                         r, (unsigned char) chr, (chr >= ' ' && chr <= '~') ? chr : '?');
          return -1; /* Wrong char in language tag */
        }
      r++;
    }
  if (r >= uname_ext_len)
    {
      mhd_log_probe ("return -1: end of language tag not found: r=%zu uname_ext_len=%zu\n",
                     r, uname_ext_len);
      return -1; /* The end of the language tag was not found */
    }
  r++; /* Advance to the next char */
  mhd_log_probe ("language tag ended, data starts at r=%zu remaining=%zu\n",
                 r, (uname_ext_len > r) ? (uname_ext_len - r) : 0);

  w = MHD_str_pct_decode_strict_n_ (uname_ext + r, uname_ext_len - r,
                                    buf, buf_size);
  mhd_log_probe ("percent-decode returned w=%zu (requested input_len=%zu)\n",
                 w, (uname_ext_len > r) ? (uname_ext_len - r) : 0);

  if ((0 == w) && (0 != uname_ext_len - r))
    {
      mhd_log_probe ("return -1: broken percent encoding: w=0 remaining=%zu\n",
                     uname_ext_len - r);
      return -1; /* Broken percent encoding */
    }
  buf[w] = 0; /* Zero terminate the result */
  mhd_log_probe ("buffer zero-terminated at buf[%zu]=0\n", w);

  /* Log the assertion check that is reported as a no-op (possible dead code).
     We keep the original mhd_assert call to preserve logic; record the values
     to allow reproduction/evidence that the assert is effectively ignored. */
  mhd_log_probe ("about to call mhd_assert(SSIZE_MAX > w): SSIZE_MAX=%lld w=%zu\n",
                 (long long) SSIZE_MAX, w);

  mhd_assert (SSIZE_MAX > w);

  mhd_log_probe ("returning (ssize_t) w=%zu\n", w);
  return (ssize_t) w;
}
