static void
mhd_probe_log (const char *label, size_t uname_ext_len, size_t r,
               size_t w, size_t buf_size)
{
  /* Probe helper: append control-flow and state info to a log file. */
  FILE *f = fopen("/tmp/logs/74.txt", "a");
  if (f == NULL)
    return;
  /* Write basic state. Avoid printing uname_ext itself (may be binary). */
  (void) fprintf (f, "%s: uname_ext_len=%zu, r=%zu, w=%zu, buf_size=%zu\n",
                  label, uname_ext_len, r, w, buf_size);
  /* Log the assertion condition's evaluation to provide evidence
     whether the assert would trigger or not. */
  (void) fprintf (f, "%s: SSIZE_MAX=%lld, condition (SSIZE_MAX > w) = %s\n",
                  label, (long long) SSIZE_MAX,
                  (SSIZE_MAX > w) ? "true" : "false");
  (void) fclose (f);
}

static ssize_t
get_rq_extended_uname_copy_z (const char *uname_ext, size_t uname_ext_len,
                              char *buf, size_t buf_size)
{
  size_t r;
  size_t w;
  /* Entry probe */
  mhd_probe_log ("entry", uname_ext_len, 0u, 0u, buf_size);

  if ((size_t) SSIZE_MAX < uname_ext_len)
  {
    mhd_probe_log ("too_long_input", uname_ext_len, 0u, 0u, buf_size);
    return -1; /* Too long input string */
  }

  if (MHD_DAUTH_EXT_PARAM_MIN_LEN > uname_ext_len)
  {
    mhd_probe_log ("missing_prefix", uname_ext_len, 0u, 0u, buf_size);
    return -1; /* Required prefix is missing */
  }

  if (! MHD_str_equal_caseless_bin_n_ (uname_ext, MHD_DAUTH_EXT_PARAM_PREFIX,
                                       MHD_STATICSTR_LEN_ ( \
                                         MHD_DAUTH_EXT_PARAM_PREFIX)))
  {
    mhd_probe_log ("prefix_mismatch", uname_ext_len, 0u, 0u, buf_size);
    return -1; /* Only UTF-8 is supported, as it is implied by RFC 7616 */
  }

  r = MHD_STATICSTR_LEN_ (MHD_DAUTH_EXT_PARAM_PREFIX);
  /* Skip language tag */
  while (r < uname_ext_len && '\'' != uname_ext[r])
  {
    const char chr = uname_ext[r];
    if ((' ' == chr) || ('\t' == chr) || ('"' == chr) || (',' == chr) ||
        (';' == chr) )
    {
      mhd_probe_log ("bad_char_in_language_tag", uname_ext_len, r, 0u, buf_size);
      return -1; /* Wrong char in language tag */
    }
    r++;
  }
  if (r >= uname_ext_len)
  {
    mhd_probe_log ("language_tag_end_not_found", uname_ext_len, r, 0u, buf_size);
    return -1; /* The end of the language tag was not found */
  }
  r++; /* Advance to the next char */

  w = MHD_str_pct_decode_strict_n_ (uname_ext + r, uname_ext_len - r,
                                    buf, buf_size);
  /* Log decode result before checking for broken percent encoding */
  mhd_probe_log ("after_pct_decode", uname_ext_len, r, w, buf_size);

  if ((0 == w) && (0 != uname_ext_len - r))
  {
    mhd_probe_log ("broken_pct_encoding", uname_ext_len, r, w, buf_size);
    return -1; /* Broken percent encoding */
  }
  buf[w] = 0; /* Zero terminate the result */

  /* Probe right before the assert: captures the exact values used in
     the assertion so a reviewer can see whether the assert is meaningful.
     Note: mhd_assert may be a no-op in production; this log provides
     evidence of the condition's evaluation. */
  mhd_probe_log ("before_assert", uname_ext_len, r, w, buf_size);

  mhd_assert (SSIZE_MAX > w);

  /* Probe after the assert (useful to show control reached here). */
  mhd_probe_log ("after_assert", uname_ext_len, r, w, buf_size);

  return (ssize_t) w;
}
