/* Probe helpers for logging control flow and state to /tmp/logs/74.txt */
#include <stdarg.h>
#include <stddef.h>
#include <limits.h>

/* Minimal opaque FILE declaration and prototypes to avoid adding heavy includes */
typedef struct _IO_FILE FILE;
extern FILE *fopen(const char *path, const char *mode);
extern int vfprintf(FILE *stream, const char *format, va_list ap);
extern int fclose(FILE *stream);

static void
log_probe (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/74.txt", "a");
  if (! f)
    return;
  va_list ap;
  va_start (ap, fmt);
  (void) vfprintf (f, fmt, ap);
  va_end (ap);
  (void) fclose (f);
}

static ssize_t
get_rq_extended_uname_copy_z (const char *uname_ext, size_t uname_ext_len,
                              char *buf, size_t buf_size)
{
  size_t r;
  size_t w;
  log_probe ("enter get_rq_extended_uname_copy_z: uname_ext=%p uname_ext_len=%zu buf=%p buf_size=%zu\n",
             (const void *) uname_ext, uname_ext_len, (const void *) buf, buf_size);

  if ((size_t) SSIZE_MAX < uname_ext_len)
  {
    log_probe ("reject: input length %zu exceeds SSIZE_MAX (%zu)\n",
               uname_ext_len, (size_t) SSIZE_MAX);
    return -1; /* Too long input string */
  }

  if (MHD_DAUTH_EXT_PARAM_MIN_LEN > uname_ext_len)
  {
    log_probe ("reject: input length %zu smaller than required min %zu\n",
               uname_ext_len, (size_t) MHD_DAUTH_EXT_PARAM_MIN_LEN);
    return -1; /* Required prefix is missing */
  }

  {
    int prefix_ok = MHD_str_equal_caseless_bin_n_ (uname_ext, MHD_DAUTH_EXT_PARAM_PREFIX,
                                                   MHD_STATICSTR_LEN_ ( \
                                                     MHD_DAUTH_EXT_PARAM_PREFIX));
    log_probe ("prefix check result: %d\n", prefix_ok);
    if (! prefix_ok)
    {
      log_probe ("reject: prefix mismatch (only UTF-8 supported)\n");
      return -1; /* Only UTF-8 is supported, as it is implied by RFC 7616 */
    }
  }

  r = MHD_STATICSTR_LEN_ (MHD_DAUTH_EXT_PARAM_PREFIX);
  log_probe ("initial r (after prefix) = %zu\n", r);
  /* Skip language tag */
  while (r < uname_ext_len && '\'' != uname_ext[r])
  {
    const char chr = uname_ext[r];
    log_probe ("language-tag char at r=%zu: '%c' (0x%02x)\n", r, (chr >= ' ' && chr <= '~') ? chr : '?', (unsigned char) chr);
    if ((' ' == chr) || ('\t' == chr) || ('\"' == chr) || (',' == chr) ||
        (';' == chr) )
    {
      log_probe ("reject: wrong char in language tag at r=%zu: 0x%02x\n", r, (unsigned char) chr);
      return -1; /* Wrong char in language tag */
    }
    r++;
  }
  if (r >= uname_ext_len)
  {
    log_probe ("reject: end of language tag not found (r=%zu uname_ext_len=%zu)\n", r, uname_ext_len);
    return -1; /* The end of the language tag was not found */
  }
  r++; /* Advance to the next char */
  log_probe ("after skipping quote, r=%zu remaining_len=%zu\n", r, uname_ext_len - r);

  w = MHD_str_pct_decode_strict_n_ (uname_ext + r, uname_ext_len - r,
                                    buf, buf_size);
  log_probe ("percent-decode returned w=%zu (remaining input len=%zu)\n",
             w, uname_ext_len - r);
  if ((0 == w) && (0 != uname_ext_len - r))
  {
    log_probe ("reject: broken percent encoding (w==0 but remaining input len=%zu)\n", uname_ext_len - r);
    return -1; /* Broken percent encoding */
  }
  buf[w] = 0; /* Zero terminate the result */
  log_probe ("decoded uname: '%s' (length=%zu)\n", buf, w);
  mhd_assert (SSIZE_MAX > w);
  log_probe ("exit get_rq_extended_uname_copy_z: returning %zd\n", (ssize_t) w);
  return (ssize_t) w;
}
