#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <stddef.h>
#include <sys/types.h>

/* Probe logging helper: append textual trace to /tmp/logs/18.txt. */
static void probe_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/18.txt", "a");
  if (f == NULL)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fclose(f);
}

static size_t
get_rq_uname (const struct MHD_RqDAuth *params,
              enum MHD_DigestAuthUsernameType uname_type,
              struct MHD_DigestAuthUsernameInfo *uname_info,
              uint8_t *buf,
              size_t buf_size)
{
  size_t buf_used;

  probe_log("get_rq_uname: enter uname_type=%d buf_size=%zu params=%p uname_info=%p buf=%p\n",
            (int) uname_type, buf_size, (const void *) params,
            (const void *) uname_info, (const void *) buf);

  buf_used = 0;
  mhd_assert (get_rq_uname_type (params) == uname_type);
  mhd_assert (MHD_DIGEST_AUTH_UNAME_TYPE_INVALID != uname_type);
  mhd_assert (MHD_DIGEST_AUTH_UNAME_TYPE_MISSING != uname_type);

  /* Initialize uname_info as in original function */
  uname_info->username = NULL;
  uname_info->username_len = 0;
  uname_info->userhash_hex = NULL;
  uname_info->userhash_hex_len = 0;
  uname_info->userhash_bin = NULL;

  probe_log("get_rq_uname: initialized uname_info fields to NULL/0\n");

  if (MHD_DIGEST_AUTH_UNAME_TYPE_STANDARD == uname_type)
  {
    probe_log("get_rq_uname: branch STANDARD\n");
    uname_info->username = (char *) (buf + buf_used);
    uname_info->username_len =
      get_rq_param_unquoted_copy_z (&params->username,
                                    uname_info->username);
    buf_used += uname_info->username_len + 1;
    uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_STANDARD;

    probe_log("get_rq_uname: STANDARD username=%p len=%zu buf_used=%zu\n",
              (const void *) uname_info->username, uname_info->username_len,
              buf_used);
  }
  else if (MHD_DIGEST_AUTH_UNAME_TYPE_USERHASH == uname_type)
  {
    probe_log("get_rq_uname: branch USERHASH\n");
    size_t res;

    uname_info->userhash_hex = (char *) (buf + buf_used);
    uname_info->userhash_hex_len =
      get_rq_param_unquoted_copy_z (&params->username,
                                    uname_info->userhash_hex);
    buf_used += uname_info->userhash_hex_len + 1;
    uname_info->userhash_bin = (uint8_t *) (buf + buf_used);
    res = MHD_hex_to_bin (uname_info->userhash_hex,
                          uname_info->userhash_hex_len,
                          uname_info->userhash_bin);
    if (res != uname_info->userhash_hex_len / 2)
    {
      uname_info->userhash_bin = NULL;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;

      probe_log("get_rq_uname: USERHASH invalid hex conversion res=%zu expected=%zu -> set INVALID\n",
                res, uname_info->userhash_hex_len / 2);
    }
    else
    {
      /* Avoid pointers outside allocated region when the size is zero */
      if (0 == res)
        uname_info->userhash_bin = (uint8_t *) uname_info->username;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_USERHASH;
      buf_used += res;

      probe_log("get_rq_uname: USERHASH success hex=%p hex_len=%zu bin=%p bin_len=%zu buf_used=%zu\n",
                (const void *) uname_info->userhash_hex, uname_info->userhash_hex_len,
                (const void *) uname_info->userhash_bin, res, buf_used);
    }
  }
  else if (MHD_DIGEST_AUTH_UNAME_TYPE_EXTENDED == uname_type)
  {
    probe_log("get_rq_uname: branch EXTENDED\n");
    ssize_t res;
    res = get_rq_extended_uname_copy_z (params->username_ext.value.str,
                                        params->username_ext.value.len,
                                        (char *) (buf + buf_used),
                                        buf_size - buf_used);
    if (0 > res)
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;
    else
    {
      uname_info->username = (char *) (buf + buf_used);
      uname_info->username_len = (size_t) res;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_EXTENDED;
      buf_used += uname_info->username_len + 1;

      probe_log("get_rq_uname: EXTENDED username=%p len=%zu buf_used=%zu\n",
                (const void *) uname_info->username, uname_info->username_len,
                buf_used);
    }
  }
  else
  {
    /* This else branch is the one reported as containing dead code (mhd_assert(0);)
       Keep original behaviour but add probes to document execution. */
    probe_log("get_rq_uname: branch ELSE (invalid/unexpected uname_type=%d) entered\n",
              (int) uname_type);

    /* Original assertion left in place */
    mhd_assert (0);

    /* Log immediately after the assertion to show execution continued. */
    probe_log("get_rq_uname: after mhd_assert(0) (note: mhd_assert may be a no-op in this build)\n");

    uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;

    probe_log("get_rq_uname: ELSE set uname_type=INVALID buf_used=%zu\n",
              buf_used);
  }
  mhd_assert (buf_size >= buf_used);

  probe_log("get_rq_uname: exit buf_used=%zu final_uname_type=%d\n",
            buf_used, (int) uname_info->uname_type);
  return buf_used;
}
