#include <stdio.h>
#include <stddef.h>

/* Probe logging helper: append human-readable state to /tmp/logs/146.txt */
static void mhd_probe_log_state(const char *tag,
                                struct MHD_Connection *c,
                                struct MHD_Response *r,
                                const char *extra)
{
    FILE *f = fopen("/tmp/logs/146.txt", "a");
    if (f == NULL)
        return; /* best-effort logging; do not change program behaviour */

    fprintf(f, "[%s] tag=%s c=%p r=%p keepalive=%d stop_with_error=%d discard_request=%d read_closed=%d rq.http_ver=%d rp.response.flags=0x%08x rp.response.flags_auto=0x%08x upgrade_handler=%p extra=%s\n",
            __func__, tag, (void *)c, (void *)r,
            (int)c->keepalive,
            (int)c->stop_with_error,
            (int)c->discard_request,
            (int)c->read_closed,
            (int)c->rq.http_ver,
            (r ? (unsigned int)r->flags : 0u),
            (r ? (unsigned int)r->flags_auto : 0u),
            (r ? (void *)r->upgrade_handler : NULL),
            (extra ? extra : ""));
    fclose(f);
}

static const char *mhd_probe_reason(enum MHD_ConnKeepAlive v)
{
    if (v == MHD_CONN_MUST_CLOSE) return "MUST_CLOSE";
    if (v == MHD_CONN_USE_KEEPALIVE) return "USE_KEEPALIVE";
    if (v == MHD_CONN_MUST_UPGRADE) return "MUST_UPGRADE";
    return "UNKNOWN";
}

static enum MHD_ConnKeepAlive
keepalive_possible (struct MHD_Connection *connection)
{
  struct MHD_Connection *const c = connection; /**< a short alias */
  struct MHD_Response *const r = c->rp.response;  /**< a short alias */

  /* Log state before the assertion so callers can observe the value(s). */
  mhd_probe_log_state("before_assert", c, r, "assert about to be evaluated");

  mhd_assert (NULL != r);

  /* Log state after the assertion to capture that the assertion did not
     mutate state in this build. This provides evidence that the statement
     is effectively a no-op here (it doesn't change the recorded state). */
  mhd_probe_log_state("after_assert", c, r, "assert executed");

  if (MHD_CONN_MUST_CLOSE == c->keepalive)
  {
    mhd_probe_log_state("return", c, r, "early MUST_CLOSE due to keepalive flag");
    return MHD_CONN_MUST_CLOSE;
  }

#ifdef UPGRADE_SUPPORT
  /* TODO: Move below the next check when MHD stops closing connections
   * when response is queued in first callback */
  if (NULL != r->upgrade_handler)
  {
    /* No "close" token is enforced by 'add_response_header_connection()' */
    mhd_assert (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
    /* Valid HTTP version is enforced by 'MHD_queue_response()' */
    mhd_assert (MHD_IS_HTTP_VER_SUPPORTED (c->rq.http_ver));
    mhd_assert (! c->stop_with_error);
    mhd_probe_log_state("return", c, r, "MUST_UPGRADE due to upgrade_handler");
    return MHD_CONN_MUST_UPGRADE;
  }
#endif /* UPGRADE_SUPPORT */

  mhd_assert ( (! c->stop_with_error) || (c->discard_request));
  mhd_probe_log_state("post_upgrade_check", c, r, "checked stop_with_error/discard_request");

  if ((c->read_closed) || (c->discard_request))
  {
    mhd_probe_log_state("return", c, r, "MUST_CLOSE due to read_closed or discard_request");
    return MHD_CONN_MUST_CLOSE;
  }

  if (0 != (r->flags & MHD_RF_HTTP_1_0_COMPATIBLE_STRICT))
  {
    mhd_probe_log_state("return", c, r, "MUST_CLOSE due to HTTP_1_0_COMPATIBLE_STRICT flag");
    return MHD_CONN_MUST_CLOSE;
  }
  if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE))
  {
    mhd_probe_log_state("return", c, r, "MUST_CLOSE due to RAF_HAS_CONNECTION_CLOSE flag");
    return MHD_CONN_MUST_CLOSE;
  }

  if (! MHD_IS_HTTP_VER_SUPPORTED (c->rq.http_ver))
  {
    mhd_probe_log_state("return", c, r, "MUST_CLOSE due to unsupported HTTP version");
    return MHD_CONN_MUST_CLOSE;
  }

  /* Preserve original single-call semantics: evaluate once and log result. */
  {
    int found = MHD_lookup_header_s_token_ci (c,
                                              MHD_HTTP_HEADER_CONNECTION,
                                              "close");
    mhd_probe_log_state("header_check_connection_close", c, r, found ? "found 'close'" : "no 'close'");
    if (found)
    {
      mhd_probe_log_state("return", c, r, "MUST_CLOSE due to Connection: close header");
      return MHD_CONN_MUST_CLOSE;
    }
  }

  if ((MHD_HTTP_VER_1_0 == connection->rq.http_ver) ||
      (0 != (connection->rp.response->flags & MHD_RF_HTTP_1_0_SERVER)))
  {
    int found = MHD_lookup_header_s_token_ci (connection,
                                              MHD_HTTP_HEADER_CONNECTION,
                                              "Keep-Alive");
    mhd_probe_log_state("http1.0_keepalive_check", c, r, found ? "found Keep-Alive" : "no Keep-Alive");
    if (found)
    {
      mhd_probe_log_state("return", c, r, "USE_KEEPALIVE for HTTP/1.0");
      return MHD_CONN_USE_KEEPALIVE;
    }

    mhd_probe_log_state("return", c, r, "MUST_CLOSE for HTTP/1.0 (no Keep-Alive)");
    return MHD_CONN_MUST_CLOSE;
  }

  if (MHD_IS_HTTP_VER_1_1_COMPAT (c->rq.http_ver))
  {
    mhd_probe_log_state("return", c, r, "USE_KEEPALIVE due to HTTP/1.1 compatibility");
    return MHD_CONN_USE_KEEPALIVE;
  }

  mhd_probe_log_state("return", c, r, "default MUST_CLOSE");
  return MHD_CONN_MUST_CLOSE;
}
