#include <stdio.h>
#include <stdarg.h>

/* Probe helper: append human-readable logs to /tmp/logs/147.txt. */
static void mhd_log_probe (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/147.txt", "a");
  if (NULL == f)
    return;

  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fputc ('\n', f);
  fclose (f);
}

static enum MHD_ConnKeepAlive
keepalive_possible (struct MHD_Connection *connection)
{
  struct MHD_Connection *const c = connection; /**< a short alias */
  struct MHD_Response *const r = c->rp.response;  /**< a short alias */

  mhd_assert (NULL != r);

  /* Log initial relevant state (after the NULL check on r). */
  mhd_log_probe ("keepalive_possible: entry: connection=%p, c=%p, r=%p, c->keepalive=%u, r->flags=0x%08x, r->flags_auto=0x%08x, r->upgrade_handler=%p, rq.http_ver=%d, stop_with_error=%d, discard_request=%d, read_closed=%d",
                 (void *)connection,
                 (void *)c,
                 (void *)r,
                 (unsigned)c->keepalive,
                 (unsigned)r->flags,
                 (unsigned)r->flags_auto,
                 (void *)r->upgrade_handler,
                 (int)c->rq.http_ver,
                 (int)c->stop_with_error,
                 (int)c->discard_request,
                 (int)c->read_closed);

  if (MHD_CONN_MUST_CLOSE == c->keepalive)
  {
    mhd_log_probe ("keepalive_possible: early MUST_CLOSE because c->keepalive == MHD_CONN_MUST_CLOSE");
    return MHD_CONN_MUST_CLOSE;
  }

#ifdef UPGRADE_SUPPORT
  /* TODO: Move below the next check when MHD stops closing connections
   * when response is queued in first callback */
  if (NULL != r->upgrade_handler)
  {
    /* No "close" token is enforced by 'add_response_header_connection()' */
    mhd_assert (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
    /* Valid HTTP version is enforced by 'MHD_queue_response()' */
    mhd_assert (MHD_IS_HTTP_VER_SUPPORTED (c->rq.http_ver));
    mhd_assert (! c->stop_with_error);
    mhd_log_probe ("keepalive_possible: MUST_UPGRADE because r->upgrade_handler != NULL (upgrade_handler=%p)", (void *)r->upgrade_handler);
    return MHD_CONN_MUST_UPGRADE;
  }
#endif /* UPGRADE_SUPPORT */

  mhd_assert ( (! c->stop_with_error) || (c->discard_request));
  if ((c->read_closed) || (c->discard_request))
  {
    mhd_log_probe ("keepalive_possible: MUST_CLOSE because read_closed=%d or discard_request=%d", (int)c->read_closed, (int)c->discard_request);
    return MHD_CONN_MUST_CLOSE;
  }

  if (0 != (r->flags & MHD_RF_HTTP_1_0_COMPATIBLE_STRICT))
  {
    mhd_log_probe ("keepalive_possible: MUST_CLOSE because r->flags & MHD_RF_HTTP_1_0_COMPATIBLE_STRICT (flags=0x%08x)", (unsigned)r->flags);
    return MHD_CONN_MUST_CLOSE;
  }
  if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE))
  {
    mhd_log_probe ("keepalive_possible: MUST_CLOSE because r->flags_auto has CONNECTION_CLOSE (flags_auto=0x%08x)", (unsigned)r->flags_auto);
    return MHD_CONN_MUST_CLOSE;
  }

  if (! MHD_IS_HTTP_VER_SUPPORTED (c->rq.http_ver))
  {
    mhd_log_probe ("keepalive_possible: MUST_CLOSE because !MHD_IS_HTTP_VER_SUPPORTED(rq.http_ver=%d)", (int)c->rq.http_ver);
    return MHD_CONN_MUST_CLOSE;
  }

  if (MHD_lookup_header_s_token_ci (c,
                                    MHD_HTTP_HEADER_CONNECTION,
                                    "close"))
  {
    mhd_log_probe ("keepalive_possible: MUST_CLOSE because Connection: close header present");
    return MHD_CONN_MUST_CLOSE;
  }

  if ((MHD_HTTP_VER_1_0 == connection->rq.http_ver) ||
      (0 != (connection->rp.response->flags & MHD_RF_HTTP_1_0_SERVER)))
  {
    if (MHD_lookup_header_s_token_ci (connection,
                                      MHD_HTTP_HEADER_CONNECTION,
                                      "Keep-Alive"))
    {
      mhd_log_probe ("keepalive_possible: USE_KEEPALIVE for HTTP/1.0 because Keep-Alive header present");
      return MHD_CONN_USE_KEEPALIVE;
    }

    mhd_log_probe ("keepalive_possible: MUST_CLOSE for HTTP/1.0 path (no Keep-Alive header)");
    return MHD_CONN_MUST_CLOSE;
  }

  if (MHD_IS_HTTP_VER_1_1_COMPAT (c->rq.http_ver))
  {
    mhd_log_probe ("keepalive_possible: USE_KEEPALIVE because HTTP/1.1 compatible (rq.http_ver=%d)", (int)c->rq.http_ver);
    return MHD_CONN_USE_KEEPALIVE;
  }

  mhd_log_probe ("keepalive_possible: final MUST_CLOSE default");
  return MHD_CONN_MUST_CLOSE;
}
