#include <stdio.h>
#include <stddef.h>
#include <string.h>

/* Probe function to log cpuset state and loop counter to a file for analysis. */
static void
mhd_tool_log_vx_cpu_state (const char *stage, const void *enb_ptr, size_t enb_size, int ret_val)
{
  FILE *fp = fopen("/tmp/logs/300.txt", "a");
  if (NULL == fp)
  {
    /* If cannot open the log file, do nothing to preserve original behaviour. */
    return;
  }

  fprintf (fp, "Stage: %s, ret=%d, enb_set_size=%zu, enb_set_bytes=0x",
           (stage != NULL) ? stage : "(null)", ret_val, enb_size);
  if ((NULL != enb_ptr) && (enb_size > 0U))
  {
    const unsigned char *p = (const unsigned char *) enb_ptr;
    size_t i;
    for (i = 0U; i < enb_size; ++i)
      fprintf (fp, "%02x", (unsigned) p[i]);
  }
  fprintf (fp, "\n");
  fclose (fp);
}

static int
mhd_tool_get_sys_cpu_count_special_api_ (void)
{
  int ret = -1;
#ifdef HAVE_PSTAT_GETDYNAMIC
  if (0 >= ret)
  {
    /* HP-UX things */
    struct pst_dynamic psd_data;
    memset ((void *) &psd_data, 0, sizeof (psd_data));
    if (1 == pstat_getdynamic (&psd_data, sizeof (psd_data), (size_t) 1, 0))
    {
      if (0 < psd_data.psd_proc_cnt)
        ret = (int) psd_data.psd_proc_cnt;
    }
  }
#endif /* HAVE_PSTAT_GETDYNAMIC */
#ifdef HAVE_VXCPUENABLEDGET
  if (0 >= ret)
  {
    /* VxWorks */
    cpuset_t enb_set;

    /* Capture entry into VxWorks branch */
    mhd_tool_log_vx_cpu_state ("vx_branch_entry", (const void *) &enb_set, sizeof (enb_set), ret);

    enb_set = vxCpuEnabledGet ();

    /* Log state after obtaining enabled CPU set */
    mhd_tool_log_vx_cpu_state ("after_vxCpuEnabledGet", (const void *) &enb_set, sizeof (enb_set), ret);

    /* Count set bits
       Note: original logic preserved: first clause initializes ret, second clause
       checks enb_set, third clause clears lowest set bit. We add logging inside
       the loop body to capture per-iteration state without changing semantics. */
    for (ret = 0; 0 != enb_set; enb_set &= enb_set - 1)
    {
      /* Log state at start of iteration (before increment) */
      mhd_tool_log_vx_cpu_state ("loop_iter_before_increment", (const void *) &enb_set, sizeof (enb_set), ret);

      ++ret;

      /* Log state after increment (enb_set will be modified by third clause when
         this iteration completes) */
      mhd_tool_log_vx_cpu_state ("loop_iter_after_increment", (const void *) &enb_set, sizeof (enb_set), ret);
    }

    /* Log exit from VxWorks branch with final values */
    mhd_tool_log_vx_cpu_state ("vx_branch_exit", (const void *) &enb_set, sizeof (enb_set), ret);
  }
#endif /* HAVE_VXCPUENABLEDGET */
#if defined(_WIN32) && ! defined (__CYGWIN__)
  if (0 >= ret)
  {
    /* Native W32 */
    HMODULE k32hndl;
    k32hndl = LoadLibraryA ("kernel32.dll");
    if (NULL != k32hndl)
    {
      typedef DWORD (WINAPI *GAPC_PTR)(WORD GroupNumber);
      GAPC_PTR ptrGetActiveProcessorCount;
      /* Available on W7 or later */
      ptrGetActiveProcessorCount =
        (GAPC_PTR) (void *) GetProcAddress (k32hndl, "GetActiveProcessorCount");
      if (NULL != ptrGetActiveProcessorCount)
      {
        DWORD res;
        res = ptrGetActiveProcessorCount (ALL_PROCESSOR_GROUPS);
        ret = (int) res;
        if (res != (DWORD) ret)
          ret = -1; /* Overflow */
      }
    }
    if ((0 >= ret) && (NULL != k32hndl))
    {
      typedef void (WINAPI *GNSI_PTR)(SYSTEM_INFO *pSysInfo);
      GNSI_PTR ptrGetNativeSystemInfo;
      /* May give incorrect (low) result on versions from W7 to W11
         when more then 64 CPUs are available */
      ptrGetNativeSystemInfo =
        (GNSI_PTR) (void *) GetProcAddress (k32hndl, "GetNativeSystemInfo");
      if (NULL != ptrGetNativeSystemInfo)
      {
        SYSTEM_INFO sysInfo;

        memset ((void *) &sysInfo, 0, sizeof (sysInfo));
        ptrGetNativeSystemInfo (&sysInfo);
        ret = (int) sysInfo.dwNumberOfProcessors;
        if (sysInfo.dwNumberOfProcessors != (DWORD) ret)
          ret = -1; /* Overflow */
      }
    }
    if (NULL != k32hndl)
      FreeLibrary (k32hndl);
  }
  if (0 >= ret)
  {
    /* May give incorrect (low) result on versions from W7 to W11
       when more then 64 CPUs are available */
    SYSTEM_INFO sysInfo;
    memset ((void *) &sysInfo, 0, sizeof (sysInfo));
    GetSystemInfo (&sysInfo);
    ret = (int) sysInfo.dwNumberOfProcessors;
    if (sysInfo.dwNumberOfProcessors != (DWORD) ret)
      ret = -1; /* Overflow */
  }
#endif /* _WIN32 && ! __CYGWIN__ */
  if (0 >= ret)
    return -1;
  return ret;
}
