#include <stdio.h>
#include <stdarg.h>
#include <unistd.h>

/* Probe helper: append logs to /tmp/logs/170.txt */
static void log_probe(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/170.txt", "a");
    if (f == NULL)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fclose(f);
}

static int
mhd_tool_get_sys_cpu_count_sysconf_ (void)
{
  int ret = -1;
#if defined(HAVE_SYSCONF) && \
  (defined(HAS_DECL__SC_NPROCESSORS_ONLN) || defined(HAS_DECL__SC_NPROC_ONLN))
  long value = -1;
  log_probe("enter mhd_tool_get_sys_cpu_count_sysconf_: initial value=%ld\n", value);
#ifdef HAS_DECL__SC_NPROCESSORS_ONLN
  log_probe("evaluating: if (0 >= value) with value=%ld -> %s\n", value, (0 >= value) ? "true" : "false");
  if (0 >= value)
  {
    long tmp = sysconf (_SC_NPROCESSORS_ONLN);
    log_probe("sysconf(_SC_NPROCESSORS_ONLN) -> %ld\n", tmp);
    value = tmp;
  }
#endif /* HAS_DECL__SC_NPROCESSORS_ONLN */
#ifdef HAS_DECL__SC_NPROC_ONLN
  log_probe("evaluating: if (0 >= value) before _SC_NPROC_ONLN with value=%ld -> %s\n", value, (0 >= value) ? "true" : "false");
  if (0 >= value)
  {
    long tmp = sysconf (_SC_NPROC_ONLN);
    log_probe("sysconf(_SC_NPROC_ONLN) -> %ld\n", tmp);
    value = tmp;
  }
#endif /* HAS_DECL__SC_NPROC_ONLN */
  log_probe("after sysconf attempts: value=%ld\n", value);
  if (0 >= value)
  {
    log_probe("return -1 because value <= 0: %ld\n", value);
    return -1;
  }
  ret = (int) value;
  log_probe("cast value to int: ret=%d (value=%ld)\n", ret, value);
  if ((long) ret != value)
  {
    log_probe("overflow detected: (long)ret=%ld != value=%ld -> return -1\n", (long) ret, value);
    return -1; /* Overflow */
  }
#endif /* HAVE_SYSCONF &&
          (HAS_DECL__SC_NPROCESSORS_ONLN || HAS_DECL__SC_NPROC_ONLN) */
  log_probe("exit mhd_tool_get_sys_cpu_count_sysconf_: ret=%d\n", ret);
  return ret;
}
