#include <stdio.h>
#include <stdarg.h>

/* External functions used by the original implementation. */
extern int mhd_tool_get_sys_cpu_count_special_api_ (void);
extern int mhd_tool_get_sys_cpu_count_sysctl_ (void);
extern int mhd_tool_get_sys_cpu_count_sysconf_ (void);
extern int mhd_tool_get_proc_cpu_count (void);
extern int mhd_tool_get_sys_cpu_count_sysctl_fallback_ (void);
extern int mhd_tool_get_sys_cpu_count_sysconf_fallback_ (void);

/* Simple logging helper that appends records to /tmp/logs/236.txt. */
static void
mhd_tool_log (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/236.txt", "a");
  if (f == NULL)
  {
    return; /* Best-effort logging: do not disturb behavior if logging fails. */
  }

  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  fprintf (f, "\n");
  va_end (ap);

  fclose (f);
}

int
mhd_tool_get_system_cpu_count (void)
{
  int res;

  mhd_tool_log("ENTRY: mhd_tool_get_system_cpu_count");

  /* Try specialised APIs first */
  res = mhd_tool_get_sys_cpu_count_special_api_ ();
  mhd_tool_log("after special_api: res=%d", res);
  if (0 < res)
  {
    mhd_tool_log("returning from special_api with res=%d", res);
    return res;
  }

  /* Try sysctl*(). This is typically a direct interface to
     kernel values. */
  res = mhd_tool_get_sys_cpu_count_sysctl_ ();
  mhd_tool_log("after sysctl: res=%d", res);
  if (0 < res)
  {
    mhd_tool_log("returning from sysctl with res=%d", res);
    return res;
  }

  /* Try sysconf() as the last resort as this is a generic interface
     which can be implemented by parsing system files. */
  res = mhd_tool_get_sys_cpu_count_sysconf_ ();
  mhd_tool_log("after sysconf: res=%d", res);
#if ! defined(__linux__) && ! defined(__GLIBC__)
  if (0 < res)
  {
    mhd_tool_log("non-linux/glibc branch: returning res=%d", res);
    return res;
  }
#else  /* __linux__ || __GLIBC__ */
  if (2 < res)
  {
    mhd_tool_log("linux/glibc branch: res>2 so returning res=%d", res);
    return res;
  }
  if (0 < res)
  {
    mhd_tool_log("linux/glibc branch: 0<res<=2 res=%d; checking proc_cpu_count", res);

    int proc_cpu_count;

    proc_cpu_count = mhd_tool_get_proc_cpu_count ();
    mhd_tool_log("proc_cpu_count=%d", proc_cpu_count);
    if (proc_cpu_count == res)
    {
      /* The detected number of CPUs available for the process
         is equal to the detected number of system CPUs.
         Assume detected number is correct. */
      mhd_tool_log("proc_cpu_count == res (%d); returning res", res);
      return res;
    }
    else
    {
      mhd_tool_log("proc_cpu_count (%d) != res (%d); continuing", proc_cpu_count, res);
    }
  }
#endif /* __linux__ || __GLIBC__  */

  /* Try available fallbacks */

  res = mhd_tool_get_sys_cpu_count_sysctl_fallback_ ();
  mhd_tool_log("after sysctl_fallback: res=%d", res);
  if (0 < res)
  {
    mhd_tool_log("returning from sysctl_fallback: res=%d", res);
    return res;
  }

  res = mhd_tool_get_sys_cpu_count_sysconf_fallback_ ();
  mhd_tool_log("after sysconf_fallback: res=%d", res);
#if ! defined(__linux__) && ! defined(__GLIBC__)
  if (0 < res)
  {
    mhd_tool_log("non-linux/glibc fallback returning res=%d", res);
    return res;
  }
#else  /* __linux__ || __GLIBC__ */
  if (2 < res)
  {
    mhd_tool_log("linux/glibc fallback res>2 returning res=%d", res);
    return res;
  }
#endif /* __linux__ || __GLIBC__  */

  mhd_tool_log("cannot detect, returning -1");
  return -1; /* Cannot detect */
}
