#include <stdio.h>
#include <stdarg.h>
#include <string.h>

/* Probe logging helper: append a formatted line to /tmp/logs/554.txt. */
static void probe_log_fmt(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/554.txt", "a");
  if (NULL == f)
    return; /* best-effort logging, do not affect program flow */

  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fputc('\n', f);
  fclose(f);
}

static enum MHD_Result
not_found_page (const void *cls,
                const char *mime,
                struct Session *session,
                struct MHD_Connection *connection)
{
  enum MHD_Result ret;
  struct MHD_Response *response;
  (void) cls;     /* Unused. Silent compiler warning. */
  (void) session; /* Unused. Silent compiler warning. */

  /* Log entry and input state */
  probe_log_fmt("ENTER not_found_page: cls=%p mime='%s' session=%p connection=%p",
                cls, (mime ? mime : "(null)"), session, connection);

  /* unsupported HTTP method */
  size_t err_len = strlen (NOT_FOUND_ERROR);
  probe_log_fmt("Preparing NOT_FOUND payload: len=%zu", err_len);

  response =
    MHD_create_response_from_buffer_static (err_len,
                                            (const void *) NOT_FOUND_ERROR);
  probe_log_fmt("MHD_create_response_from_buffer_static -> response=%p",
                response);

  if (NULL == response)
  {
    probe_log_fmt("Response creation failed: returning MHD_NO");
    return MHD_NO;
  }

  ret = MHD_queue_response (connection,
                            MHD_HTTP_NOT_FOUND,
                            response);
  probe_log_fmt("MHD_queue_response returned ret=%d", (int) ret);

  int add_res = MHD_add_response_header (response,
                                         MHD_HTTP_HEADER_CONTENT_ENCODING,
                                         mime);
  probe_log_fmt("MHD_add_response_header returned %d (expected MHD_YES=%d)",
                add_res, (int) MHD_YES);

  if (MHD_YES != add_res)
  {
    fprintf (stderr,
             "Failed to set content encoding header!\n");
    probe_log_fmt("Failed to set content encoding header for mime='%s'", (mime ? mime : "(null)"));
  }
  else
  {
    probe_log_fmt("Content encoding header set successfully for mime='%s'", (mime ? mime : "(null)"));
  }

  MHD_destroy_response (response);
  probe_log_fmt("Exiting not_found_page with ret=%d", (int) ret);
  return ret;
}
