static void mhd_probe_log(const char *where,
                              struct MHD_Connection *connection,
                              struct MHD_HTTP_Req_Header *saved_tail,
                              enum _MHD_ParseCookie parse_res,
                              bool allow_partially_correct_cookie,
                              const char *extra)
{
  /* Lightweight probe: append a single-line record to the log file. */
  FILE *f = fopen("/tmp/logs/194.txt", "a");
  if (NULL == f)
    return;
  /* Print basic control-flow/state information useful to reproduce the
     violation scenario. Keep output simple to avoid altering program logic. */
  fprintf(f,
          "%s: conn=%p saved_tail=%p parse_res=%d allow_partial=%d extra=%s\n",
          where,
          (void *) connection,
          (void *) saved_tail,
          (int) parse_res,
          (int) allow_partially_correct_cookie,
          (NULL == extra) ? "" : extra);
  fclose(f);
}

static enum _MHD_ParseCookie
parse_cookie_header (struct MHD_Connection *connection)
{
  const char *hdr;
  size_t hdr_len;
  char *cpy;
  size_t i;
  enum _MHD_ParseCookie parse_res;
  struct MHD_HTTP_Req_Header *const saved_tail =
    connection->rq.headers_received_tail;
  const bool allow_partially_correct_cookie =
    (1 >= connection->daemon->client_discipline);

  /* Log entry and initial state */
  mhd_probe_log("enter", connection, saved_tail, -1,
                allow_partially_correct_cookie, "start");

  if (MHD_NO ==
      MHD_lookup_connection_value_n (connection,
                                     MHD_HEADER_KIND,
                                     MHD_HTTP_HEADER_COOKIE,
                                     MHD_STATICSTR_LEN_ (
                                       MHD_HTTP_HEADER_COOKIE),
                                     &hdr,
                                     &hdr_len)) {
    /* Log missing header and early exit */
    mhd_probe_log("no_cookie_header", connection, saved_tail,
                  MHD_PARSE_COOKIE_OK,
                  allow_partially_correct_cookie,
                  "header not present");
    return MHD_PARSE_COOKIE_OK;
  }
  if (0 == hdr_len) {
    /* Log empty header and early exit */
    mhd_probe_log("empty_cookie_header", connection, saved_tail,
                  MHD_PARSE_COOKIE_OK,
                  allow_partially_correct_cookie,
                  "hdr_len==0");
    return MHD_PARSE_COOKIE_OK;
  }

  cpy = MHD_connection_alloc_memory_ (connection,
                                      hdr_len + 1);
  if (NULL == cpy)
    parse_res = MHD_PARSE_COOKIE_NO_MEMORY;
  else
  {
    memcpy (cpy,
            hdr,
            hdr_len);
    cpy[hdr_len] = '\0';

    i = 0;
    /* Skip all initial whitespaces */
    while (i < hdr_len && (' ' == cpy[i] || '\t' == cpy[i]))
      i++;

    parse_res = parse_cookies_string (cpy + i, hdr_len - i, connection);
  }

  /* Log parse result before handling it */
  mhd_probe_log("after_parse", connection, saved_tail, parse_res,
                allow_partially_correct_cookie, "parsed cookie header");

  switch (parse_res)
  {
  case MHD_PARSE_COOKIE_OK:
    break;
  case MHD_PARSE_COOKIE_OK_LAX:
#ifdef HAVE_MESSAGES
    if (saved_tail != connection->rq.headers_received_tail)
      MHD_DLOG (connection->daemon,
                _ ("The Cookie header has been parsed, but it is not fully "
                   "compliant with the standard.\n"));
#endif /* HAVE_MESSAGES */
    break;
  case MHD_PARSE_COOKIE_MALFORMED:
    if (saved_tail != connection->rq.headers_received_tail)
    {
      if (! allow_partially_correct_cookie)
      {
        /* Remove extracted values from partially broken cookie */
        /* Memory remains allocated until the end of the request processing */
        connection->rq.headers_received_tail = saved_tail;
        saved_tail->next = NULL;
#ifdef HAVE_MESSAGES
        MHD_DLOG (connection->daemon,
                  _ ("The Cookie header has been ignored as it contains "
                     "malformed data.\n"));
#endif /* HAVE_MESSAGES */
      }
#ifdef HAVE_MESSAGES
      else
        MHD_DLOG (connection->daemon,
                  _ ("The Cookie header has been only partially parsed as it "
                     "contains malformed data.\n"));
#endif /* HAVE_MESSAGES */
    }
#ifdef HAVE_MESSAGES
    else
      MHD_DLOG (connection->daemon,
                _ ("The Cookie header has malformed data.\n"));
#endif /* HAVE_MESSAGES */
    break;
  case MHD_PARSE_COOKIE_NO_MEMORY:
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Not enough memory in the connection pool to "
                 "parse client cookies!\n"));
#endif /* HAVE_MESSAGES */
    break;
  default:
    /* Log that the default branch was reached, including the unexpected
       parse_res value and relevant pointers. This provides runtime
       evidence that the assertion site was reached; if mhd_assert is a
       no-op in this build the program will continue after this point.
       Keep the original mhd_assert call to preserve behaviour. */
    mhd_probe_log("default_branch_reached", connection, saved_tail,
                  parse_res, allow_partially_correct_cookie,
                  "about to call mhd_assert(0)");
    mhd_assert (0);
    break;
  }
#ifndef HAVE_MESSAGES
  (void) saved_tail; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */

  /* Final log before return */
  mhd_probe_log("exit", connection, saved_tail, parse_res,
                allow_partially_correct_cookie, "returning");

  return parse_res;
}
