#include <stdio.h>
#include <stdarg.h>

/* Probe logging helper: append human-readable traces to /tmp/logs/194.txt */
static void
mhd_probe_log (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/194.txt", "a");
  if (NULL == f)
    return;

  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fputs("\n", f);
  fclose (f);
}

static enum _MHD_ParseCookie
parse_cookie_header (struct MHD_Connection *connection)
{
  const char *hdr;
  size_t hdr_len;
  char *cpy;
  size_t i;
  enum _MHD_ParseCookie parse_res;
  struct MHD_HTTP_Req_Header *const saved_tail =
    connection->rq.headers_received_tail;
  const bool allow_partially_correct_cookie =
    (1 >= connection->daemon->client_discipline);

  /* Log entry and key connection state */
  mhd_probe_log ("parse_cookie_header: enter connection=%p daemon=%p client_discipline=%d saved_tail=%p",
                 (void *) connection,
                 (void *) connection->daemon,
                 (int) connection->daemon->client_discipline,
                 (void *) saved_tail);

  if (MHD_NO ==
      MHD_lookup_connection_value_n (connection,
                                     MHD_HEADER_KIND,
                                     MHD_HTTP_HEADER_COOKIE,
                                     MHD_STATICSTR_LEN_ (
                                       MHD_HTTP_HEADER_COOKIE),
                                     &hdr,
                                     &hdr_len))
  {
    mhd_probe_log ("parse_cookie_header: no Cookie header found -> return MHD_PARSE_COOKIE_OK");
    return MHD_PARSE_COOKIE_OK;
  }
  if (0 == hdr_len)
  {
    mhd_probe_log ("parse_cookie_header: Cookie header length is 0 -> return MHD_PARSE_COOKIE_OK");
    return MHD_PARSE_COOKIE_OK;
  }

  cpy = MHD_connection_alloc_memory_ (connection,
                                      hdr_len + 1);
  if (NULL == cpy)
  {
    parse_res = MHD_PARSE_COOKIE_NO_MEMORY;
    mhd_probe_log ("parse_cookie_header: allocation failed -> parse_res=MHD_PARSE_COOKIE_NO_MEMORY");
  }
  else
  {
    memcpy (cpy,
            hdr,
            hdr_len);
    cpy[hdr_len] = '\0';

    i = 0;
    /* Skip all initial whitespaces */
    while (i < hdr_len && (' ' == cpy[i] || '\t' == cpy[i]))
      i++;

    parse_res = parse_cookies_string (cpy + i, hdr_len - i, connection);
    mhd_probe_log ("parse_cookie_header: parse_cookies_string returned %d hdr_len=%zu trimmed=%zu",
                   (int) parse_res, hdr_len, i);
  }

  /* Log state just before switching on the result */
  mhd_probe_log ("parse_cookie_header: before switch parse_res=%d saved_tail=%p current_tail=%p allow_partially_correct_cookie=%d",
                 (int) parse_res,
                 (void *) saved_tail,
                 (void *) connection->rq.headers_received_tail,
                 (int) allow_partially_correct_cookie);

  switch (parse_res)
  {
  case MHD_PARSE_COOKIE_OK:
    mhd_probe_log ("parse_cookie_header: branch MHD_PARSE_COOKIE_OK");
    break;
  case MHD_PARSE_COOKIE_OK_LAX:
#ifdef HAVE_MESSAGES
    if (saved_tail != connection->rq.headers_received_tail)
      MHD_DLOG (connection->daemon,
                _ ("The Cookie header has been parsed, but it is not fully "
                   "compliant with the standard.\n"));
#endif /* HAVE_MESSAGES */
    mhd_probe_log ("parse_cookie_header: branch MHD_PARSE_COOKIE_OK_LAX saved_tail_changed=%d",
                   (saved_tail != connection->rq.headers_received_tail));
    break;
  case MHD_PARSE_COOKIE_MALFORMED:
    mhd_probe_log ("parse_cookie_header: branch MHD_PARSE_COOKIE_MALFORMED saved_tail_changed=%d",
                   (saved_tail != connection->rq.headers_received_tail));
    if (saved_tail != connection->rq.headers_received_tail)
    {
      if (! allow_partially_correct_cookie)
      {
        /* Remove extracted values from partially broken cookie */
        /* Memory remains allocated until the end of the request processing */
        connection->rq.headers_received_tail = saved_tail;
        saved_tail->next = NULL;
#ifdef HAVE_MESSAGES
        MHD_DLOG (connection->daemon,
                  _ ("The Cookie header has been ignored as it contains "
                     "malformed data.\n"));
#endif /* HAVE_MESSAGES */
      }
#ifdef HAVE_MESSAGES
      else
        MHD_DLOG (connection->daemon,
                  _ ("The Cookie header has been only partially parsed as it "
                     "contains malformed data.\n"));
#endif /* HAVE_MESSAGES */
    }
#ifdef HAVE_MESSAGES
    else
      MHD_DLOG (connection->daemon,
                _ ("The Cookie header has malformed data.\n"));
#endif /* HAVE_MESSAGES */
    break;
  case MHD_PARSE_COOKIE_NO_MEMORY:
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Not enough memory in the connection pool to "
                 "parse client cookies!\n"));
#endif /* HAVE_MESSAGES */
    mhd_probe_log ("parse_cookie_header: branch MHD_PARSE_COOKIE_NO_MEMORY");
    break;
  default:
    /* Log that we reached the default branch and the value that caused it. */
    mhd_probe_log ("parse_cookie_header: branch DEFAULT reached -> parse_res=%d. About to call mhd_assert(0)",
                   (int) parse_res);
    mhd_assert (0);
    break;
  }
#ifndef HAVE_MESSAGES
  (void) saved_tail; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */

  mhd_probe_log ("parse_cookie_header: exit parse_res=%d", (int) parse_res);

  return parse_res;
}
