static void probe_log_stage(const char *stage,
                                struct MHD_Daemon *daemon,
                                struct MHD_InterimParams_ *params,
                                const enum MHD_Result *retptr)
{
  FILE *f = fopen("/tmp/logs/370.txt", "a");
  if (f != NULL)
    {
      /* Log control point, pointer state and return value if available */
      if (retptr != NULL)
        {
          fprintf(f, "%s: daemon=%p params=%p ret=%d\n",
                  stage,
                  (void *) daemon,
                  (void *) params,
                  (int) *retptr);
        }
      else
        {
          fprintf(f, "%s: daemon=%p params=%p ret=<unset>\n",
                  stage,
                  (void *) daemon,
                  (void *) params);
        }
      fclose(f);
    }
}

static enum MHD_Result
parse_options (struct MHD_Daemon *daemon,
               struct MHD_InterimParams_ *params,
               ...)
{
  va_list ap;
  enum MHD_Result ret;

  /* Log entry into parse_options with current pointers */
  probe_log_stage("ENTER_parse_options", daemon, params, NULL);

  va_start (ap, params);

  /* Log that va_list has been started (we cannot inspect its contents portably) */
  probe_log_stage("VA_STARTED", daemon, params, NULL);

  ret = parse_options_va (daemon,
                          params,
                          ap);

  /* Log return value produced by parse_options_va */
  probe_log_stage("AFTER_parse_options_va", daemon, params, &ret);

  va_end (ap);

  /* Log exit from parse_options */
  probe_log_stage("EXIT_parse_options", daemon, params, &ret);

  return ret;
}
