#include <stdio.h>
#include <stdarg.h>

/* Probe logger: append human-readable logs to /tmp/logs/370.txt. */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/370.txt", "a");
    if (f != NULL) {
        va_list args;
        va_start(args, fmt);
        vfprintf(f, fmt, args);
        fputc('\n', f);
        va_end(args);
        fclose(f);
    }
}

static enum MHD_Result
parse_options (struct MHD_Daemon *daemon,
               struct MHD_InterimParams_ *params,
               ...)
{
  va_list ap;
  enum MHD_Result ret;

  /* Log entry and input state */
  probe_log("parse_options: entry daemon=%p params=%p", (void *)daemon, (void *)params);

  va_start (ap, params);
  probe_log("parse_options: va_start called");

  /* Call the original vararg helper and log the result */
  ret = parse_options_va (daemon,
                          params,
                          ap);
  probe_log("parse_options: parse_options_va returned %d", (int)ret);

  va_end (ap);
  probe_log("parse_options: va_end called; returning %d", (int)ret);

  return ret;
}
