#include <stdio.h>
#include <string.h>
#include <stdint.h>
#include <stddef.h>

/* Probe logging helper: append trace information to /tmp/logs/389.txt */
static void
post_iterator_log (const char *stage,
                   const char *key,
                   uint64_t off,
                   size_t size,
                   const struct Session *session,
                   const char *note)
{
  FILE *fp = fopen("/tmp/logs/389.txt", "a");
  if (fp == NULL)
    return; /* best-effort logging */

  fprintf (fp, "stage=%s key=%s off=%llu size=%zu note=%s\n",
           stage,
           (key != NULL) ? key : "(null)",
           (unsigned long long) off,
           size,
           (note != NULL) ? note : "");
  if (session != NULL)
  {
    /* Print known session fields used by post_iterator. Guard against NULL strings. */
    fprintf (fp, "  session->sid=%s\n",
             (session->sid != NULL) ? session->sid : "(null)");
    fprintf (fp, "  session->value_1=%s\n",
             (session->value_1 != NULL) ? session->value_1 : "(null)");
    fprintf (fp, "  session->value_2=%s\n",
             (session->value_2 != NULL) ? session->value_2 : "(null)");
  }
  fflush (fp);
  fclose (fp);
}

static enum MHD_Result
post_iterator (void *cls,
               enum MHD_ValueKind kind,
               const char *key,
               const char *filename,
               const char *content_type,
               const char *transfer_encoding,
               const char *data, uint64_t off, size_t size)
{
  struct Request *request = cls;
  struct Session *session = request->session;
  (void) kind;              /* Unused. Silent compiler warning. */
  (void) filename;          /* Unused. Silent compiler warning. */
  (void) content_type;      /* Unused. Silent compiler warning. */
  (void) transfer_encoding; /* Unused. Silent compiler warning. */

  /* Log entry with initial state */
  post_iterator_log ("entry", key, off, size, session, "entering post_iterator");

  if (0 == strcmp ("DONE", key))
  {
    fprintf (stdout,
             "Session `%s' submitted `%s', `%s'\n",
             session->sid,
             session->value_1,
             session->value_2);
    post_iterator_log ("done_branch", key, off, size, session, "DONE branch taken");
    return MHD_YES;
  }
  if (0 == strcmp ("v1", key))
  {
    post_iterator_log ("v1_enter", key, off, size, session, "enter v1 branch");
    if (off >= sizeof(session->value_1) - 1)
    {
      post_iterator_log ("v1_discard", key, off, size, session, "off beyond buffer: discarding");
      return MHD_YES; /* Discard extra data */
    }
    if (size + off >= sizeof(session->value_1))
    {
      /* Log the fact we're about to crop size (this is the modification of the parameter) */
      post_iterator_log ("v1_before_crop", key, off, size, session, "about to crop size parameter");
      size = (size_t) (sizeof (session->value_1) - off - 1); /* crop extra data */
      post_iterator_log ("v1_after_crop", key, off, size, session, "size parameter cropped");
    }
    memcpy (&session->value_1[off],
            data,
            size);
    session->value_1[size + off] = '\0';
    post_iterator_log ("v1_finish", key, off, size, session, "finished v1 write");
    return MHD_YES;
  }
  if (0 == strcmp ("v2", key))
  {
    post_iterator_log ("v2_enter", key, off, size, session, "enter v2 branch");
    if (off >= sizeof(session->value_2) - 1)
    {
      post_iterator_log ("v2_discard", key, off, size, session, "off beyond buffer: discarding");
      return MHD_YES; /* Discard extra data */
    }
    if (size + off >= sizeof(session->value_2))
    {
      post_iterator_log ("v2_before_crop", key, off, size, session, "about to crop size parameter");
      size = (size_t) (sizeof (session->value_2) - off - 1); /* crop extra data */
      post_iterator_log ("v2_after_crop", key, off, size, session, "size parameter cropped");
    }
    memcpy (&session->value_2[off],
            data,
            size);
    session->value_2[size + off] = '\0';
    post_iterator_log ("v2_finish", key, off, size, session, "finished v2 write");
    return MHD_YES;
  }
  fprintf (stderr,
           "Unsupported form value `%s'\n",
           key);
  post_iterator_log ("unsupported", key, off, size, session, "unsupported key");
  return MHD_YES;
}
