static char *safe_bounded_strstr (const char *hay, size_t hlen, const char *needle)
{
  size_t nlen;
  size_t i;

  if (NULL == hay || NULL == needle)
    return NULL;
  nlen = strlen (needle);
  if (nlen == 0)
    return (char *) hay;
  if (hlen < nlen)
    return NULL;
  for (i = 0; i <= hlen - nlen; ++i)
  {
    if (0 == memcmp (&hay[i], needle, nlen))
      return (char *) &hay[i];
  }
  return NULL;
}

