#include <stdio.h>
#include <stdarg.h>
#include <stddef.h>

/* Helper logging function that appends to /tmp/logs/97.txt. */
static void postproc_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/97.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  fprintf(f, "\n");
  va_end(ap);
  fclose(f);
}

static inline ptrdiff_t ptr_offset(const char *base, const char *p)
{
  if (NULL == p || NULL == base)
    return (ptrdiff_t)(-1);
  return (ptrdiff_t)(p - base);
}

static enum MHD_Result
post_process_urlencoded (struct MHD_PostProcessor *pp,
                         const char *post_data,
                         size_t post_data_len)
{
  char *kbuf = (char *) &pp[1];
  size_t poff;
  const char *start_key = NULL;
  const char *end_key = NULL;
  const char *start_value = NULL;
  const char *end_value = NULL;
  const char *last_escape = NULL;

  /* Log entry with initial state */
  postproc_log("ENTER post_process_urlencoded: pp=%p state=%d post_data=%p post_data_len=%zu", (void *)pp, (int)pp->state, (const void *)post_data, post_data_len);

  mhd_assert (PP_Callback != pp->state);

  poff = 0;
  while ( ( (poff < post_data_len) ||
            (pp->state == PP_Callback) ) &&
          (pp->state != PP_Error) )
  {
    /* Log loop iteration start and key/value pointer offsets */
    postproc_log("LOOP ITER start: poff=%zu state=%d start_key_off=%td end_key_off=%td start_value_off=%td end_value_off=%td last_escape_off=%td",
                 poff,
                 (int)pp->state,
                 ptr_offset(post_data, start_key),
                 ptr_offset(post_data, end_key),
                 ptr_offset(post_data, start_value),
                 ptr_offset(post_data, end_value),
                 ptr_offset(post_data, last_escape));

    switch (pp->state)
    {
    case PP_Error:
      /* clearly impossible as per while loop invariant */
      postproc_log("CASE PP_Error entered: UNEXPECTED - pp=%p state=%d poff=%zu", (void *)pp, (int)pp->state, poff);
      abort ();
      break; /* Unreachable */
    case PP_Init:
      postproc_log("CASE PP_Init: poff=%zu", poff);
      /* initial phase */
      mhd_assert (NULL == start_key);
      mhd_assert (NULL == end_key);
      mhd_assert (NULL == start_value);
      mhd_assert (NULL == end_value);
      switch (post_data[poff])
      {
      case '=':
        /* Case: (no key)'=' */
        /* Empty key with value */
        postproc_log("TRANSITION: PP_Init -> PP_Error due to '=' at poff=%zu", poff);
        pp->state = PP_Error;
        continue;
      case '&':
        /* Case: (no key)'&' */
        /* Empty key without value */
        poff++;
        postproc_log("PP_Init: found '&' at poff=%zu, advance to %zu", poff - 1, poff);
        continue;
      case '\n':
      case '\r':
        /* Case: (no key)'\n' or (no key)'\r' */
        pp->state = PP_Done;
        poff++;
        postproc_log("PP_Init: newline -> PP_Done poff=%zu", poff);
        break;
      default:
        /* normal character, key start, advance! */
        pp->state = PP_ProcessKey;
        start_key = &post_data[poff];
        pp->must_ikvi = true;
        poff++;
        postproc_log("PP_Init: normal char -> PP_ProcessKey start_key_off=%td poff=%zu", ptr_offset(post_data, start_key), poff);
        continue;
      }
      break; /* end PP_Init */
    case PP_ProcessKey:
      postproc_log("CASE PP_ProcessKey: poff=%zu start_key_off=%td end_key_off=%td", poff, ptr_offset(post_data, start_key), ptr_offset(post_data, end_key));
      /* key phase */
      mhd_assert (NULL == start_value);
      mhd_assert (NULL == end_value);
      mhd_assert (NULL != start_key || 0 == poff);
      mhd_assert (0 != poff || NULL == start_key);
      mhd_assert (NULL == end_key);
      switch (post_data[poff])
      {
      case '=':
        /* Case: 'key=' */
        if (0 != poff)
          end_key = &post_data[poff];
        poff++;
        pp->state = PP_ProcessValue;
        postproc_log("PP_ProcessKey: found '=' end_key_off=%td -> PP_ProcessValue poff=%zu", ptr_offset(post_data, end_key), poff);
        break;
      case '&':
        /* Case: 'key&' */
        if (0 != poff)
          end_key = &post_data[poff];
        poff++;
        pp->state = PP_Callback;
        postproc_log("PP_ProcessKey: found '&' end_key_off=%td -> PP_Callback poff=%zu", ptr_offset(post_data, end_key), poff);
        break;
      case '\n':
      case '\r':
        /* Case: 'key\n' or 'key\r' */
        if (0 != poff)
          end_key = &post_data[poff];
        /* No advance here, 'PP_Done' will be selected by next 'PP_Init' phase */
        pp->state = PP_Callback;
        postproc_log("PP_ProcessKey: newline end_key_off=%td -> PP_Callback", ptr_offset(post_data, end_key));
        break;
      default:
        /* normal character, advance! */
        if (0 == poff)
          start_key = post_data;
        poff++;
        postproc_log("PP_ProcessKey: normal char advance poff=%zu start_key_off=%td", poff, ptr_offset(post_data, start_key));
        break;
      }
      mhd_assert (NULL == end_key || NULL != start_key);
      break; /* end PP_ProcessKey */
    case PP_ProcessValue:
      postproc_log("CASE PP_ProcessValue: poff=%zu start_value_off=%td end_value_off=%td last_escape_off=%td", poff, ptr_offset(post_data, start_value), ptr_offset(post_data, end_value), ptr_offset(post_data, last_escape));
      if (NULL == start_value)
        start_value = &post_data[poff];
      switch (post_data[poff])
      {
      case '=':
        /* case 'key==' */
        postproc_log("PP_ProcessValue: found extra '=' at poff=%zu -> PP_Error", poff);
        pp->state = PP_Error;
        continue;
      case '&':
        /* case 'value&' */
        end_value = &post_data[poff];
        poff++;
        if (pp->must_ikvi ||
            (start_value != end_value) )
        {
          pp->state = PP_Callback;
          postproc_log("PP_ProcessValue: end_value_off=%td -> PP_Callback poff=%zu", ptr_offset(post_data, end_value), poff);
        }
        else
        {
          pp->buffer_pos = 0;
          pp->value_offset = 0;
          pp->state = PP_Init;
          start_value = NULL;
          end_value = NULL;
          postproc_log("PP_ProcessValue: empty value and not must_ikvi -> reset to PP_Init");
        }
        continue;
      case '\n':
      case '\r':
        /* Case: 'value\n' or 'value\r' */
        end_value = &post_data[poff];
        if (pp->must_ikvi ||
            (start_value != end_value) )
          pp->state = PP_Callback; /* No poff advance here to set PP_Done in the next iteration */
        else
        {
          poff++;
          pp->state = PP_Done;
        }
        postproc_log("PP_ProcessValue: newline end_value_off=%td -> state=%d poff=%zu", ptr_offset(post_data, end_value), (int)pp->state, poff);
        break;
      case '%':
        last_escape = &post_data[poff];
        poff++;
        postproc_log("PP_ProcessValue: escape start at off=%td poff=%zu", ptr_offset(post_data, last_escape), poff);
        break;
      case '0':
      case '1':
      case '2':
      case '3':
      case '4':
      case '5':
      case '6':
      case '7':
      case '8':
      case '9':
        /* character, may be part of escaping */
        poff++;
        postproc_log("PP_ProcessValue: digit advance poff=%zu", poff);
        continue;
      default:
        /* normal character, no more escaping! */
        last_escape = NULL;
        poff++;
        postproc_log("PP_ProcessValue: normal char advance poff=%zu", poff);
        continue;
      }
      break; /* end PP_ProcessValue */
    case PP_Done:
      postproc_log("CASE PP_Done: poff=%zu", poff);
      switch (post_data[poff])
      {
      case '\n':
      case '\r':
        poff++;
        postproc_log("PP_Done: skip newline poff=%zu", poff);
        continue;
      }
      /* unexpected data at the end, fail! */
      postproc_log("PP_Done: unexpected data at poff=%zu -> PP_Error", poff);
      pp->state = PP_Error;
      break;
    case PP_Callback:
      postproc_log("CASE PP_Callback: poff=%zu start_key_off=%td end_key_off=%td start_value_off=%td end_value_off=%td", poff, ptr_offset(post_data, start_key), ptr_offset(post_data, end_key), ptr_offset(post_data, start_value), ptr_offset(post_data, end_value));
      mhd_assert ((NULL != end_key) || (NULL == start_key));
      if (1)
      {
        const size_t key_len = (size_t) (end_key - start_key);
        mhd_assert (end_key >= start_key);
        if (0 != key_len)
        {
          if ( (pp->buffer_pos + key_len >= pp->buffer_size) ||
               (pp->buffer_pos + key_len < pp->buffer_pos) )
          {
            /* key too long, cannot parse! */
            postproc_log("PP_Callback: key too long key_len=%zu buffer_pos=%zu buffer_size=%zu -> PP_Error", key_len, pp->buffer_pos, pp->buffer_size);
            pp->state = PP_Error;
            continue;
          }
          /* compute key, if we have not already */
          memcpy (&kbuf[pp->buffer_pos],
                  start_key,
                  key_len);
          pp->buffer_pos += key_len;
          start_key = NULL;
          end_key = NULL;
          pp->must_unescape_key = true;
          postproc_log("PP_Callback: copied key key_len=%zu new_buffer_pos=%zu", key_len, pp->buffer_pos);
        }
      }
#ifdef _DEBUG
      else
        mhd_assert (0 != pp->buffer_pos);
#endif /* _DEBUG */
      if (pp->must_unescape_key)
      {
        kbuf[pp->buffer_pos] = '\0'; /* 0-terminate key */
        MHD_unescape_plus (kbuf);
        MHD_http_unescape (kbuf);
        pp->must_unescape_key = false;
        postproc_log("PP_Callback: unescaped key and cleared must_unescape_key");
      }
      postproc_log("PP_Callback: calling process_value start_value_off=%td end_value_off=%td last_escape_off=%td",
                   ptr_offset(post_data, start_value), ptr_offset(post_data, end_value), ptr_offset(post_data, last_escape));
      process_value (pp,
                     start_value,
                     end_value,
                     NULL);
      if (PP_Error == pp->state)
      {
        postproc_log("PP_Callback: process_value set PP_Error -> continue");
        continue;
      }
      pp->value_offset = 0;
      start_value = NULL;
      end_value = NULL;
      pp->buffer_pos = 0;
      pp->state = PP_Init;
      postproc_log("PP_Callback: processed value -> reset to PP_Init");
      break;
    case PP_NextBoundary:
    case PP_ProcessEntryHeaders:
    case PP_PerformCheckMultipart:
    case PP_ProcessValueToBoundary:
    case PP_PerformCleanup:
    case PP_Nested_Init:
    case PP_Nested_PerformMarking:
    case PP_Nested_ProcessEntryHeaders:
    case PP_Nested_ProcessValueToBoundary:
    case PP_Nested_PerformCleanup:
    default:
      MHD_PANIC (_ ("internal error.\n")); /* should never happen! */
    }
    mhd_assert ((end_key == NULL) || (start_key != NULL));
    mhd_assert ((end_value == NULL) || (start_value != NULL));
  }

  postproc_log("LOOP EXIT: poff=%zu final_state=%d", (size_t)0, (int)pp->state);

  mhd_assert (PP_Callback != pp->state);

  if (PP_Error == pp->state)
  {
    /* State in error, returning failure */
    postproc_log("EXIT -> MHD_NO due to PP_Error state=%d", (int)pp->state);
    return MHD_NO;
  }

  /* save remaining data for next iteration */
  if (NULL != start_key)
  {
    size_t key_len;
    mhd_assert ((PP_ProcessKey == pp->state) || (NULL != end_key));
    if (NULL == end_key)
      end_key = &post_data[poff];
    mhd_assert (end_key >= start_key);
    key_len = (size_t) (end_key - start_key);
    mhd_assert (0 != key_len); /* it must be always non-zero here */
    if (pp->buffer_pos + key_len >= pp->buffer_size)
    {
      pp->state = PP_Error;
      postproc_log("EXIT: remaining key too large key_len=%zu buffer_pos=%zu buffer_size=%zu -> PP_Error", key_len, pp->buffer_pos, pp->buffer_size);
      return MHD_NO;
    }
    memcpy (&kbuf[pp->buffer_pos],
            start_key,
            key_len);
    pp->buffer_pos += key_len;
    pp->must_unescape_key = true;
    start_key = NULL;
    end_key = NULL;
    postproc_log("Saved remaining key key_len=%zu new_buffer_pos=%zu", key_len, pp->buffer_pos);
  }
  if ( (NULL != start_value) &&
       (PP_ProcessValue == pp->state) )
  {
    /* compute key, if we have not already */
    if (pp->must_unescape_key)
    {
      kbuf[pp->buffer_pos] = '\0'; /* 0-terminate key */
      MHD_unescape_plus (kbuf);
      MHD_http_unescape (kbuf);
      pp->must_unescape_key = false;
      postproc_log("Saved remaining value: unescaped key before processing remaining value");
    }
    if (NULL == end_value)
      end_value = &post_data[poff];
    if ( (NULL != last_escape) &&
         (2 < (end_value - last_escape)) )
      last_escape = NULL;
    postproc_log("Calling process_value for remaining data start_value_off=%td end_value_off=%td last_escape_off=%td", ptr_offset(post_data, start_value), ptr_offset(post_data, end_value), ptr_offset(post_data, last_escape));
    process_value (pp,
                   start_value,
                   end_value,
                   last_escape);
    pp->must_ikvi = false;
  }
  if (PP_Error == pp->state)
  {
    /* State in error, returning failure */
    postproc_log("EXIT -> MHD_NO due to PP_Error (post final processing) state=%d", (int)pp->state);
    return MHD_NO;
  }
  postproc_log("EXIT -> MHD_YES final state=%d", (int)pp->state);
  return MHD_YES;
}
