#include <stdio.h>
#include <string.h>
#include <stddef.h>
#include <stdlib.h>

/* Probe logging helper: write reproducible information about control flow and
   values relevant to the suspected MISRA 8.13 violation. */
static void
probe_log_state(const struct MHD_PostProcessor *pp,
                const char *where,
                const char *start_value,
                const char *end_value,
                const char *last_escape,
                const char *kbuf_snapshot,
                size_t key_len)
{
  FILE *f = fopen("/tmp/logs/99.txt", "a");
  if (NULL == f)
    return;

  /* Basic processor state */
  fprintf(f, "-- probe: %s\n", where);
  fprintf(f, "  pp->state=%d pp->must_ikvi=%d pp->must_unescape_key=%d\n",
          (int) pp->state,
          (int) pp->must_ikvi,
          (int) pp->must_unescape_key);
  fprintf(f, "  pp->buffer_pos=%zu pp->buffer_size=%zu pp->value_offset=%zu\n",
          (size_t) pp->buffer_pos,
          (size_t) pp->buffer_size,
          (size_t) pp->value_offset);

  /* Pointer info for value range */
  fprintf(f, "  start_value=%p end_value=%p last_escape=%p\n",
          (const void *) start_value,
          (const void *) end_value,
          (const void *) last_escape);

  if (NULL != start_value && NULL != end_value && end_value >= start_value)
  {
    size_t vlen = (size_t) (end_value - start_value);
    /* limit snapshot to a reasonable length */
    size_t printlen = (vlen > 256u) ? 256u : vlen;
    fprintf(f, "  value_len=%zu (showing up to %zu bytes):\n", vlen, printlen);
    fprintf(f, "  value=\"");
    /* print raw bytes safely */
    fwrite(start_value, 1, printlen, f);
    if (printlen < vlen)
      fprintf(f, "...\n");
    else
      fprintf(f, "\n");
    fprintf(f, "  (end of value)\n");
  }
  else
  {
    fprintf(f, "  value_len=(null or invalid)\n");
  }

  /* Key buffer snapshot if available */
  if (NULL != kbuf_snapshot)
  {
    fprintf(f, "  key_len=%zu key=\"%.*s\"\n",
            key_len,
            (int) ((key_len > 256u) ? 256u : key_len),
            kbuf_snapshot);
  }
  else
  {
    fprintf(f, "  key_len=(none)\n");
  }

  fprintf(f, "-- end probe (%s)\n\n", where);
  fclose(f);
}

static enum MHD_Result
post_process_urlencoded (struct MHD_PostProcessor *pp,
                         const char *post_data,
                         size_t post_data_len)
{
  char *kbuf = (char *) &pp[1];
  size_t poff;
  const char *start_key = NULL;
  const char *end_key = NULL;
  const char *start_value = NULL;
  const char *end_value = NULL;
  const char *last_escape = NULL;

  mhd_assert (PP_Callback != pp->state);

  /* Entry probe */
  probe_log_state(pp, "entry", start_value, end_value, last_escape, NULL, 0);

  poff = 0;
  while ( ( (poff < post_data_len) ||
            (pp->state == PP_Callback) ) &&
          (pp->state != PP_Error) )
  {
    switch (pp->state)
    {
    case PP_Error:
      /* clearly impossible as per while loop invariant */
      abort ();
      break; /* Unreachable */
    case PP_Init:
      /* initial phase */
      mhd_assert (NULL == start_key);
      mhd_assert (NULL == end_key);
      mhd_assert (NULL == start_value);
      mhd_assert (NULL == end_value);
      switch (post_data[poff])
      {
      case '=':
        /* Case: (no key)'=' */
        /* Empty key with value */
        pp->state = PP_Error;
        continue;
      case '&':
        /* Case: (no key)'&' */
        /* Empty key without value */
        poff++;
        continue;
      case '\n':
      case '\r':
        /* Case: (no key)'\n' or (no key)'\r' */
        pp->state = PP_Done;
        poff++;
        break;
      default:
        /* normal character, key start, advance! */
        pp->state = PP_ProcessKey;
        start_key = &post_data[poff];
        pp->must_ikvi = true;
        poff++;
        continue;
      }
      break; /* end PP_Init */
    case PP_ProcessKey:
      /* key phase */
      mhd_assert (NULL == start_value);
      mhd_assert (NULL == end_value);
      mhd_assert (NULL != start_key || 0 == poff);
      mhd_assert (0 != poff || NULL == start_key);
      mhd_assert (NULL == end_key);
      switch (post_data[poff])
      {
      case '=':
        /* Case: 'key=' */
        if (0 != poff)
          end_key = &post_data[poff];
        poff++;
        pp->state = PP_ProcessValue;
        break;
      case '&':
        /* Case: 'key&' */
        if (0 != poff)
          end_key = &post_data[poff];
        poff++;
        pp->state = PP_Callback;
        break;
      case '\n':
      case '\r':
        /* Case: 'key\n' or 'key\r' */
        if (0 != poff)
          end_key = &post_data[poff];
        /* No advance here, 'PP_Done' will be selected by next 'PP_Init' phase */
        pp->state = PP_Callback;
        break;
      default:
        /* normal character, advance! */
        if (0 == poff)
          start_key = post_data;
        poff++;
        break;
      }
      mhd_assert (NULL == end_key || NULL != start_key);
      break; /* end PP_ProcessKey */
    case PP_ProcessValue:
      if (NULL == start_value)
        start_value = &post_data[poff];
      switch (post_data[poff])
      {
      case '=':
        /* case 'key==' */
        pp->state = PP_Error;
        continue;
      case '&':
        /* case 'value&' */
        end_value = &post_data[poff];
        poff++;
        if (pp->must_ikvi ||
            (start_value != end_value) )
        {
          pp->state = PP_Callback;
        }
        else
        {
          pp->buffer_pos = 0;
          pp->value_offset = 0;
          pp->state = PP_Init;
          start_value = NULL;
          end_value = NULL;
        }
        continue;
      case '\n':
      case '\r':
        /* Case: 'value\n' or 'value\r' */
        end_value = &post_data[poff];
        if (pp->must_ikvi ||
            (start_value != end_value) )
          pp->state = PP_Callback; /* No poff advance here to set PP_Done in the next iteration */
        else
        {
          poff++;
          pp->state = PP_Done;
        }
        break;
      case '%':
        last_escape = &post_data[poff];
        poff++;
        break;
      case '0':
      case '1':
      case '2':
      case '3':
      case '4':
      case '5':
      case '6':
      case '7':
      case '8':
      case '9':
        /* character, may be part of escaping */
        poff++;
        continue;
      default:
        /* normal character, no more escaping! */
        last_escape = NULL;
        poff++;
        continue;
      }
      break; /* end PP_ProcessValue */
    case PP_Done:
      switch (post_data[poff])
      {
      case '\n':
      case '\r':
        poff++;
        continue;
      }
      /* unexpected data at the end, fail! */
      pp->state = PP_Error;
      break;
    case PP_Callback:
      mhd_assert ((NULL != end_key) || (NULL == start_key));
      if (1)
      {
        const size_t key_len = (size_t) (end_key - start_key);
        mhd_assert (end_key >= start_key);
        if (0 != key_len)
        {
          if ( (pp->buffer_pos + key_len >= pp->buffer_size) ||
               (pp->buffer_pos + key_len < pp->buffer_pos) )
          {
            /* key too long, cannot parse! */
            pp->state = PP_Error;
            continue;
          }
          /* compute key, if we have not already */
          memcpy (&kbuf[pp->buffer_pos],
                  start_key,
                  key_len);
          pp->buffer_pos += key_len;
          start_key = NULL;
          end_key = NULL;
          pp->must_unescape_key = true;
        }
      }
#ifdef _DEBUG
      else
        mhd_assert (0 != pp->buffer_pos);
#endif /* _DEBUG */
      if (pp->must_unescape_key)
      {
        kbuf[pp->buffer_pos] = '\0'; /* 0-terminate key */
        MHD_unescape_plus (kbuf);
        MHD_http_unescape (kbuf);
        pp->must_unescape_key = false;
      }

      /* Probe right before calling process_value from PP_Callback. This logs
         the pointers (start_value/end_value), the current key buffer and
         related flags so other agents can reproduce the situation where
         const-qualified pointers are passed into process_value. */
      probe_log_state(pp, "PP_Callback_before_process_value",
                      start_value, end_value, last_escape, kbuf, (size_t) strlen(kbuf));

      process_value (pp,
                     start_value,
                     end_value,
                     NULL);

      /* Probe just after process_value call to capture any state changes */
      probe_log_state(pp, "PP_Callback_after_process_value",
                      start_value, end_value, last_escape, kbuf, (size_t) strlen(kbuf));

      if (PP_Error == pp->state)
        continue;
      pp->value_offset = 0;
      start_value = NULL;
      end_value = NULL;
      pp->buffer_pos = 0;
      pp->state = PP_Init;
      break;
    case PP_NextBoundary:
    case PP_ProcessEntryHeaders:
    case PP_PerformCheckMultipart:
    case PP_ProcessValueToBoundary:
    case PP_PerformCleanup:
    case PP_Nested_Init:
    case PP_Nested_PerformMarking:
    case PP_Nested_ProcessEntryHeaders:
    case PP_Nested_ProcessValueToBoundary:
    case PP_Nested_PerformCleanup:
    default:
      MHD_PANIC (_ ("internal error.\n")); /* should never happen! */
    }
    mhd_assert ((end_key == NULL) || (start_key != NULL));
    mhd_assert ((end_value == NULL) || (start_value != NULL));
  }

  mhd_assert (PP_Callback != pp->state);

  if (PP_Error == pp->state)
  {
    /* State in error, returning failure */
    return MHD_NO;
  }

  /* save remaining data for next iteration */
  if (NULL != start_key)
  {
    size_t key_len;
    mhd_assert ((PP_ProcessKey == pp->state) || (NULL != end_key));
    if (NULL == end_key)
      end_key = &post_data[poff];
    mhd_assert (end_key >= start_key);
    key_len = (size_t) (end_key - start_key);
    mhd_assert (0 != key_len); /* it must be always non-zero here */
    if (pp->buffer_pos + key_len >= pp->buffer_size)
    {
      pp->state = PP_Error;
      return MHD_NO;
    }
    memcpy (&kbuf[pp->buffer_pos],
            start_key,
            key_len);
    pp->buffer_pos += key_len;
    pp->must_unescape_key = true;
    start_key = NULL;
    end_key = NULL;
  }
  if ( (NULL != start_value) &&
       (PP_ProcessValue == pp->state) )
  {
    /* compute key, if we have not already */
    if (pp->must_unescape_key)
    {
      kbuf[pp->buffer_pos] = '\0'; /* 0-terminate key */
      MHD_unescape_plus (kbuf);
      MHD_http_unescape (kbuf);
      pp->must_unescape_key = false;
    }
    if (NULL == end_value)
      end_value = &post_data[poff];
    if ( (NULL != last_escape) &&
         (2 < (end_value - last_escape)) )
      last_escape = NULL;

    /* Probe before final process_value call (end of input handling). This
       logs the pointers and the last_escape pointer so that one can verify
       that const-qualified pointers are passed to process_value. */
    probe_log_state(pp, "final_before_process_value",
                    start_value, end_value, last_escape, kbuf, (size_t) strlen(kbuf));

    process_value (pp,
                   start_value,
                   end_value,
                   last_escape);

    /* Probe after final process_value */
    probe_log_state(pp, "final_after_process_value",
                    start_value, end_value, last_escape, kbuf, (size_t) strlen(kbuf));

    pp->must_ikvi = false;
  }
  if (PP_Error == pp->state)
  {
    /* State in error, returning failure */
    return MHD_NO;
  }

  /* Exit probe */
  probe_log_state(pp, "exit", start_value, end_value, last_escape, NULL, 0);

  return MHD_YES;
}
