static void log_probe(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/423.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fputc('\n', f);
  fclose(f);
}

static enum MHD_Result
process_upload_data (void *cls,
                     enum MHD_ValueKind kind,
                     const char *key,
                     const char *filename,
                     const char *content_type,
                     const char *transfer_encoding,
                     const char *data,
                     uint64_t off,
                     size_t size)
{
  struct UploadContext *uc = cls;
  size_t i;
  int res;
  (void) kind;              /* Unused. Silent compiler warning. */
  (void) content_type;      /* Unused. Silent compiler warning. */
  (void) transfer_encoding; /* Unused. Silent compiler warning. */
  (void) off;               /* Unused. Silent compiler warning. */

  /* Log entry into function and key/filename pointers */
  log_probe("process_upload_data: entry; key=%p filename=%p size=%zu",
            (void *) key, (void *) filename, size);

  if (0 == strcmp (key, "category")) {
    log_probe("process_upload_data: appending to category; data_size=%zu", size);
    return do_append (&uc->category, data, size);
  }
  if (0 == strcmp (key, "language")) {
    log_probe("process_upload_data: appending to language; data_size=%zu", size);
    return do_append (&uc->language, data, size);
  }
  if (0 != strcmp (key, "upload"))
  {
    fprintf (stderr,
             "Ignoring unexpected form value `%s'\n",
             key);
    log_probe("process_upload_data: unexpected form key='%s' (ignored)", key);
    return MHD_YES;   /* ignore */
  }
  if (NULL == filename)
  {
    fprintf (stderr, "No filename, aborting upload.\n");
    log_probe("process_upload_data: filename is NULL; aborting");
    return MHD_NO;   /* no filename, error */
  }

  /* Log that filename is non-NULL; do not attempt to read beyond what original
     code would read. We will call the same string functions as original code
     and capture their results so we don't duplicate reads. */
  log_probe("process_upload_data: filename pointer non-NULL=%p", (void *) filename);

  if ( (NULL == uc->category) ||
       (NULL == uc->language) )
  {
    fprintf (stderr,
             "Missing form data for upload `%s'\n",
             filename);
    log_probe("process_upload_data: missing form data; filename='%s'", filename);
    uc->response = request_refused_response;
    return MHD_NO;
  }
  if (-1 == uc->fd)
  {
    char fn[PATH_MAX];

    /* To avoid multiple traversals of the potentially non-terminated
       filename (which is the original source of the rule violation),
       call the checked functions once and record results, then log them. */
    const char *found_parent = strstr (filename, "..");
    const char *found_slash = strchr (filename, '/');
    const char *found_backslash = strchr (filename, '\\');

    log_probe("process_upload_data: filename checks: filename=%p found_parent=%p found_slash=%p found_backslash=%p",
              (void *) filename,
              (void *) found_parent,
              (void *) found_slash,
              (void *) found_backslash);

    if ( (NULL != found_parent) ||
         (NULL != found_slash) ||
         (NULL != found_backslash) )
    {
      log_probe("process_upload_data: refused upload due to suspicious filename; filename=%p", (void *) filename);
      uc->response = request_refused_response;
      return MHD_NO;
    }
    /* create directories -- if they don't exist already */
#ifdef WINDOWS
    (void) mkdir (uc->language);
#else
    (void) mkdir (uc->language, S_IRWXU);
#endif
    snprintf (fn, sizeof (fn),
              "%s/%s",
              uc->language,
              uc->category);
#ifdef WINDOWS
    (void) mkdir (fn);
#else
    (void) mkdir (fn, S_IRWXU);
#endif
    /* open file */
    res = snprintf (fn, sizeof (fn),
                    "%s/%s/%s",
                    uc->language,
                    uc->category,
                    filename);
    log_probe("process_upload_data: snprintf returned %d for path generation", res);
    if ((0 >= res) || (sizeof (fn) <= (size_t) res))
    {
      log_probe("process_upload_data: path too long or snprintf error; res=%d", res);
      uc->response = request_refused_response;
      return MHD_NO;
    }
    for (i = 0; i < (size_t) res; i++)
      if (! isprint ((unsigned char) fn[i]))
        fn[i] = '_';
    uc->fd = open (fn,
                   O_CREAT | O_EXCL
#ifdef O_LARGEFILE
                   | O_LARGEFILE
#endif
                   | O_WRONLY,
                   S_IRUSR | S_IWUSR);
    if (-1 == uc->fd)
    {
      fprintf (stderr,
               "Error opening file `%s' for upload: %s\n",
               fn,
               strerror (errno));
      log_probe("process_upload_data: open failed for '%s' errno=%d (%s)", fn, errno, strerror(errno));
      uc->response = request_refused_response;
      return MHD_NO;
    }
    uc->filename = strdup (fn);
    log_probe("process_upload_data: created and opened file '%s' fd=%d", fn, uc->fd);
  }
  if ( (0 != size) &&
#if ! defined(_WIN32) || defined(__CYGWIN__)
       (size != (size_t) write (uc->fd, data, size))
#else  /* Native W32 */
       (size != (size_t) write (uc->fd, data, (unsigned int) size))
#endif /* Native W32 */
       )
  {
    /* write failed; likely: disk full */
    fprintf (stderr,
             "Error writing to file `%s': %s\n",
             uc->filename,
             strerror (errno));
    log_probe("process_upload_data: write failed to '%s' fd=%d errno=%d (%s)",
              uc->filename ? uc->filename : "(null)", uc->fd, errno, strerror(errno));
    uc->response = internal_error_response;
    (void) close (uc->fd);
    uc->fd = -1;
    if (NULL != uc->filename)
    {
      unlink (uc->filename);
      free (uc->filename);
      uc->filename = NULL;
    }
    return MHD_NO;
  }
  log_probe("process_upload_data: write ok (or no data to write); returning MHD_YES");
  return MHD_YES;
}
