  buf[p++] = ',';
  buf[p++] = ' ';
  /* 'opaque="xxxx", ' */
  if (NULL != opaque)
  {
    memcpy (buf + p, prefix_opaque,
            MHD_STATICSTR_LEN_ (prefix_opaque));
    p += MHD_STATICSTR_LEN_ (prefix_opaque);
    /* Log values that would be checked by the assertion */
    mhd_probe_log ("ASSERT_CHECK", "before opaque quote: buf_size=%zu p=%zu opaque_len=%zu need=%zu",
                   buf_size, p, opaque_len, opaque_len * 2);
    mhd_assert ((buf_size - p) >= (opaque_len * 2));
    p += MHD_str_quote (opaque, opaque_len, buf + p, buf_size - p);
    mhd_probe_log ("STATE", "after opaque quote: p=%zu", p);
    buf[p++] = '\"';
    buf[p++] = ',';
    buf[p++] = ' ';
  }
  /* 'domain="xxxx", ' */
  if (NULL != domain)
  {
    memcpy (buf + p, prefix_domain,
            MHD_STATICSTR_LEN_ (prefix_domain));
    p += MHD_STATICSTR_LEN_ (prefix_domain);
    mhd_probe_log ("ASSERT_CHECK", "before domain quote: buf_size=%zu p=%zu domain_len=%zu need=%zu",
                   buf_size, p, domain_len, domain_len * 2);
    mhd_assert ((buf_size - p) >= (domain_len * 2));
    p += MHD_str_quote (domain, domain_len, buf + p, buf_size - p);
    mhd_probe_log ("STATE", "after domain quote: p=%zu", p);
    buf[p++] = '\"';
    buf[p++] = ',';
    buf[p++] = ' ';
  }
  /* 'charset=UTF-8' */
  if (MHD_NO != prefer_utf8)
  {
    memcpy (buf + p, str_charset,
            MHD_STATICSTR_LEN_ (str_charset));
    p += MHD_STATICSTR_LEN_ (str_charset);
    buf[p++] = ',';
    buf[p++] = ' ';
    mhd_probe_log ("STATE", "added charset, p=%zu", p);
  }
  /* 'userhash=true' */
  if (MHD_NO != userhash_support)
  {
    memcpy (buf + p, str_userhash,
            MHD_STATICSTR_LEN_ (str_userhash));
    p += MHD_STATICSTR_LEN_ (str_userhash);
    buf[p++] = ',';
    buf[p++] = ' ';
    mhd_probe_log ("STATE", "added userhash, p=%zu", p);
  }
  /* 'stale=true' */
  if (MHD_NO != signal_stale)
  {
    memcpy (buf + p, str_stale,
            MHD_STATICSTR_LEN_ (str_stale));
    p += MHD_STATICSTR_LEN_ (str_stale);
    buf[p++] = ',';
    buf[p++] = ' ';
    mhd_probe_log ("STATE", "added stale, p=%zu", p);
  }
  mhd_probe_log ("ASSERT_CHECK", "final assert: buf_size=%zu p=%zu", buf_size, p);
  mhd_assert (buf_size >= p);
