static enum MHD_Result
send_bad_request (struct MHD_Connection *con)
{
  struct MHD_Response *res;
  enum MHD_Result ret;
  size_t page_len = strlen (BAD_REQUEST_PAGE);

  /* Probe: log entry and initial state */
  {
    FILE *f = fopen("/tmp/logs/498.txt", "a");
    if (f != NULL)
    {
      fprintf(f, "send_bad_request: enter\n");
      fprintf(f, "  con=%p\n", (void *) con);
      fprintf(f, "  BAD_REQUEST_PAGE_len=%zu\n", page_len);
      fclose(f);
    }
  }

  res =
    MHD_create_response_from_buffer_static (page_len,
                                            (const void *) BAD_REQUEST_PAGE);

  /* Probe: log result of create_response */
  {
    FILE *f = fopen("/tmp/logs/498.txt", "a");
    if (f != NULL)
    {
      fprintf(f, "send_bad_request: after create_response\n");
      fprintf(f, "  res=%p\n", (void *) res);
      fclose(f);
    }
  }

  ret = MHD_queue_response (con, MHD_HTTP_BAD_REQUEST, res);

  /* Probe: log queue result */
  {
    FILE *f = fopen("/tmp/logs/498.txt", "a");
    if (f != NULL)
    {
      fprintf(f, "send_bad_request: after queue_response\n");
      fprintf(f, "  ret=%d\n", (int) ret);
      fclose(f);
    }
  }

  MHD_destroy_response (res);

  /* Probe: log exit and final state */
  {
    FILE *f = fopen("/tmp/logs/498.txt", "a");
    if (f != NULL)
    {
      fprintf(f, "send_bad_request: exit\n");
      fprintf(f, "  returning ret=%d\n", (int) ret);
      fclose(f);
    }
  }

  return ret;
}
