#include <stdio.h>
#include <string.h>

/* Probe function: append control-flow and state information to a log file.
   Kept minimal to avoid changing original behavior. */
static void send_bad_request_log_probe(const char *stage,
                                      struct MHD_Connection *con,
                                      struct MHD_Response *res,
                                      int ret_code)
{
  FILE *f = fopen("/tmp/logs/498.txt", "a");
  if (f == NULL)
  {
    return; /* Do not alter original control flow if logging fails. */
  }

  fprintf(f, "send_bad_request: stage=%s\n", stage);
  fprintf(f, "  con=%p\n", (void *) con);
  fprintf(f, "  res=%p\n", (void *) res);
  fprintf(f, "  ret=%d\n", ret_code);
  fflush(f);
  fclose(f);
}

static enum MHD_Result
send_bad_request (struct MHD_Connection *con)
{
  struct MHD_Response *res;
  enum MHD_Result ret;

  /* Log entry with no response allocated yet. */
  send_bad_request_log_probe("entry", con, NULL, -1);

  res =
    MHD_create_response_from_buffer_static (strlen (BAD_REQUEST_PAGE),
                                            (const void *) BAD_REQUEST_PAGE);

  /* Log after creating the response object. */
  send_bad_request_log_probe("after_create_response", con, res, -1);

  ret = MHD_queue_response (con, MHD_HTTP_BAD_REQUEST, res);

  /* Log after queuing the response (captures the return/result). */
  send_bad_request_log_probe("after_queue_response", con, res, (int) ret);

  MHD_destroy_response (res);

  /* Log after destroying the response to capture final state. */
  send_bad_request_log_probe("after_destroy_response", con, NULL, (int) ret);

  return ret;
}
