#include <stdio.h>
#include <string.h>

/* Probe helper: appends a short trace to /tmp/logs/498.txt.
   Keeps signature limited to types visible in the original context.
*/
static void
probe_log (const char *stage, struct MHD_Connection *con,
           const struct MHD_Response *res, enum MHD_Result ret)
{
  FILE *f = fopen("/tmp/logs/498.txt", "a");
  if (f == NULL)
  {
    return; /* If logging fails, preserve original behaviour. */
  }

  /* Log key control points and lightweight state: pointers and return code.
     BAD_REQUEST_PAGE length is included for context.
  */
  (void) fprintf (f, "stage=%s con=%p res=%p ret=%d page_len=%zu\n",
                  stage, (void *) con, (const void *) res,
                  (int) ret, strlen (BAD_REQUEST_PAGE));
  (void) fclose (f);
}

static enum MHD_Result
send_bad_request (struct MHD_Connection *con)
{
  struct MHD_Response *res;
  enum MHD_Result ret;

  /* Log entry into the function. */
  probe_log ("enter", con, NULL, (enum MHD_Result) -1);

  res =
    MHD_create_response_from_buffer_static (strlen (BAD_REQUEST_PAGE),
                                            (const void *) BAD_REQUEST_PAGE);

  /* Log after creating the response object. */
  probe_log ("after_create", con, res, (enum MHD_Result) -1);

  ret = MHD_queue_response (con, MHD_HTTP_BAD_REQUEST, res);

  /* Log the result of queuing the response. */
  probe_log ("after_queue", con, res, ret);

  MHD_destroy_response (res);

  /* Log after destroying the response. */
  probe_log ("after_destroy", con, NULL, ret);

  return ret;
}
