    return MHD_ERR_NOTCONN_;
  }

  items_to_send = r_iov->cnt - r_iov->sent;
  probe_log("Computed items_to_send=%zu (cnt=%zu - sent=%zu)",
            items_to_send, r_iov->cnt, r_iov->sent);
#ifdef _MHD_IOV_MAX
  if (_MHD_IOV_MAX < items_to_send)
  {
    mhd_assert (0 < _MHD_IOV_MAX);
    if (0 == _MHD_IOV_MAX)
    {
      probe_log("_MHD_IOV_MAX == 0: returning not connected (unexpected)");
      return MHD_ERR_NOTCONN_; /* Should never happen */
    }
    probe_log("_MHD_IOV_MAX (%d) < items_to_send (%zu): capping and disabling push_data",
              (int) _MHD_IOV_MAX, items_to_send);
    items_to_send = _MHD_IOV_MAX;
    /* Log the modification of the parameter push_data (this is the violation scenario) */
    probe_log("Before modifying push_data in _MHD_IOV_MAX block, push_data=%d", (int) push_data);
    push_data = false; /* Incomplete response */
    probe_log("After modifying push_data in _MHD_IOV_MAX block, push_data=%d", (int) push_data);
  }
#endif /* _MHD_IOV_MAX */
#ifdef HAVE_SENDMSG
  memset (&msg, 0, sizeof(struct msghdr));
  msg.msg_iov = r_iov->iov + r_iov->sent;
  msg.msg_iovlen = items_to_send;

