        r_iov->iov[r_iov->sent].iov_len -= (MHD_iov_size_) track_sent;
      }
    }
  }

  probe_log("EXIT send_iov_nontls: res=%zd, r_iov->sent=%zu, push_data=%d",
            res, r_iov->sent, (int) push_data);

  return res;
}

