static void
mhd_log_probe(const char *where,
              const struct MHD_Connection *connection,
              const struct MHD_iovec_track_ *const r_iov,
              size_t items_to_send,
              bool push_data,
              ssize_t res,
              int err)
{
  FILE *f = fopen("/tmp/logs/642.txt", "a");
  if (!f)
    return;

  /* Print control-path/state information useful to reproduce the scenario. */
  fprintf(f, "[probe] %s: conn=%p\n", where, (void *) connection);
  if (connection)
  {
    /* socket_fd and state are implementation-defined types; print via casts */
    fprintf(f, "  socket_fd=%lld state=%d\n",
            (long long) connection->socket_fd,
            (int) connection->state);
    if (connection->daemon)
    {
      fprintf(f, "  daemon=%p options=0x%llx\n",
              (void *) connection->daemon,
              (unsigned long long) connection->daemon->options);
#ifdef MHD_USE_TLS
      fprintf(f, "  assert_condition: (0 == (options & MHD_USE_TLS)) => %d\n",
              (0 == (connection->daemon->options & MHD_USE_TLS)) ? 1 : 0);
#else
      fprintf(f, "  MHD_USE_TLS not available at probe compile time\n");
#endif
    }
  }
  if (r_iov)
  {
    fprintf(f, "  r_iov=%p cnt=%zu sent=%zu items_to_send=%zu push_data=%d res=%zd err=%d\n",
            (void *) r_iov, (size_t) r_iov->cnt, (size_t) r_iov->sent,
            items_to_send, push_data ? 1 : 0, res, err);
  }
  fprintf(f, "[probe] %s: end\n", where);
  fclose(f);
}

static ssize_t
send_iov_nontls (struct MHD_Connection *connection,
                 struct MHD_iovec_track_ *const r_iov,
                 bool push_data)
{
  ssize_t res;
  size_t items_to_send;
#ifdef HAVE_SENDMSG
  struct msghdr msg;
#elif defined(MHD_WINSOCK_SOCKETS)
  DWORD bytes_sent;
  DWORD cnt_w;
#endif /* MHD_WINSOCK_SOCKETS */

  /* Log state before the assertion line so that callers can observe the
   * evaluated data even when the real mhd_assert expands to ((void)0). */
  mhd_log_probe("enter", connection, r_iov, 0, push_data, -1, 0);

  mhd_assert (0 == (connection->daemon->options & MHD_USE_TLS));

  /* Log immediately after the assert to demonstrate whether the function
   * continued execution (useful to show the assertion had no runtime effect
   * in configurations where mhd_assert is a no-op). */
  mhd_log_probe("after_mhd_assert", connection, r_iov, 0, push_data, -1, 0);

  if ( (MHD_INVALID_SOCKET == connection->socket_fd) ||
       (MHD_CONNECTION_CLOSED == connection->state) )
  {
    mhd_log_probe("early_return_notconn", connection, r_iov, 0, push_data, -1, 0);
    return MHD_ERR_NOTCONN_;
  }

  items_to_send = r_iov->cnt - r_iov->sent;
#ifdef _MHD_IOV_MAX
  if (_MHD_IOV_MAX < items_to_send)
  {
    mhd_assert (0 < _MHD_IOV_MAX);
    if (0 == _MHD_IOV_MAX)
    {
      mhd_log_probe("iov_max_zero_return", connection, r_iov, items_to_send, push_data, -1, 0);
      return MHD_ERR_NOTCONN_; /* Should never happen */
    }
    items_to_send = _MHD_IOV_MAX;
    push_data = false; /* Incomplete response */
    mhd_log_probe("iov_max_truncated", connection, r_iov, items_to_send, push_data, -1, 0);
  }
#endif /* _MHD_IOV_MAX */
#ifdef HAVE_SENDMSG
  memset (&msg, 0, sizeof(struct msghdr));
  msg.msg_iov = r_iov->iov + r_iov->sent;
  msg.msg_iovlen = items_to_send;

  pre_send_setopt (connection, true, push_data);
#ifdef MHD_USE_MSG_MORE
  res = sendmsg (connection->socket_fd, &msg,
                 MSG_NOSIGNAL_OR_ZERO | (push_data ? 0 : MSG_MORE));
#else  /* ! MHD_USE_MSG_MORE */
  res = sendmsg (connection->socket_fd, &msg, MSG_NOSIGNAL_OR_ZERO);
#endif /* ! MHD_USE_MSG_MORE */
#elif defined(HAVE_WRITEV)
  pre_send_setopt (connection, true, push_data);
  res = writev (connection->socket_fd, r_iov->iov + r_iov->sent,
                items_to_send);
#elif defined(MHD_WINSOCK_SOCKETS)
#ifdef _WIN64
  if (items_to_send > UINT32_MAX)
  {
    cnt_w = UINT32_MAX;
    push_data = false; /* Incomplete response */
  }
  else
    cnt_w = (DWORD) items_to_send;
#else  /* ! _WIN64 */
  cnt_w = (DWORD) items_to_send;
#endif /* ! _WIN64 */
  pre_send_setopt (connection, true, push_data);
  if (0 == WSASend (connection->socket_fd,
                    (LPWSABUF) (r_iov->iov + r_iov->sent),
                    cnt_w,
                    &bytes_sent, 0, NULL, NULL))
    res = (ssize_t) bytes_sent;
  else
    res = -1;
#else /* !HAVE_SENDMSG && !HAVE_WRITEV && !MHD_WINSOCK_SOCKETS */
#error No vector-send function available
#endif

  if (0 > res)
  {
    const int err = MHD_socket_get_error_ ();

    mhd_log_probe("send_error", connection, r_iov, items_to_send, push_data, res, err);

    if (MHD_SCKT_ERR_IS_EAGAIN_ (err))
    {
#ifdef EPOLL_SUPPORT
      /* EAGAIN --- no longer write-ready */
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
      mhd_log_probe("err_eagain", connection, r_iov, items_to_send, push_data, res, err);
      return MHD_ERR_AGAIN_;
    }
    if (MHD_SCKT_ERR_IS_EINTR_ (err))
    {
      mhd_log_probe("err_eintr", connection, r_iov, items_to_send, push_data, res, err);
      return MHD_ERR_AGAIN_;
    }
    if (MHD_SCKT_ERR_IS_REMOTE_DISCNN_ (err))
    {
      mhd_log_probe("err_remote_disc", connection, r_iov, items_to_send, push_data, res, err);
      return MHD_ERR_CONNRESET_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EPIPE_))
    {
      mhd_log_probe("err_epipe", connection, r_iov, items_to_send, push_data, res, err);
      return MHD_ERR_PIPE_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EOPNOTSUPP_))
    {
      mhd_log_probe("err_eopnotsupp", connection, r_iov, items_to_send, push_data, res, err);
      return MHD_ERR_OPNOTSUPP_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_ENOTCONN_))
    {
      mhd_log_probe("err_enotconn", connection, r_iov, items_to_send, push_data, res, err);
      return MHD_ERR_NOTCONN_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EINVAL_))
    {
      mhd_log_probe("err_einval", connection, r_iov, items_to_send, push_data, res, err);
      return MHD_ERR_INVAL_;
    }
    if (MHD_SCKT_ERR_IS_LOW_RESOURCES_ (err))
    {
      mhd_log_probe("err_low_resources", connection, r_iov, items_to_send, push_data, res, err);
      return MHD_ERR_NOMEM_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EBADF_))
    {
      mhd_log_probe("err_ebadf", connection, r_iov, items_to_send, push_data, res, err);
      return MHD_ERR_BADF_;
    }
    /* Treat any other error as a hard error. */
    mhd_log_probe("err_other", connection, r_iov, items_to_send, push_data, res, err);
    return MHD_ERR_NOTCONN_;
  }

  /* Some data has been sent */
  if (1)
  {
    size_t track_sent = (size_t) res;
    /* Adjust the internal tracking information for the iovec to
     * take this last send into account. */
    while ((0 != track_sent) && (r_iov->iov[r_iov->sent].iov_len <= track_sent))
    {
      track_sent -= r_iov->iov[r_iov->sent].iov_len;
      r_iov->sent++; /* The iov element has been completely sent */
      mhd_assert ((r_iov->cnt > r_iov->sent) || (0 == track_sent));
    }

    if (r_iov->cnt == r_iov->sent)
      post_send_setopt (connection, true, push_data);
    else
    {
#ifdef EPOLL_SUPPORT
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
      if (0 != track_sent)
      {
        mhd_assert (r_iov->cnt > r_iov->sent);
        /* The last iov element has been partially sent */
        r_iov->iov[r_iov->sent].iov_base =
          (void *) ((uint8_t *) r_iov->iov[r_iov->sent].iov_base + track_sent);
        r_iov->iov[r_iov->sent].iov_len -= (MHD_iov_size_) track_sent;
      }
    }
  }

  mhd_log_probe("exit_success", connection, r_iov, items_to_send, push_data, res, 0);

  return res;
}
