#include <stdio.h>
#include <stdarg.h>
#include <stdbool.h>
#include <stdint.h>
#include <string.h>

/* Probe logging helper. Appends human-readable logs to /tmp/logs/125.txt. */
static void mhd_probe_log(const void *con_ptr, const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/125.txt", "a");
    if (f == NULL)
        return;

    va_list ap;
    va_start(ap, fmt);
    fprintf(f, "[thread_main_handle_connection] con=%p: ", con_ptr);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

static MHD_THRD_RTRN_TYPE_  MHD_THRD_CALL_SPEC_
thread_main_handle_connection (void *data)
{
  struct MHD_Connection *con = data;
  struct MHD_Daemon *daemon = con->daemon;
  int num_ready;
  fd_set rs;
  fd_set ws;
  fd_set es;
  MHD_socket maxsock;
#ifdef WINDOWS
#ifdef HAVE_POLL
  unsigned int extra_slot;
#endif /* HAVE_POLL */
#define EXTRA_SLOTS 1
#else  /* !WINDOWS */
#define EXTRA_SLOTS 0
#endif /* !WINDOWS */
#ifdef HAVE_POLL
  struct pollfd p[1 + EXTRA_SLOTS];
#endif
#undef EXTRA_SLOTS
#ifdef HAVE_POLL
  const bool use_poll = MHD_D_IS_USING_POLL_ (daemon);
#else  /* ! HAVE_POLL */
  const bool use_poll = 0;
#endif /* ! HAVE_POLL */
  bool was_suspended = false;
  MHD_thread_handle_ID_set_current_thread_ID_ (&(con->tid));

  /* Log entry for this thread handling a connection. */
  mhd_probe_log(con, "ENTER: daemon=%p state=%d socket_fd=%d event_loop_info=0x%08x tls_read_ready=%d connection_timeout_ms=%d",
                (void*)daemon,
                (int) con->state,
                (int) con->socket_fd,
                (unsigned) con->event_loop_info,
                (int) con->tls_read_ready,
                (int) con->connection_timeout_ms);

  while ( (! daemon->shutdown) &&
          (MHD_CONNECTION_CLOSED != con->state) )
  {
    bool use_zero_timeout;
#ifdef UPGRADE_SUPPORT
    struct MHD_UpgradeResponseHandle *const urh = con->urh;
#else  /* ! UPGRADE_SUPPORT */
    static const void *const urh = NULL;
#endif /* ! UPGRADE_SUPPORT */

    if ( (con->suspended) &&
         (NULL == urh) )
    {
      /* Connection was suspended, wait for resume. */
      was_suspended = true;
      mhd_probe_log(con, "suspended: waiting for resume");
      if (! use_poll)
      {
        FD_ZERO (&rs);
        if (! MHD_add_to_fd_set_ (MHD_itc_r_fd_ (daemon->itc),
                                  &rs,
                                  NULL,
                                  FD_SETSIZE))
        {
#ifdef HAVE_MESSAGES
          MHD_DLOG (con->daemon,
                    _ ("Failed to add FD to fd_set.\n"));
#endif
          goto exit;
        }
        if (0 > MHD_SYS_select_ (MHD_itc_r_fd_ (daemon->itc) + 1,
                                 &rs,
                                 NULL,
                                 NULL,
                                 NULL))
        {
          const int err = MHD_socket_get_error_ ();

          if (MHD_SCKT_ERR_IS_EINTR_ (err))
            continue;
#ifdef HAVE_MESSAGES
          MHD_DLOG (con->daemon,
                    _ ("Error during select (%d): `%s'\n"),
                    err,
                    MHD_socket_strerr_ (err));
#endif
          break;
        }
      }
#ifdef HAVE_POLL
      else     /* use_poll */
      {
        p[0].events = POLLIN;
        p[0].fd = MHD_itc_r_fd_ (daemon->itc);
        p[0].revents = 0;
        if (0 > MHD_sys_poll_ (p,
                               1,
                               -1))
        {
          if (MHD_SCKT_LAST_ERR_IS_ (MHD_SCKT_EINTR_))
            continue;
#ifdef HAVE_MESSAGES
          MHD_DLOG (con->daemon,
                    _ ("Error during poll: `%s'\n"),
                    MHD_socket_last_strerr_ ());
#endif
          break;
        }
      }
#endif /* HAVE_POLL */
      MHD_itc_clear_ (daemon->itc);
      continue; /* Check again for resume. */
    }           /* End of "suspended" branch. */

    if (was_suspended)
    {
      MHD_update_last_activity_ (con);     /* Reset timeout timer. */
      /* Process response queued during suspend and update states. */
      MHD_connection_handle_idle (con);
      was_suspended = false;
      mhd_probe_log(con, "resumed: processed queued response, reset activity");
    }

    use_zero_timeout =
      (0 != (MHD_EVENT_LOOP_INFO_PROCESS & con->event_loop_info)
#ifdef HTTPS_SUPPORT
       || ( (con->tls_read_ready) &&
            (0 != (MHD_EVENT_LOOP_INFO_READ & con->event_loop_info)) )
#endif /* HTTPS_SUPPORT */
      );

    /* Log computation of use_zero_timeout and related state. */
    mhd_probe_log(con, "use_zero_timeout=%d event_loop_info=0x%08x tls_read_ready=%d",
                  (int) use_zero_timeout,
                  (unsigned) con->event_loop_info,
                  (int) con->tls_read_ready);

    if (! use_poll)
    {
      /* use select */
      bool err_state = false;
      struct timeval tv;
      struct timeval *tvp;
      if (use_zero_timeout)
      {
        tv.tv_sec = 0;
        tv.tv_usec = 0;
        tvp = &tv;
      }
      else if (con->connection_timeout_ms > 0)
      {
        const uint64_t mseconds_left = connection_get_wait (con);
#if (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC
        if (mseconds_left / 1000 > TIMEVAL_TV_SEC_MAX)
          tv.tv_sec = TIMEVAL_TV_SEC_MAX;
        else
#endif /* (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC */
        tv.tv_sec = (_MHD_TIMEVAL_TV_SEC_TYPE) mseconds_left / 1000;

        tv.tv_usec = ((uint16_t) (mseconds_left % 1000)) * ((int32_t) 1000);
        tvp = &tv;
      }
      else
        tvp = NULL;

      FD_ZERO (&rs);
      FD_ZERO (&ws);
      FD_ZERO (&es);
      maxsock = MHD_INVALID_SOCKET;
      switch (con->event_loop_info)
      {
      case MHD_EVENT_LOOP_INFO_READ:
      case MHD_EVENT_LOOP_INFO_PROCESS_READ:
        if (! MHD_add_to_fd_set_ (con->socket_fd,
                                  &rs,
                                  &maxsock,
                                  FD_SETSIZE))
          err_state = true;
        break;
      case MHD_EVENT_LOOP_INFO_WRITE:
        if (! MHD_add_to_fd_set_ (con->socket_fd,
                                  &ws,
                                  &maxsock,
                                  FD_SETSIZE))
          err_state = true;
        break;
      case MHD_EVENT_LOOP_INFO_PROCESS:
        if (! MHD_add_to_fd_set_ (con->socket_fd,
                                  &es,
                                  &maxsock,
                                  FD_SETSIZE))
          err_state = true;
        break;
      case MHD_EVENT_LOOP_INFO_CLEANUP:
        /* how did we get here!? */
        goto exit;
      }
#ifdef WINDOWS
      if (MHD_ITC_IS_VALID_ (daemon->itc) )
      {
        if (! MHD_add_to_fd_set_ (MHD_itc_r_fd_ (daemon->itc),
                                  &rs,
                                  &maxsock,
                                  FD_SETSIZE))
          err_state = 1;
      }
#endif
      if (err_state)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (con->daemon,
                  _ ("Failed to add FD to fd_set.\n"));
#endif
        goto exit;
      }

      num_ready = MHD_SYS_select_ (maxsock + 1,
                                   &rs,
                                   &ws,
                                   &es,
                                   tvp);
      if (num_ready < 0)
      {
        const int err = MHD_socket_get_error_ ();

        if (MHD_SCKT_ERR_IS_EINTR_ (err))
          continue;
#ifdef HAVE_MESSAGES
        MHD_DLOG (con->daemon,
                  _ ("Error during select (%d): `%s'\n"),
                  err,
                  MHD_socket_strerr_ (err));
#endif
        break;
      }
#ifdef WINDOWS
      /* Clear ITC before other processing so additional
       * signals will trigger select() again */
      if ( (MHD_ITC_IS_VALID_ (daemon->itc)) &&
           (FD_ISSET (MHD_itc_r_fd_ (daemon->itc),
                      &rs)) )
        MHD_itc_clear_ (daemon->itc);
#endif

      /* Compute readiness booleans and log them before calling handlers. */
      {
        const bool read_ready = FD_ISSET (con->socket_fd, &rs);
        const bool write_ready = FD_ISSET (con->socket_fd, &ws);
        const bool except_ready = FD_ISSET (con->socket_fd, &es);
        mhd_probe_log(con, "calling call_handlers (select path): read_ready=%d write_ready=%d except_ready=%d num_ready=%d maxsock=%d use_zero_timeout=%d",
                      (int) read_ready,
                      (int) write_ready,
                      (int) except_ready,
                      num_ready,
                      (int) maxsock,
                      (int) use_zero_timeout);

        if (MHD_NO ==
            call_handlers (con,
                           read_ready,
                           write_ready,
                           except_ready) )
        {
          mhd_probe_log(con, "call_handlers returned MHD_NO (select path), exiting loop");
          goto exit;
        }
        else
        {
          mhd_probe_log(con, "call_handlers returned non-MHD_NO (select path), continuing");
        }
      }
    }
#ifdef HAVE_POLL
    else
    {
      int timeout_val;
      /* use poll */
      if (use_zero_timeout)
        timeout_val = 0;
      else if (con->connection_timeout_ms > 0)
      {
        const uint64_t mseconds_left = connection_get_wait (con);
#if SIZEOF_UINT64_T >= SIZEOF_INT
        if (mseconds_left >= INT_MAX)
          timeout_val = INT_MAX;
        else
#endif /* SIZEOF_UINT64_T >= SIZEOF_INT */
        timeout_val = (int) mseconds_left;
      }
      else
        timeout_val = -1;
      memset (&p,
              0,
              sizeof (p));
      p[0].fd = con->socket_fd;
      switch (con->event_loop_info)
      {
      case MHD_EVENT_LOOP_INFO_READ:
      case MHD_EVENT_LOOP_INFO_PROCESS_READ:
        p[0].events |= POLLIN | MHD_POLL_EVENTS_ERR_DISC;
        break;
      case MHD_EVENT_LOOP_INFO_WRITE:
        p[0].events |= POLLOUT | MHD_POLL_EVENTS_ERR_DISC;
        break;
      case MHD_EVENT_LOOP_INFO_PROCESS:
        p[0].events |= MHD_POLL_EVENTS_ERR_DISC;
        break;
      case MHD_EVENT_LOOP_INFO_CLEANUP:
        /* how did we get here!? */
        goto exit;
      }
#ifdef WINDOWS
      extra_slot = 0;
      if (MHD_ITC_IS_VALID_ (daemon->itc))
      {
        p[1].events |= POLLIN;
        p[1].fd = MHD_itc_r_fd_ (daemon->itc);
        p[1].revents = 0;
        extra_slot = 1;
      }
#endif
      if (MHD_sys_poll_ (p,
#ifdef WINDOWS
                         1 + extra_slot,
#else
                         1,
#endif
                         timeout_val) < 0)
      {
        if (MHD_SCKT_LAST_ERR_IS_ (MHD_SCKT_EINTR_))
          continue;
#ifdef HAVE_MESSAGES
        MHD_DLOG (con->daemon,
                  _ ("Error during poll: `%s'\n"),
                  MHD_socket_last_strerr_ ());
#endif
        break;
      }
#ifdef WINDOWS
      /* Clear ITC before other processing so additional
       * signals will trigger poll() again */
      if ( (MHD_ITC_IS_VALID_ (daemon->itc)) &&
           (0 != (p[1].revents & (POLLERR | POLLHUP | POLLIN))) )
        MHD_itc_clear_ (daemon->itc);
#endif

      /* Compute readiness booleans and log them before calling handlers (poll path). */
      {
        const bool read_ready = (0 != (p[0].revents & POLLIN));
        const bool write_ready = (0 != (p[0].revents & POLLOUT));
        const bool except_ready = (0 != (p[0].revents & MHD_POLL_REVENTS_ERR_DISC));
        mhd_probe_log(con, "calling call_handlers (poll path): revents=0x%08x read_ready=%d write_ready=%d except_ready=%d timeout_val=%d",
                      (unsigned) p[0].revents,
                      (int) read_ready,
                      (int) write_ready,
                      (int) except_ready,
                      timeout_val);

        if (MHD_NO ==
            call_handlers (con,
                           read_ready,
                           write_ready,
                           except_ready) )
        {
          mhd_probe_log(con, "call_handlers returned MHD_NO (poll path), exiting loop");
          goto exit;
        }
        else
        {
          mhd_probe_log(con, "call_handlers returned non-MHD_NO (poll path), continuing");
        }
      }
    }
#endif
#ifdef UPGRADE_SUPPORT
    if (MHD_CONNECTION_UPGRADE == con->state)
    {
      /* Normal HTTP processing is finished,
       * notify application. */
      if ( (NULL != daemon->notify_completed) &&
           (con->rq.client_aware) )
        daemon->notify_completed (daemon->notify_completed_cls,
                                  con,
                                  &con->rq.client_context,
                                  MHD_REQUEST_TERMINATED_COMPLETED_OK);
      con->rq.client_aware = false;

      thread_main_connection_upgrade (con);
      /* MHD_connection_finish_forward_() was called by thread_main_connection_upgrade(). */

      /* "Upgraded" data will not be used in this thread from this point. */
      con->urh->clean_ready = true;
      /* If 'urh->was_closed' set to true, connection will be
       * moved immediately to cleanup list. Otherwise connection
       * will stay in suspended list until 'urh' will be marked
       * with 'was_closed' by application. */
      MHD_resume_connection (con);

      /* skip usual clean up  */
      mhd_probe_log(con, "connection upgraded: switching to upgrade handling and returning");
      return (MHD_THRD_RTRN_TYPE_) 0;
    }
#endif /* UPGRADE_SUPPORT */
  }
#if _MHD_DEBUG_CLOSE
#ifdef HAVE_MESSAGES
  MHD_DLOG (con->daemon,
            _ ("Processing thread terminating. Closing connection.\n"));
#endif
#endif
  if (MHD_CONNECTION_CLOSED != con->state)
    MHD_connection_close_ (con,
                           (daemon->shutdown) ?
                           MHD_REQUEST_TERMINATED_DAEMON_SHUTDOWN :
                           MHD_REQUEST_TERMINATED_WITH_ERROR);
  MHD_connection_handle_idle (con);
exit:
  /* Log exit state and cleanup decisions. */
  mhd_probe_log(con, "EXIT: final state=%d rp.response=%p socket_fd=%d",
                (int) con->state,
                (void*) con->rp.response,
                (int) con->socket_fd);

  if (NULL != con->rp.response)
  {
    MHD_destroy_response (con->rp.response);
    con->rp.response = NULL;
  }

  if (MHD_INVALID_SOCKET != con->socket_fd)
  {
    shutdown (con->socket_fd,
              SHUT_WR);
    /* 'socket_fd' can be used in other thread to signal shutdown.
     * To avoid data races, do not close socket here. Daemon will
     * use more connections only after cleanup anyway. */
  }
  if ( (MHD_ITC_IS_VALID_ (daemon->itc)) &&
       (! MHD_itc_activate_ (daemon->itc, "t")) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to signal thread termination via inter-thread " \
                 "communication channel.\n"));
#endif
  }
  return (MHD_THRD_RTRN_TYPE_) 0;
}
