#include <unistd.h>
#include <sys/types.h>
#include <sys/stat.h>
#include <fcntl.h>
#include <stdio.h>
#include <string.h>
#include <errno.h>
#include <stdarg.h>
#include <stdint.h>

static void probe_log(const char *fmt, ...)
{
    char buf[256];
    va_list ap;
    va_start(ap, fmt);
    int len = vsnprintf(buf, sizeof(buf), fmt, ap);
    va_end(ap);

    if (len <= 0) {
        return;
    }

    int fd = open("/tmp/logs/34.txt", O_CREAT | O_WRONLY | O_APPEND, 0644);
    if (fd >= 0) {
        size_t to_write = (size_t)len;
        if (to_write >= sizeof(buf)) {
            to_write = sizeof(buf) - 1;
        }
        /* ignore write errors on the probe to avoid changing behaviour */
        (void)write(fd, buf, to_write);
        (void)close(fd);
    }
}

void OS_BSP_ConsoleOutput_Impl(const char *Str, size_t DataLen)
{
    ssize_t WriteLen;

    probe_log("OS_BSP_ConsoleOutput_Impl entry: Str=%p DataLen=%zu\n", (const void *)Str, DataLen);

    while (DataLen > 0)
    {
        /* writes the raw data directly to STDOUT_FILENO (unbuffered) */
        probe_log("Attempting write: Str=%p DataLen=%zu\n", (const void *)Str, DataLen);
        WriteLen = write(STDOUT_FILENO, Str, DataLen);
        probe_log("write() returned: %zd errno=%d (%s)\n", WriteLen, errno, strerror(errno));
        if (WriteLen <= 0)
        {
            probe_log("Write failed or returned 0, breaking out: Str=%p DataLen=%zu WriteLen=%zd\n",
                      (const void *)Str, DataLen, WriteLen);
            /* no recourse if this fails, just stop. */
            break;
        }
        Str += WriteLen;
        DataLen -= WriteLen;
        probe_log("After successful write: Str=%p DataLen=%zu\n", (const void *)Str, DataLen);
    }

    probe_log("OS_BSP_ConsoleOutput_Impl exit\n");
}
